INSERT INTO quranLatin VALUES (4060, '39:2', 39, 2, 'inn<u>aa</u> anzaln<u>aa</u> ilayka lkit<u>aa</u>ba bil<u>h</u>aqqi fa\'budill<u>aa</u>ha mukhli<u>sh</u>an lahu ddiin\r\r\n');
INSERT INTO quranLatin VALUES (4061, '39:3', 39, 3, 'al<u>aa</u> lill<u>aa</u>hi ddiinu lkh<u>aa</u>li<u>sh</u>u walla<u>dz</u>iina ittakha<u>dz</u>uu min duunihi awliy<u>aa</u>-a m<u>aa</u> na\'buduhum ill<u>aa</u> liyuqarribuun<u>aa</u> il<u>aa</u>ll<u>aa</u>hi zulf<u>aa</u> innall<u>aa</u>ha ya<u>h</u>kumu baynahum fii m<u>aa</u> hum fiihi yakhtalifuuna innall<u>aa</u>ha l<u>aa</u> yahdii man huwa k<u>aadz</u>ibun kaff<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (4062, '39:4', 39, 4, 'law ar<u>aa</u>dall<u>aa</u>hu an yattakhi<u>dz</u>a waladan la<u>sth</u>af<u>aa</u> mimm<u>aa</u> yakhluqu m<u>aa</u> yasy<u>aa</u>u sub<u>haa</u>nahu huwall<u>aa</u>hu lw<u>aah</u>idu lqahh<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (4063, '39:5', 39, 5, 'khalaqa ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a bil<u>h</u>aqqi yukawwirullayla \'al<u>aa</u> nnah<u>aa</u>ri wayukawwiru nnah<u>aa</u>ra \'al<u>aa</u>llayli wasakhkhara sysyamsa walqamara kullun yajrii li-ajalin musamman l<u>aa</u> huwa l\'aziizu lghaff<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (4064, '39:6', 39, 6, 'khalaqakum min nafsin w<u>aah</u>idatin tsumma ja\'ala minh<u>aa</u> zawjah<u>aa</u> wa-anzala lakum mina l-an\'<u>aa</u>mi tsam<u>aa</u>niyata azw<u>aa</u>jin yakhluqukum fii bu<u>th</u>uuni ummah<u>aa</u>tikum khalqan min ba\'di khalqin fii <u>zh</u>ulum<u>aa</u>tin tsal<u>aa</u>tsin <u>dzaa</u>likumull<u>aa</u>hu rabbukum lahu lmulku l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa fa-ann<u>aa</u> tu<u>sh</u>rafuun\r\r\n');
INSERT INTO quranLatin VALUES (4065, '39:7', 39, 7, 'in takfuruu fa-innall<u>aa</u>ha ghaniyyun \'ankum wal<u>aa</u> yar<u>daa</u> li\'ib<u>aa</u>dihi lkufra wa-in tasykuruu yar<u>dh</u>ahu lakum wal<u>aa</u> taziru w<u>aa</u>ziratun wizra ukhr<u>aa</u> tsumma il<u>aa</u> rabbikum marji\'ukum fayunabbi-ukum bim<u>aa</u> kuntum ta\'maluuna innahu \'aliimun bi<u>dzaa</u>ti <u>shsh</u>uduur\r\r\n');
INSERT INTO quranLatin VALUES (4066, '39:8', 39, 8, 'wa-i<u>dzaa</u> massa l-ins<u>aa</u>na <u>dh</u>urrun da\'<u>aa</u> rabbahu muniiban ilayhi tsumma i<u>dzaa</u> khawwalahu ni\'matan minhu nasiya m<u>aa</u> k<u>aa</u>na yad\'uu ilayhi min qablu waja\'ala lill<u>aa</u>hi and<u>aa</u>dan liyu<u>dh</u>illa \'an sabiilihi qul tamatta\'bikufrika qaliilan innaka min a<u>sh</u>-<u>haa</u>bi nn<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (4067, '39:9', 39, 9, 'amman huwa q<u>aa</u>nitun <u>aa</u>n<u>aa</u>-allayli s<u>aa</u>jidan waq<u>aa</u>-iman ya<u>hts</u>aru l-<u>aa</u>khirata wayarjuu ra<u>h</u>mata rabbihi qul hal yastawiilla<u>dz</u>iina ya\'lamuuna walla<u>dz</u>iina l<u>aa</u> ya\'lamuuna innam<u>aa</u> yata<u>dz</u>akkaru uluu l-alb<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (4068, '39:10', 39, 10, 'qul y<u>aa</u> \'ib<u>aa</u>dilla<u>dz</u>iina <u>aa</u>manuu ittaquu rabbakum lilla<u>dz</u>iina a<u>h</u>sanuu fii h<u>aadz</u>ihi dduny<u>aa</u> <u>h</u>asanatun wa-ar<u>dh</u>ull<u>aa</u>hi w<u>aa</u>si\'atun innam<u>aa</u> yuwaff<u>aa</u> <u>shshaa</u>biruuna ajrahum bighayri <u>h</u>is<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (4069, '39:11', 39, 11, 'qul innii umirtu an a\'budall<u>aa</u>ha mukhli<u>sh</u>an lahu ddiin\r\r\n');
INSERT INTO quranLatin VALUES (4070, '39:12', 39, 12, 'waumirtu li-an akuuna awwala lmuslimiin\r\r\n');
INSERT INTO quranLatin VALUES (4071, '39:13', 39, 13, 'qul innii akh<u>aa</u>fu in \'a<u>sh</u>aytu rabbii \'a<u>dzaa</u>ba yawmin \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (4072, '39:14', 39, 14, 'qulill<u>aa</u>ha a\'budu mukhli<u>sh</u>an lahu diinii</u>\r\r\n');
INSERT INTO quranLatin VALUES (4073, '39:15', 39, 15, 'fa\'buduu m<u>aa</u> syi/tum min duunihi qul inna lkh<u>aa</u>siriinalla<u>dz</u>iina khasiruu anfusahum wa-ahliihim yawma lqiy<u>aa</u>mati l<u>aa</u> <u>dzaa</u>lika huwa lkhusr<u>aa</u>nu lmubiin\r\r\n');
INSERT INTO quranLatin VALUES (4074, '39:16', 39, 16, 'lahum min fawqihim <u>zh</u>ulalun mina nn<u>aa</u>ri wamin ta<u>h</u>tihim <u>zh</u>ulalun <u>dzaa</u>lika yukhawwifull<u>aa</u>hu bihi \'ib<u>aa</u>dahu y<u>aa</u> \'ib<u>aa</u>di fattaquun\r\r\n');
INSERT INTO quranLatin VALUES (4075, '39:17', 39, 17, 'walla<u>dz</u>iina ijtanabuu <u>ththaa</u>ghuuta an ya\'buduuh<u>aa</u> wa-an<u>aa</u>buu il<u>aa</u>ll<u>aa</u>hi lahumu lbusyr<u>aa</u> fabasysyir \'ib<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (4076, '39:18', 39, 18, '<B>al</b>la<u>dz</u>iina yastami\'uuna lqawla fayattabi\'uuna a<u>h</u>sanahu ul<u>aa</u>-ikalla<u>dz</u>iina had<u>aa</u>humull<u>aa</u>hu waul<u>aa</u>-ika hum uluu l-alb<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (4077, '39:19', 39, 19, 'afaman <u>h</u>aqqa \'alayhi kalimatu l\'a<u>dzaa</u>bi afa-anta tunqi<u>dz</u>u man fii nn<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (4078, '39:20', 39, 20, 'l<u>aa</u>kinilla<u>dz</u>iina ittaqaw rabbahum lahum ghurafun min fawqih<u>aa</u> ghurafun mabniyyatun tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru wa\'dall<u>aa</u>hi l<u>aa</u> yukhlifull<u>aa</u>hu lmii\'<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (4079, '39:21', 39, 21, 'alam tara annall<u>aa</u>ha anzala mina ssam<u>aa</u>-i m<u>aa</u>-an fasalakahu yan<u>aa</u>bii\'a fii l-ar<u>dh</u>i tsumma yukhriju bihi zar\'an mukhtalifan lw<u>aa</u>nuhu tsumma yahiiju fatar<u>aa</u>hu mu<u>sh</u>farran tsumma yaj\'aluhu <u>h</u>u<u>th</u><u>aa</u>man inna fii <u>dzaa</u>lika la<u>dz</u>ikr<u>aa</u> li-ulii l-alb<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (4080, '39:22', 39, 22, 'afaman syara<u>h</u>all<u>aa</u>hu <u>sh</u>adrahu lil-isl<u>aa</u>mi fahuwa \'al<u>aa</u> nuurin min rabbihi fawaylun lilq<u>aa</u>siyati quluubuhum min <u>dz</u>ikrill<u>aa</u>hi ul<u>aa</u>-ika fii <u>dh</u>al<u>aa</u>lin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (4081, '39:23', 39, 23, '<B>al</b>l<u>aa</u>hu nazzala a<u>h</u>sana l<u>h</u>adiitsi kit<u>aa</u>ban mutasy<u>aa</u>bihan mats<u>aa</u>niya taqsya\'irru minhu juluudulla<u>dz</u>iina yakhsyawna rabbahum tsumma taliinu juluuduhum waquluubuhum il<u>aa</u> <u>dz</u>ikrill<u>aa</u>hi <u>dzaa</u>lika hud<u>aa</u>ll<u>aa</u>hi yahdii bihi man yasy<u>aa</u>u waman yu<u>dh</u>lilill<u>aa</u>hu fam<u>aa</u> lahu min h<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (4082, '39:24', 39, 24, 'afaman yattaqii biwajhihi suu-a l\'a<u>dzaa</u>bi yawma lqiy<u>aa</u>mati waqiila li<u>zhzhaa</u>limiina <u>dz</u>uuquu m<u>aa</u> kuntum taksibuun\r\r\n');
INSERT INTO quranLatin VALUES (4083, '39:25', 39, 25, 'ka<u>dzdz</u>aballa<u>dz</u>iina min qablihim fa-at<u>aa</u>humu l\'a<u>dzaa</u>bu min <u>h</u>aytsu l<u>aa</u> yasy\'uruun\r\r\n');
INSERT INTO quranLatin VALUES (4084, '39:26', 39, 26, 'fa-a<u>dzaa</u>qahumull<u>aa</u>hu lkhizya fii l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> wala\'a<u>dzaa</u>bu l-<u>aa</u>khirati akbaru law k<u>aa</u>nuu ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (4085, '39:27', 39, 27, 'walaqad <u>dh</u>arabn<u>aa</u> linn<u>aa</u>si fii h<u>aadzaa</u> lqur-<u>aa</u>ni min kulli matsalin la\'allahum yata<u>dz</u>akkaruun\r\r\n');
INSERT INTO quranLatin VALUES (4086, '39:28', 39, 28, 'qur-<u>aa</u>nan \'arabiyyan ghayra <u>dz</u>ii \'iwajin la\'allahum yattaquun\r\r\n');
INSERT INTO quranLatin VALUES (4087, '39:29', 39, 29, '<u>dh</u>araball<u>aa</u>hu matsalan rajulan fiihi syurak<u>aa</u>u mutasy<u>aa</u>kisuuna warajulan salaman lirajulin hal yastawiy<u>aa</u>ni matsalan l<u>h</u>amdu lill<u>aa</u>hi bal aktsaruhum l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (4088, '39:30', 39, 30, 'innaka mayyitun wa-innahum mayyituun\r\r\n');
INSERT INTO quranLatin VALUES (4089, '39:31', 39, 31, 'tsumma innakum yawma lqiy<u>aa</u>mati \'inda rabbikum takhta<u>sh</u>imuun\r\r\n');
INSERT INTO quranLatin VALUES (4090, '39:32', 39, 32, 'faman a<u>zh</u>lamu mimman ka<u>dz</u>aba \'al<u>aa</u>ll<u>aa</u>hi waka<u>dzdz</u>aba bi<u>shsh</u>idqi i<u>dz</u> j<u>aa</u>-ahu alaysa fii jahannama matswan lilk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (4091, '39:33', 39, 33, 'walla<u>dz</u>ii j<u>aa</u>-a bi<u>shsh</u>idqi wa<u>sh</u>addaqa bihi ul<u>aa</u>-ika humu lmuttaquun\r\r\n');
INSERT INTO quranLatin VALUES (4092, '39:34', 39, 34, 'lahum m<u>aa</u> yasy<u>aa</u>uuna \'inda rabbihim <u>dzaa</u>lika jaz<u>aa</u>u lmu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (4093, '39:35', 39, 35, 'liyukaffirall<u>aa</u>hu \'anhum aswa-alla<u>dz</u>ii \'amiluu wayajziyahum ajrahum bi-a<u>h</u>sanilla<u>dz</u>ii k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (4094, '39:36', 39, 36, 'alaysall<u>aa</u>hu bik<u>aa</u>fin \'abdahu wayukhawwifuunaka billa<u>dz</u>iina min duunihi waman yu<u>dh</u>lilill<u>aa</u>hu fam<u>aa</u> lahu min h<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (4095, '39:37', 39, 37, 'waman yahdill<u>aa</u>hu fam<u>aa</u> lahu min mu<u>dh</u>illin alaysall<u>aa</u>hu bi\'aziizin <u>dz</u>ii intiq<u>aa</u>m\r\r\n');
INSERT INTO quranLatin VALUES (4096, '39:38', 39, 38, 'wala-in sa-altahum man khalaqa ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a layaquulunnall<u>aa</u>hu qul afara-aytum m<u>aa</u> tad\'uuna min duunill<u>aa</u>hi in ar<u>aa</u>daniyall<u>aa</u>hu bi<u>dh</u>urrin hal hunna k<u>aa</u>syif<u>aa</u>tu <u>dh</u>urrihi aw ar<u>aa</u>danii bira<u>h</u>matin hal hunna mumsik<u>aa</u>tu ra<u>h</u>matihi qul <u>h</u>asbiyall<u>aa</u>hu \'alayhi yatawakkalu lmutawakkiluun\r\r\n');
INSERT INTO quranLatin VALUES (4097, '39:39', 39, 39, 'qul y<u>aa</u> qawmi i\'maluu \'al<u>aa</u> mak<u>aa</u>natikum innii \'<u>aa</u>milun fasawfa ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (4098, '39:40', 39, 40, 'man ya/tiihi \'a<u>dzaa</u>bun yukhziihi waya<u>h</u>illu \'alayhi \'a<u>dzaa</u>bun muqiim\r\r\n');
INSERT INTO quranLatin VALUES (4099, '39:41', 39, 41, 'inn<u>aa</u> anzaln<u>aa</u> \'alayka lkit<u>aa</u>ba linn<u>aa</u>si bil<u>h</u>aqqi famani ihtad<u>aa</u> falinafsihi waman <u>dh</u>alla fa-innam<u>aa</u> ya<u>dh</u>illu \'alayh<u>aa</u> wam<u>aa</u> anta \'alayhim biwakiil\r\r\n');
INSERT INTO quranLatin VALUES (4100, '39:42', 39, 42, '<B>al</b>l<u>aa</u>hu yatawaff<u>aa</u> l-anfusa <u>h</u>iina mawtih<u>aa</u> wallatii lam tamut fii man<u>aa</u>mih<u>aa</u> fayumsikullatii qa<u>daa</u> \'alayh<u>aa</u> lmawta wayursilu l-ukhr<u>aa</u> il<u>aa</u> ajalin musamman inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin liqawmin yatafakkaruun\r\r\n');
INSERT INTO quranLatin VALUES (4101, '39:43', 39, 43, 'ami ittakha<u>dz</u>uu min duunill<u>aa</u>hi syufa\'<u>aa</u>-a qul awa law k<u>aa</u>nuu l<u>aa</u> yamlikuuna syay-an wal<u>aa</u> ya\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (4102, '39:44', 39, 44, 'qul lill<u>aa</u>hi sysyaf<u>aa</u>\'atu jamii\'an lahu mulku ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i tsumma ilayhi turja\'uun\r\r\n');
INSERT INTO quranLatin VALUES (4103, '39:45', 39, 45, 'wa-i<u>dzaa</u> <u>dz</u>ukirall<u>aa</u>hu wa<u>h</u>dahu isyma-azzat quluubulla<u>dz</u>iina l<u>aa</u> yu/minuuna bil-<u>aa</u>khirati wa-i<u>dzaa</u> <u>dz</u>ukiralla<u>dz</u>iina min duunihi i<u>dzaa</u> hum yastabsyiruun\r\r\n');
INSERT INTO quranLatin VALUES (4104, '39:46', 39, 46, 'qulill<u>aa</u>humma f<u>aath</u>ira ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i \'<u>aa</u>lima lghaybi wasysyah<u>aa</u>dati anta ta<u>h</u>kumu bayna \'ib<u>aa</u>dika fii m<u>aa</u> k<u>aa</u>nuu fiihi yakhtalifuun\r\r\n');
INSERT INTO quranLatin VALUES (4105, '39:47', 39, 47, 'walaw anna lilla<u>dz</u>iina <u>zh</u>alamuu m<u>aa</u> fii l-ar<u>dh</u>i jamii\'an wamitslahu ma\'ahu laftadaw bihi min suu-i l\'a<u>dzaa</u>bi yawma lqiy<u>aa</u>mati wabad<u>aa</u> lahum minall<u>aa</u>hi m<u>aa</u> lam yakuunuu ya<u>h</u>tasibuun\r\r\n');
INSERT INTO quranLatin VALUES (4106, '39:48', 39, 48, 'wabad<u>aa</u> lahum sayyi-<u>aa</u>tu m<u>aa</u> kasabuu wa<u>haa</u>qa bihim m<u>aa</u> k<u>aa</u>nuu bihi yastahzi-uun\r\r\n');
INSERT INTO quranLatin VALUES (4107, '39:49', 39, 49, 'fa-i<u>dzaa</u> massa l-ins<u>aa</u>na <u>dh</u>urrun da\'<u>aa</u>n<u>aa</u> tsumma i<u>dzaa</u> khawwaln<u>aa</u>hu ni\'matan minn<u>aa</u> q<u>aa</u>la innam<u>aa</u> uutiituhu \'al<u>aa</u> \'ilmin bal hiya fitnatun wal<u>aa</u>kinna aktsarahum l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (4108, '39:50', 39, 50, 'qad q<u>aa</u>lah<u>aa</u>lla<u>dz</u>iina min qablihim fam<u>aa</u> aghn<u>aa</u> \'anhum m<u>aa</u> k<u>aa</u>nuu yaksibuun\r\r\n');
INSERT INTO quranLatin VALUES (4109, '39:51', 39, 51, 'fa-a<u>shaa</u>bahum sayyi-<u>aa</u>tu m<u>aa</u> kasabuu walla<u>dz</u>iina <u>zh</u>alamuu min h<u>aa</u>ul<u>aa</u>-i sayu<u>sh</u>iibuhum sayyi-<u>aa</u>tu m<u>aa</u> kasabuu wam<u>aa</u> hum bimu\'jiziin\r\r\n');
INSERT INTO quranLatin VALUES (4110, '39:52', 39, 52, 'awa lam ya\'lamuu annall<u>aa</u>ha yabsu<u>th</u>u rrizqa liman yasy<u>aa</u>u wayaqdiru inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin liqawmin yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (4111, '39:53', 39, 53, 'qul y<u>aa</u> \'ib<u>aa</u>diyalla<u>dz</u>iina asrafuu \'al<u>aa</u> anfusihim l<u>aa</u> taqna<u>th</u>uu min ra<u>h</u>matill<u>aa</u>hi innall<u>aa</u>ha yaghfiru <u>dzdz</u>unuuba jamii\'an innahu huwa lghafuuru rra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (4112, '39:54', 39, 54, 'wa-aniibuu il<u>aa</u> rabbikum wa-aslimuu lahu min qabli an ya/tiyakumu l\'a<u>dzaa</u>bu tsumma l<u>aa</u> tun<u>sh</u>aruun\r\r\n');
INSERT INTO quranLatin VALUES (4113, '39:55', 39, 55, 'wattabi\'uu a<u>h</u>sana m<u>aa</u> unzila ilaykum min rabbikum min qabli an ya/tiyakumu l\'a<u>dzaa</u>bu baghtatan wa-antum l<u>aa</u> tasy\'uruun\r\r\n');
INSERT INTO quranLatin VALUES (4114, '39:56', 39, 56, 'an taquula nafsun y<u>aa</u> <u>h</u>asrat<u>aa</u> \'al<u>aa</u> m<u>aa</u> farra<u>th</u>tu fii janbill<u>aa</u>hi wa-in kuntu lamina ss<u>aa</u>khiriin\r\r\n');
INSERT INTO quranLatin VALUES (4115, '39:57', 39, 57, 'aw taquula law annall<u>aa</u>ha had<u>aa</u>nii lakuntu mina lmuttaqiin\r\r\n');
INSERT INTO quranLatin VALUES (4116, '39:58', 39, 58, 'aw taquula <u>h</u>iina tar<u>aa</u> l\'a<u>dzaa</u>ba law anna lii karratan fa-akuuna mina lmu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (4117, '39:59', 39, 59, 'bal<u>aa</u> qad j<u>aa</u>-atka <u>aa</u>y<u>aa</u>tii faka<u>dzdz</u>abta bih<u>aa</u> wastakbarta wakunta mina lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (4118, '39:60', 39, 60, 'wayawma alqiy<u>aa</u>mati tar<u>aa</u>lla<u>dz</u>iina ka<u>dz</u>abuu \'al<u>aa</u>ll<u>aa</u>hi wujuuhuhum muswaddatun alaysa fii jahannama matswan lilmutakabbiriin\r\r\n');
INSERT INTO quranLatin VALUES (4119, '39:61', 39, 61, 'wayunajjiill<u>aa</u>hulla<u>dz</u>iina ittaqaw bimaf<u>aa</u>zatihim l<u>aa</u> yamassuhumu ssuu-u wal<u>aa</u> hum ya<u>h</u>zanuun\r\r\n');
INSERT INTO quranLatin VALUES (4120, '39:62', 39, 62, '<B>al</b>l<u>aa</u>hu kh<u>aa</u>liqu kulli syay-in wahuwa \'al<u>aa</u> kulli syay-in wakiil\r\r\n');
INSERT INTO quranLatin VALUES (4121, '39:63', 39, 63, 'lahu maq<u>aa</u>liidu ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i walla<u>dz</u>iina kafaruu bi-<u>aa</u>y<u>aa</u>till<u>aa</u>hi ul<u>aa</u>-ika humu lkh<u>aa</u>siruun\r\r\n');
INSERT INTO quranLatin VALUES (4122, '39:64', 39, 64, 'qul afaghayrall<u>aa</u>hi ta/muruunnii a\'budu ayyuh<u>aa</u> lj<u>aa</u>hiluun\r\r\n');
INSERT INTO quranLatin VALUES (4123, '39:65', 39, 65, 'walaqad uu<u>h</u>iya ilayka wa-il<u>aa</u>lla<u>dz</u>iina min qablika la-in asyrakta laya<u>h</u>ba<u>th</u>anna \'amaluka walatakuunanna mina lkh<u>aa</u>siriin\r\r\n');
INSERT INTO quranLatin VALUES (4124, '39:66', 39, 66, 'balill<u>aa</u>ha fa\'bud wakun mina sysy<u>aa</u>kiriin\r\r\n');
INSERT INTO quranLatin VALUES (4125, '39:67', 39, 67, 'wam<u>aa</u> qadaruull<u>aa</u>ha <u>h</u>aqqa qadrihi wal-ar<u>dh</u>u jamii\'an qab<u>dh</u>atuhu yawma lqiy<u>aa</u>mati wassam<u>aa</u>w<u>aa</u>tu ma<u>th</u>wiyy<u>aa</u>tun biyamiinihi sub<u>haa</u>nahu wata\'<u>aa</u>l<u>aa</u> \'amm<u>aa</u> yusyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (4126, '39:68', 39, 68, 'wanufikha fii <u>shsh</u>uuri fa<u>sh</u>a\'iqa man fii ssam<u>aa</u>w<u>aa</u>ti waman fii l-ar<u>dh</u>i ill<u>aa</u> man sy<u>aa</u>-all<u>aa</u>hu tsumma nufikha fiihi ukhr<u>aa</u> fa-i<u>dzaa</u> hum qiy<u>aa</u>mun yan<u>zh</u>uruun\r\r\n');
INSERT INTO quranLatin VALUES (4127, '39:69', 39, 69, 'wa-asyraqati l-ar<u>dh</u>u binuuri rabbih<u>aa</u> wawu<u>dh</u>i\'a lkit<u>aa</u>bu wajii-a binnabiyyiina wasysyuhad<u>aa</u>-i waqu<u>dh</u>iya baynahum bil<u>h</u>aqqi wahum l<u>aa</u> yu<u>zh</u>lamuun\r\r\n');
INSERT INTO quranLatin VALUES (4128, '39:70', 39, 70, 'wawuffiyat kullu nafsin m<u>aa</u> \'amilat wahuwa a\'lamu bim<u>aa</u> yaf\'aluun\r\r\n');
INSERT INTO quranLatin VALUES (4129, '39:71', 39, 71, 'wasiiqalla<u>dz</u>iina kafaruu il<u>aa</u> jahannama zumaran <u>h</u>att<u>aa</u> i<u>dzaa</u> j<u>aa</u>uuh<u>aa</u> futi<u>h</u>at abw<u>aa</u>buh<u>aa</u> waq<u>aa</u>la lahum khazanatuh<u>aa</u> alam ya/tikum rusulun minkum yatluuna \'alaykum <u>aa</u>y<u>aa</u>ti rabbikum wayun<u>dz</u>iruunakum liq<u>aa</u>-a yawmikum h<u>aadzaa</u> q<u>aa</u>luu bal<u>aa</u> wal<u>aa</u>kin <u>h</u>aqqat kalimatu al\'a<u>dzaa</u>bi \'al<u>aa</u> alk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (4130, '39:72', 39, 72, 'qiila udkhuluu abw<u>aa</u>ba jahannama kh<u>aa</u>lidiina fiih<u>aa</u> fabi/sa matsw<u>aa</u> lmutakabbiriin\r\r\n');
INSERT INTO quranLatin VALUES (4131, '39:73', 39, 73, 'wasiiqalla<u>dz</u>iina ittaqaw rabbahum il<u>aa</u> ljannati zumaran <u>h</u>att<u>aa</u> i<u>dzaa</u> j<u>aa</u>uuh<u>aa</u> wafuti<u>h</u>at abw<u>aa</u>buh<u>aa</u> waq<u>aa</u>la lahum khazanatuh<u>aa</u> sal<u>aa</u>mun \'alaykum <u>th</u>ibtum fadkhuluuh<u>aa</u> kh<u>aa</u>lidiin\r\r\n');
INSERT INTO quranLatin VALUES (4132, '39:74', 39, 74, 'waq<u>aa</u>luu l<u>h</u>amdu lill<u>aa</u>hilla<u>dz</u>ii <u>sh</u>adaqan<u>aa</u> wa\'dahu wa-awratsan<u>aa</u> l-ar<u>dh</u>a natabawwau mina ljannati <u>h</u>aytsu nasy<u>aa</u>u fani\'ma ajru l\'<u>aa</u>miliin\r\r\n');
INSERT INTO quranLatin VALUES (4133, '39:75', 39, 75, 'watar<u>aa</u> lmal<u>aa</u>-ikata <u>haa</u>ffiina min <u>h</u>awli l\'arsyi yusabbi<u>h</u>uuna bi<u>h</u>amdi rabbihim waqu<u>dh</u>iya baynahum bil<u>h</u>aqqi waqiila l<u>h</u>amdu lill<u>aa</u>hi rabbi l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (4134, '40:1', 40, 1, '<u>haa</u>-miim</u>\r\r\n');
INSERT INTO quranLatin VALUES (4135, '40:2', 40, 2, 'tanziilu lkit<u>aa</u>bi minall<u>aa</u>hi l\'aziizi l\'aliim\r\r\n');
INSERT INTO quranLatin VALUES (4136, '40:3', 40, 3, 'gh<u>aa</u>firi <u>dzdz</u>anbi waq<u>aa</u>bili ttawbi syadiidi l\'iq<u>aa</u>bi <u>dz</u>ii <u>ththh</u>awli l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa ilayhi lma<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (4137, '40:4', 40, 4, 'm<u>aa</u> yuj<u>aa</u>dilu fii <u>aa</u>y<u>aa</u>till<u>aa</u>hi ill<u>aa</u>lla<u>dz</u>iina kafaruu fal<u>aa</u> yaghrurka taqallubuhum fii lbil<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (4138, '40:5', 40, 5, 'ka<u>dzdz</u>abat qablahum qawmu nuu<u>h</u>in wal-a<u>h</u>z<u>aa</u>bu min ba\'dihim wahammat kullu ummatin birasuulihim liya/khu<u>dz</u>uuhu waj<u>aa</u>daluu bilb<u>aath</u>ili liyud<u>h</u>i<u>dh</u>uu bihi l<u>h</u>aqqa fa-akha<u>dz</u>tuhum fakayfa k<u>aa</u>na \'iq<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (4139, '40:6', 40, 6, 'waka<u>dzaa</u>lika <u>h</u>aqqat kalimatu rabbika \'al<u>aa</u>lla<u>dz</u>iina kafaruu annahum a<u>sh</u>-<u>haa</u>bu nn<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (4140, '40:7', 40, 7, '<B>al</b>la<u>dz</u>iina ya<u>h</u>miluuna l\'arsya waman <u>h</u>awlahu yusabbi<u>h</u>uuna bi<u>h</u>amdi rabbihim wayu/minuuna bihi wayastaghfiruuna lilla<u>dz</u>iina <u>aa</u>manuu rabban<u>aa</u> wasi\'ta kulla syay-in ra<u>h</u>matan wa\'ilman faghfir lilla<u>dz</u>iina t<u>aa</u>buu wattaba\'uu sabiilaka waqihim \'a<u>dzaa</u>ba lja<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (4141, '40:8', 40, 8, 'rabban<u>aa</u> wa-adkhilhum jann<u>aa</u>ti \'adninllatii wa\'adtahum waman <u>sh</u>ala<u>h</u>a min <u>aa</u>b<u>aa</u>-ihim wa-azw<u>aa</u>jihim wa<u>dz</u>urriyy<u>aa</u>tihim innaka anta l\'aziizu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (4142, '40:9', 40, 9, 'waqihimu ssayyi-<u>aa</u>ti waman taqi ssayyi-<u>aa</u>ti yawma-i<u>dz</u>in faqad ra<u>h</u>imtahu wa<u>dzaa</u>lika huwa lfawzu l\'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (4143, '40:10', 40, 10, 'innalla<u>dz</u>iina kafaruu yun<u>aa</u>dawna lamaqtull<u>aa</u>hi akbaru min maqtikum anfusakum i<u>dz</u> tud\'awna il<u>aa</u> l-iim<u>aa</u>ni fatakfuruun\r\r\n');
INSERT INTO quranLatin VALUES (4144, '40:11', 40, 11, 'q<u>aa</u>luu rabban<u>aa</u> amattan<u>aa</u> itsnatayni wa-a<u>h</u>yaytan<u>aa</u> itsnatayni fa\'tarafn<u>aa</u> bi<u>dz</u>unuubin<u>aa</u> fahal il<u>aa</u> khuruujin min sabiil\r\r\n');
INSERT INTO quranLatin VALUES (4145, '40:12', 40, 12, '<u>dzaa</u>likum bi-annahu i<u>dzaa</u> du\'iyall<u>aa</u>hu wa<u>h</u>dahu kafartum wa-in yusyrak bihi tu/minuu fal<u>h</u>ukmu lill<u>aa</u>hi l\'aliyyi lkabiir\r\r\n');
INSERT INTO quranLatin VALUES (4146, '40:13', 40, 13, 'huwalla<u>dz</u>ii yuriikum <u>aa</u>y<u>aa</u>tihi wayunazzilu lakum mina ssam<u>aa</u>-i rizqan wam<u>aa</u> yata<u>dz</u>akkaru ill<u>aa</u> man yuniib\r\r\n');
INSERT INTO quranLatin VALUES (4147, '40:14', 40, 14, 'fad\'uull<u>aa</u>ha mukhli<u>sh</u>iina lahu ddiina walaw kariha lk<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (4148, '40:15', 40, 15, 'rafii\'u ddaraj<u>aa</u>ti <u>dz</u>uu l\'arsyi yulqii rruu<u>h</u>a min amrihi \'al<u>aa</u> man yasy<u>aa</u>u min \'ib<u>aa</u>dihi liyun<u>dz</u>ira yawma ttal<u>aa</u>q\r\r\n');
INSERT INTO quranLatin VALUES (4149, '40:16', 40, 16, 'yawma hum b<u>aa</u>rizuuna l<u>aa</u> yakhf<u>aa</u> \'al<u>aa</u>ll<u>aa</u>hi minhum syay-un limani lmulku lyawma lill<u>aa</u>hi lw<u>aah</u>idi lqahh<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (4150, '40:17', 40, 17, 'alyawma tujz<u>aa</u> kullu nafsin bim<u>aa</u> kasabat l<u>aa</u> <u>zh</u>ulma lyawma innall<u>aa</u>ha sarii\'u l<u>h</u>is<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (4151, '40:18', 40, 18, 'wa-an<u>dz</u>irhum yawma l-<u>aa</u>zifati i<u>dz</u>i lquluubu lad<u>aa</u> l<u>h</u>an<u>aa</u>jiri k<u>aats</u>imiina m<u>aa</u> li<u>zhzhaa</u>limiina min <u>h</u>amiimin wal<u>aa</u> syafii\'in yu<u>th</u><u>aa</u>\'<B>u</b>\r\r\n');
INSERT INTO quranLatin VALUES (4152, '40:19', 40, 19, 'ya\'lamu kh<u>aa</u>-inata l-a\'yuni wam<u>aa</u> tukhfii <u>shsh</u>uduur\r\r\n');
INSERT INTO quranLatin VALUES (4153, '40:20', 40, 20, 'wal<u>aa</u>hu yaq<u>dh</u>ii bil<u>h</u>aqqi walla<u>dz</u>iina yad\'uuna min duunihi l<u>aa</u> yaq<u>dh</u>uuna bisyay-in innall<u>aa</u>ha huwa ssamii\'u lba<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (4154, '40:21', 40, 21, 'awa lam yasiiruu fii l-ar<u>dh</u>i fayan<u>zh</u>uruu kayfa k<u>aa</u>na \'<u>aa</u>qibatulla<u>dz</u>iina k<u>aa</u>nuu min qablihim k<u>aa</u>nuu hum asyadda minhum quwwatan wa<u>aa</u>ts<u>aa</u>ran fii l-ar<u>dh</u>i fa-akha<u>dz</u>ahumull<u>aa</u>hu bi<u>dz</u>unuubihim wam<u>aa</u> k<u>aa</u>na lahum minall<u>aa</u>hi min w<u>aa</u>q\r\r\n');
INSERT INTO quranLatin VALUES (4155, '40:22', 40, 22, '<u>dzaa</u>lika bi-annahum k<u>aa</u>nat ta/tiihim rusuluhum bilbayyin<u>aa</u>ti fakafaruu fa-akha<u>dz</u>ahumull<u>aa</u>hu innahu qawiyyun syadiidu l\'iq<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (4156, '40:23', 40, 23, 'walaqad arsaln<u>aa</u> muus<u>aa</u> bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> wasul<u>th</u><u>aa</u>nin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (4157, '40:24', 40, 24, 'il<u>aa</u> fir\'awna wah<u>aa</u>m<u>aa</u>na waq<u>aa</u>ruuna faq<u>aa</u>luu s<u>aah</u>irun ka<u>dzdzaa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (4158, '40:25', 40, 25, 'falamm<u>aa</u> j<u>aa</u>-ahum bil<u>h</u>aqqi min \'indin<u>aa</u> q<u>aa</u>luu uqtuluu abn<u>aa</u>-alla<u>dz</u>iina <u>aa</u>manuu ma\'ahu wasta<u>h</u>yuu nis<u>aa</u>-ahum wam<u>aa</u> kaydu lk<u>aa</u>firiina ill<u>aa</u> fii <u>dh</u>al<u>aa</u>l\r\r\n');
INSERT INTO quranLatin VALUES (4159, '40:26', 40, 26, 'waq<u>aa</u>la fir\'awnu <u>dz</u>aruunii aqtul muus<u>aa</u> walyad\'u rabbahu innii akh<u>aa</u>fu an yubaddila diinakum aw an yu<u>zh</u>hira fii l-ar<u>dh</u>i lfas<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (4160, '40:27', 40, 27, 'waq<u>aa</u>la muus<u>aa</u> innii \'u<u>dz</u>tu birabbii warabbikum min kulli mutakabbirin l<u>aa</u> yu/minu biyawmi l<u>h</u>is<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (4161, '40:28', 40, 28, 'waq<u>aa</u>la rajulun mu/minun min <u>aa</u>li fir\'awna yaktumu iim<u>aa</u>nahu ataqtuluuna rajulan an yaquula rabbiyyall<u>aa</u>hu waqad j<u>aa</u>-akum bilbayyin<u>aa</u>ti min rabbikum wa-in yaku k<u>aadz</u>iban fa\'alayhi ka<u>dz</u>ibuhu wa-in yaku <u>shaa</u>diqan yu<u>sh</u>ibkum ba\'<u>dh</u>ulla<u>dz</u>ii ya\'idukum innall<u>aa</u>ha l<u>aa</u> yahdii man huwa musrifun ka<u>dzdzaa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (4162, '40:29', 40, 29, 'y<u>aa</u> qawmi lakumu lmulku lyawma <u>zhaa</u>hiriina fii l-ar<u>dh</u>i faman yan<u>sh</u>urun<u>aa</u> min ba/sill<u>aa</u>hi in j<u>aa</u>-an<u>aa</u> q<u>aa</u>la fir\'awnu m<u>aa</u> uriikum ill<u>aa</u> m<u>aa</u> ar<u>aa</u> wam<u>aa</u> ahdiikum ill<u>aa</u> sabiila rrasy<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (4163, '40:30', 40, 30, 'waq<u>aa</u>lalla<u>dz</u>ii <u>aa</u>mana y<u>aa</u> qawmi innii akh<u>aa</u>fu \'alaykum mitsla yawmi l-a<u>h</u>z<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (4164, '40:31', 40, 31, 'mitsla da/bi qawmi nuu<u>h</u>in wa\'<u>aa</u>din watsamuuda walla<u>dz</u>iina min ba\'dihim wam<u>aa</u>ll<u>aa</u>hu yuriidu <u>zh</u>ulman lil\'ib<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (4165, '40:32', 40, 32, 'way<u>aa</u> qawmi innii akh<u>aa</u>fu \'alaykum yawma ttan<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (4166, '40:33', 40, 33, 'yawma tuwalluuna mudbiriina m<u>aa</u> lakum minall<u>aa</u>hi min \'<u>aas</u>imin waman yu<u>dh</u>lilill<u>aa</u>hu fam<u>aa</u> lahu min h<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (4167, '40:34', 40, 34, 'walaqad j<u>aa</u>-akum yuusufu min qablu bilbayyin<u>aa</u>ti fam<u>aa</u> ziltum fii syakkin mimm<u>aa</u> j<u>aa</u>-akum bihi <u>h</u>att<u>aa</u> i<u>dzaa</u> halaka qultum lan yab\'atsall<u>aa</u>hu min ba\'dihi rasuulan ka<u>dzaa</u>lika yu<u>dh</u>illull<u>aa</u>hu man huwa musrifun murt<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (4168, '40:35', 40, 35, '<B>al</b>la<u>dz</u>iina yuj<u>aa</u>diluuna fii <u>aa</u>y<u>aa</u>till<u>aa</u>hi bighayri sul<u>th</u><u>aa</u>nin at<u>aa</u>hum kabura maqtan \'indall<u>aa</u>hi wa\'indalla<u>dz</u>iina <u>aa</u>manuu ka<u>dzaa</u>lika ya<u>th</u>ba\'ull<u>aa</u>hu \'al<u>aa</u> kulli qalbi mutakabbirin jabb<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (4169, '40:36', 40, 36, 'waq<u>aa</u>la fir\'awnu y<u>aa</u> h<u>aa</u>m<u>aa</u>nu ibni lii <u>sh</u>ar<u>h</u>an la\'allii ablughu l-asb<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (4170, '40:37', 40, 37, 'asb<u>aa</u>ba ssam<u>aa</u>w<u>aa</u>ti fa-a<u>ththh</u>ali\'a il<u>aa</u> il<u>aa</u>hi muus<u>aa</u> wa-innii la-a<u>zh</u>unnuhu k<u>aadz</u>iban waka<u>dzaa</u>lika zuyyina lifir\'awna suu-u \'amalihi wa<u>sh</u>udda \'ani ssabiili wam<u>aa</u> kaydu fir\'awna ill<u>aa</u> fii tab<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (4171, '40:38', 40, 38, 'waq<u>aa</u>lalla<u>dz</u>ii <u>aa</u>mana y<u>aa</u> qawmi ittabi\'uuni ahdikum sabiila rrasy<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (4172, '40:39', 40, 39, 'y<u>aa</u> qawmi innam<u>aa</u> h<u>aadz</u>ihi l<u>h</u>ay<u>aa</u>tu dduny<u>aa</u> mat<u>aa</u>\'un wa-inna l-<u>aa</u>khirata hiya d<u>aa</u>ru lqar<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (4173, '40:40', 40, 40, 'man \'amila sayyi-atan fal<u>aa</u> yujz<u>aa</u> ill<u>aa</u> mitslah<u>aa</u> waman \'amila <u>shaa</u>li<u>h</u>an min <u>dz</u>akarin aw unts<u>aa</u> wahuwa mu/minun faul<u>aa</u>-ika yadkhuluuna ljannata yurzaquuna fiih<u>aa</u> bighayri <u>h</u>is<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (4174, '40:41', 40, 41, 'way<u>aa</u> qawmi m<u>aa</u>lii ad\'uukum il<u>aa</u> nnaj<u>aa</u>ti watad\'uunanii il<u>aa</u> nn<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (4175, '40:42', 40, 42, 'tad\'uunanii li-akfura bil<u>aa</u>hi wausyrika bihi m<u>aa</u> laysa lii bihi \'ilmun wa-an<u>aa</u> ad\'uukum il<u>aa</u> l\'aziizi lghaff<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (4176, '40:43', 40, 43, 'l<u>aa</u> jarama annam<u>aa</u> tad\'uunanii ilayhi laysa lahu da\'watun fii dduny<u>aa</u> wal<u>aa</u> fii l-<u>aa</u>khirati wa-anna maraddan<u>aa</u> il<u>aa</u>ll<u>aa</u>hi wa-anna lmusrifiina hum a<u>sh</u>-<u>haa</u>bu nn<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (4177, '40:44', 40, 44, 'fasata<u>dz</u>kuruuna m<u>aa</u> aquulu lakum waufawwi<u>dh</u>u amrii il<u>aa</u>ll<u>aa</u>hi innall<u>aa</u>ha ba<u>sh</u>iirun bil\'ib<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (4178, '40:45', 40, 45, 'fawaq<u>aa</u>hull<u>aa</u>hu sayyi-<u>aa</u>ti m<u>aa</u> makaruu wa<u>haa</u>qa bi-<u>aa</u>li fir\'awna suu-u l\'a<u>dzaa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (4179, '40:46', 40, 46, 'ann<u>aa</u>ru yu\'ra<u>dh</u>uuna \'alayh<u>aa</u> ghuduwwan wa\'asyiyyan wayawma taquumu ss<u>aa</u>\'atu adkhiluu <u>aa</u>la fir\'awna asyadda l\'a<u>dzaa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (4180, '40:47', 40, 47, 'wa-i<u>dz</u> yata<u>haa</u>jjuuna fii nn<u>aa</u>ri fayaquulu <u>dhdh</u>u\'af<u>aa</u>u lilla<u>dz</u>iina istakbaruu inn<u>aa</u> kunn<u>aa</u> lakum taba\'an fahal antum mughnuuna \'ann<u>aa</u> na<u>sh</u>iiban mina nn<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (4181, '40:48', 40, 48, 'q<u>aa</u>lalla<u>dz</u>iina istakbaruu inn<u>aa</u> kullun fiih<u>aa</u> innall<u>aa</u>ha qad <u>h</u>akama bayna l\'ib<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (4182, '40:49', 40, 49, 'waq<u>aa</u>lalla<u>dz</u>iina fii nn<u>aa</u>ri likhazanati jahannama ud\'uu rabbakum yukhaffif \'ann<u>aa</u> yawman mina l\'a<u>dzaa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (4183, '40:50', 40, 50, 'q<u>aa</u>luu awa lam taku ta/tiikum rusulukum bilbayyin<u>aa</u>ti q<u>aa</u>luu bal<u>aa</u> q<u>aa</u>luu fad\'uu wam<u>aa</u> du\'<u>aa</u>u lk<u>aa</u>firiina ill<u>aa</u> fii <u>dh</u>al<u>aa</u>l\r\r\n');
INSERT INTO quranLatin VALUES (4184, '40:51', 40, 51, 'inn<u>aa</u> lanan<u>sh</u>uru rusulan<u>aa</u> walla<u>dz</u>iina <u>aa</u>manuu fii l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> wayawma yaquumu l-asyh<u>aa</u>d\r\r\n');
INSERT INTO quranLatin VALUES (4185, '40:52', 40, 52, 'yawma l<u>aa</u> yanfa\'u <u>zhzhaa</u>limiina ma\'<u>dz</u>iratuhum walahumulla\'natu walahum suu-u dd<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (4186, '40:53', 40, 53, 'walaqad <u>aa</u>tayn<u>aa</u> muus<u>aa</u> lhud<u>aa</u> wa-awratsn<u>aa</u> banii isr<u>aa</u>-iila lkit<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (4187, '40:54', 40, 54, 'hudan wa<u>dz</u>ikr<u>aa</u> li-ulii l-alb<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (4188, '40:55', 40, 55, 'fa<u>sh</u>bir inna wa\'dall<u>aa</u>hi <u>h</u>aqqun wastaghfir li<u>dz</u>anbika wasabbi<u>h</u> bi<u>h</u>amdi rabbika bil\'asyiyyi wal-ibk<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (4189, '40:56', 40, 56, 'innalla<u>dz</u>iina yuj<u>aa</u>diluuna fii <u>aa</u>y<u>aa</u>till<u>aa</u>hi bighayri sul<u>th</u><u>aa</u>nin at<u>aa</u>hum in fii <u>sh</u>uduurihim ill<u>aa</u> kibrun m<u>aa</u> hum bib<u>aa</u>lighiihi fasta\'i<u>dz</u> bil<u>aa</u>hi innahu huwa ssamii\'u lba<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (4190, '40:57', 40, 57, 'lakhalqu ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i akbaru min khalqi nn<u>aa</u>si wal<u>aa</u>kinna aktsara nn<u>aa</u>si l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (4191, '40:58', 40, 58, 'wam<u>aa</u> yastawii l-a\'m<u>aa</u> walba<u>sh</u>iiru walla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti wal<u>aa</u> lmusii-u qaliilan m<u>aa</u> tata<u>dz</u>akkaruun\r\r\n');
INSERT INTO quranLatin VALUES (4192, '40:59', 40, 59, 'inna ss<u>aa</u>\'ata la<u>aa</u>tiyatun l<u>aa</u> rayba fiih<u>aa</u> wal<u>aa</u>kinna aktsara nn<u>aa</u>si l<u>aa</u> yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (4193, '40:60', 40, 60, 'waq<u>aa</u>la rabbukumu ud\'uunii astajib lakum innalla<u>dz</u>iina yastakbiruuna \'an \'ib<u>aa</u>datii sayadkhuluuna jahannama d<u>aa</u>khiriin\r\r\n');
INSERT INTO quranLatin VALUES (4194, '40:61', 40, 61, '<B>al</b>l<u>aa</u>hulla<u>dz</u>ii ja\'ala lakumullayla litaskunuu fiihi wannah<u>aa</u>ra mub<u>sh</u>iran innall<u>aa</u>ha la<u>dz</u>uu fa<u>dh</u>lin \'al<u>aa</u> nn<u>aa</u>si wal<u>aa</u>kinna aktsara nn<u>aa</u>si l<u>aa</u> yasykuruun\r\r\n');
INSERT INTO quranLatin VALUES (4195, '40:62', 40, 62, '<u>dzaa</u>likumull<u>aa</u>hu rabbukum kh<u>aa</u>liqu kulli syay-in l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa fa-ann<u>aa</u> tu/fakuun\r\r\n');
INSERT INTO quranLatin VALUES (4196, '40:63', 40, 63, 'ka<u>dzaa</u>lika yu/fakulla<u>dz</u>iina k<u>aa</u>nuu bi-<u>aa</u>y<u>aa</u>till<u>aa</u>hi yaj<u>h</u>aduun\r\r\n');
INSERT INTO quranLatin VALUES (4197, '40:64', 40, 64, '<B>al</b>l<u>aa</u>hulla<u>dz</u>ii ja\'ala lakumu l-ar<u>dh</u>a qar<u>aa</u>ran wassam<u>aa</u>-a bin<u>aa</u>-an wa<u>sh</u>awwarakum fa-a<u>h</u>sana <u>sh</u>uwarakum warazaqakum mina <u>ththh</u>ayyib<u>aa</u>ti <u>dzaa</u>likumull<u>aa</u>hu rabbukum fatab<u>aa</u>rakall<u>aa</u>hu rabbu l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (4198, '40:65', 40, 65, 'huwa l<u>h</u>ayyu l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa fad\'uuhu mukhli<u>sh</u>iina lahu ddiina l<u>h</u>amdu lill<u>aa</u>hi rabbi l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (4199, '40:66', 40, 66, 'qul innii nuhiitu an a\'budalla<u>dz</u>iina tad\'uuna min duunill<u>aa</u>hi lamm<u>aa</u> j<u>aa</u>-aniya lbayyin<u>aa</u>tu min rabbii waumirtu an uslima lirabbi l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (4200, '40:67', 40, 67, 'huwalla<u>dz</u>ii khalaqakum min tur<u>aa</u>bin tsumma min nu<u>th</u>fatin tsumma min \'alaqatin tsumma yukhrijukum <u>th</u>iflan tsumma litablughuu asyuddakum tsumma litakuunuu syuyuukhan waminkum man yutawaff<u>aa</u> min qablu walitablughuu ajalan musamman wala\'allakum ta\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (4201, '40:68', 40, 68, 'huwalla<u>dz</u>ii yu<u>h</u>yii wayumiitu fa-i<u>dzaa</u> qa<u>daa</u> amran fa-innam<u>aa</u> yaquulu lahu kun fayakuun\r\r\n');
INSERT INTO quranLatin VALUES (4202, '40:69', 40, 69, 'alam tara il<u>aa</u>lla<u>dz</u>iina yuj<u>aa</u>diluuna fii <u>aa</u>y<u>aa</u>till<u>aa</u>hi ann<u>aa</u> yu<u>sh</u>rafuun\r\r\n');
INSERT INTO quranLatin VALUES (4203, '40:70', 40, 70, '<B>al</b>la<u>dz</u>iina ka<u>dzdz</u>abuu bilkit<u>aa</u>bi wabim<u>aa</u> arsaln<u>aa</u> bihi rusulan<u>aa</u> fasawfa ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (4204, '40:71', 40, 71, 'i<u>dz</u>i l-aghl<u>aa</u>lu fii a\'n<u>aa</u>qihim wassal<u>aa</u>silu yus<u>h</u>abuun\r\r\n');
INSERT INTO quranLatin VALUES (4205, '40:72', 40, 72, 'fii l<u>h</u>amiimi tsumma fii nn<u>aa</u>ri yusjaruun\r\r\n');
INSERT INTO quranLatin VALUES (4206, '40:73', 40, 73, 'tsumma qiila lahum ayna m<u>aa</u> kuntum tusyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (4207, '40:74', 40, 74, 'min duunill<u>aa</u>hi q<u>aa</u>luu <u>dh</u>alluu \'ann<u>aa</u> bal lam nakun nad\'uu min qablu syay-an ka<u>dzaa</u>lika yu<u>dh</u>illull<u>aa</u>hu lk<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (4208, '40:75', 40, 75, '<u>dzaa</u>likum bim<u>aa</u> kuntum tafra<u>h</u>uuna fii l-ar<u>dh</u>i bighayri l<u>h</u>aqqi wabim<u>aa</u> kuntum tamra<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (4209, '40:76', 40, 76, 'udkhuluu abw<u>aa</u>ba jahannama kh<u>aa</u>lidiina fiih<u>aa</u> fabi/sa matsw<u>aa</u> lmutakabbiriin\r\r\n');
INSERT INTO quranLatin VALUES (4210, '40:77', 40, 77, 'fa<u>sh</u>bir inna wa\'dall<u>aa</u>hi <u>h</u>aqqun fa-imm<u>aa</u> nuriyannaka ba\'<u>dh</u>alla<u>dz</u>ii na\'iduhum aw natawaffayannaka fa-ilayn<u>aa</u> yurja\'uun\r\r\n');
INSERT INTO quranLatin VALUES (4211, '40:78', 40, 78, 'walaqad arsaln<u>aa</u> rusulan min qablika minhum man qa<u>sh</u>a<u>sh</u>n<u>aa</u> \'alayka waminhum man lam naq<u>sh</u>u<u>sh</u> \'alayka wam<u>aa</u> k<u>aa</u>na lirasuulin an ya/tiya bi-<u>aa</u>yatin ill<u>aa</u> bi-i<u>dz</u>nill<u>aa</u>hi fa-i<u>dzaa</u> j<u>aa</u>-a amrull<u>aa</u>hi qu<u>dh</u>iya bil<u>h</u>aqqi wakhasira hun<u>aa</u>lika lmub<u>th</u>iluun\r\r\n');
INSERT INTO quranLatin VALUES (4212, '40:79', 40, 79, '<B>al</b>l<u>aa</u>hulla<u>dz</u>ii ja\'ala lakumu l-an\'<u>aa</u>ma litarkabuu minh<u>aa</u> waminh<u>aa</u> ta/kuluun\r\r\n');
INSERT INTO quranLatin VALUES (4213, '40:80', 40, 80, 'walakum fiih<u>aa</u> man<u>aa</u>fi\'u walitablughuu \'alayh<u>aa</u> <u>haa</u>jatan fii <u>sh</u>uduurikum wa\'alayh<u>aa</u> wa\'al<u>aa</u> lfulki tu<u>h</u>maluun\r\r\n');
INSERT INTO quranLatin VALUES (4214, '40:81', 40, 81, 'wayuriikum <u>aa</u>y<u>aa</u>tihi fa-ayya <u>aa</u>y<u>aa</u>till<u>aa</u>hi tunkiruun\r\r\n');
INSERT INTO quranLatin VALUES (4215, '40:82', 40, 82, 'afalam yasiiruu fii l-ar<u>dh</u>i fayan<u>zh</u>uruu kayfa k<u>aa</u>na \'<u>aa</u>qibatulla<u>dz</u>iina min qablihim k<u>aa</u>nuu aktsara minhum wa-asyadda quwwatan wa<u>aa</u>ts<u>aa</u>ran fii l-ar<u>dh</u>i fam<u>aa</u> aghn<u>aa</u> \'anhum m<u>aa</u> k<u>aa</u>nuu yaksibuun\r\r\n');
INSERT INTO quranLatin VALUES (4216, '40:83', 40, 83, 'falamm<u>aa</u> j<u>aa</u>-at-hum rusuluhum bilbayyin<u>aa</u>ti fari<u>h</u>uu bim<u>aa</u> \'indahum mina l\'ilmi wa<u>haa</u>qa bihim m<u>aa</u> k<u>aa</u>nuu bihi yastahzi-uun\r\r\n');
INSERT INTO quranLatin VALUES (4217, '40:84', 40, 84, 'falamm<u>aa</u> ra-aw ba/san<u>aa</u> q<u>aa</u>luu <u>aa</u>mann<u>aa</u> bil<u>aa</u>hi wa<u>h</u>dahu wakafarn<u>aa</u> bim<u>aa</u> kunn<u>aa</u> bihi musyrikiin\r\r\n');
INSERT INTO quranLatin VALUES (4218, '40:85', 40, 85, 'falam yaku yanfa\'uhum iim<u>aa</u>nuhum lamm<u>aa</u> ra-aw ba/san<u>aa</u> sunnatall<u>aa</u>hillatii qad khalat fii \'ib<u>aa</u>dihi wakhasira hun<u>aa</u>lika lk<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (4219, '41:1', 41, 1, '<u>haa</u>-miim</u>\r\r\n');
INSERT INTO quranLatin VALUES (4220, '41:2', 41, 2, 'tanziilun mina rra<u>h</u>m<u>aa</u>ni rra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (4221, '41:3', 41, 3, 'kit<u>aa</u>bun fu<u>shsh</u>ilat <u>aa</u>y<u>aa</u>tuhu qur-<u>aa</u>nan \'arabiyyan liqawmin ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (4222, '41:4', 41, 4, 'basyiiran wana<u>dz</u>iiran fa-a\'ra<u>dh</u>a aktsaruhum fahum l<u>aa</u> yasma\'uun\r\r\n');
INSERT INTO quranLatin VALUES (4223, '41:5', 41, 5, 'waq<u>aa</u>luu quluubun<u>aa</u> fii akinnatin mimm<u>aa</u> tad\'uun<u>aa</u> ilayhi wafii <u>aatsaa</u>nin<u>aa</u> waqrun wamin baynin<u>aa</u> wabaynika <u>h</u>ij<u>aa</u>bun fa\'mal innan<u>aa</u> \'<u>aa</u>miluun\r\r\n');
INSERT INTO quranLatin VALUES (4224, '41:6', 41, 6, 'qul innam<u>aa</u> an<u>aa</u> basyarun mitslukum yuu<u>haa</u> ilayya annam<u>aa</u> il<u>aa</u>hukum il<u>aa</u>hun w<u>aah</u>idun fastaqiimuu ilayhi wastaghfiruuhu wawaylun lilmusyrikiin\r\r\n');
INSERT INTO quranLatin VALUES (4225, '41:7', 41, 7, '<B>al</b>la<u>dz</u>iina l<u>aa</u> yu/tuuna zzak<u>aa</u>ta wahum bil-<u>aa</u>khirati hum k<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (4226, '41:8', 41, 8, 'innalla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti lahum ajrun ghayru mamnuun\r\r\n');
INSERT INTO quranLatin VALUES (4227, '41:9', 41, 9, 'qul a-innakum latakfuruuna billa<u>dz</u>ii khalaqa l-ar<u>dh</u>a fii yawmayni wataj\'aluuna lahu and<u>aa</u>dan <u>dzaa</u>lika rabbu l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (4228, '41:10', 41, 10, 'waja\'ala fiih<u>aa</u> raw<u>aa</u>siya min fawqih<u>aa</u> wab<u>aa</u>raka fiih<u>aa</u> waqaddara fiih<u>aa</u> aqw<u>aa</u>tah<u>aa</u> fii arba\'ati ayy<u>aa</u>min saw<u>aa</u>-an liss<u>aa</u>-iliin\r\r\n');
INSERT INTO quranLatin VALUES (4229, '41:11', 41, 11, 'tsumma istaw<u>aa</u> il<u>aa</u> ssam<u>aa</u>-i wahiya dukh<u>aa</u>nun faq<u>aa</u>la lah<u>aa</u> walil-ar<u>dh</u>i i/tiy<u>aa</u> <u>th</u>aw\'an aw karhan q<u>aa</u>lat<u>aa</u> atayn<u>aa</u> <u>th</u><u>aa</u>-i\'iin\r\r\n');
INSERT INTO quranLatin VALUES (4230, '41:12', 41, 12, 'faqa<u>daa</u>hunna sab\'a sam<u>aa</u>w<u>aa</u>tin fii yawmayni wa-aw<u>haa</u> fii kulli sam<u>aa</u>-in amrah<u>aa</u> wazayyann<u>aa</u> ssam<u>aa</u>-a dduny<u>aa</u> bima<u>shaa</u>bii<u>h</u>a wa<u>h</u>if<u>zh</u>an <u>dzaa</u>lika taqdiiru l\'aziizi l\'aliim\r\r\n');
INSERT INTO quranLatin VALUES (4231, '41:13', 41, 13, 'fa-in a\'ra<u>dh</u>uu fuqul an<u>dz</u>artukum <u>shaa</u>\'iqatan mitsla <u>shaa</u>\'iqati \'<u>aa</u>din watsamuud\r\r\n');
INSERT INTO quranLatin VALUES (4232, '41:14', 41, 14, 'i<u>dz</u> j<u>aa</u>-at-humu rrusulu min bayni aydiihim wamin khalfihimll<u>aa</u> ta\'buduu ill<u>aa</u>ll<u>aa</u>ha q<u>aa</u>luu law sy<u>aa</u>-a rabbun<u>aa</u> la-anzala mal<u>aa</u>-ikatan fa-inn<u>aa</u> bim<u>aa</u> ursiltum bihi k<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (4233, '41:15', 41, 15, 'fa-amm<u>aa</u> \'<u>aa</u>dun fastakbaruu fii l-ar<u>dh</u>i bighayri l<u>h</u>aqqi waq<u>aa</u>luu man asyaddu minn<u>aa</u> quwwatan awa lam yaraw annall<u>aa</u>halla<u>dz</u>ii khalaqahum huwa asyaddu minhum quwwatan wak<u>aa</u>nuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> yaj<u>h</u>aduun\r\r\n');
INSERT INTO quranLatin VALUES (4234, '41:16', 41, 16, 'fa-arsaln<u>aa</u> \'alayhim rii<u>h</u>an <u>sh</u>ar<u>sh</u>aran fii ayy<u>aa</u>min na<u>h</u>is<u>aa</u>tin linu<u>dz</u>iiqahum \'a<u>dzaa</u>ba lkhizyi fii l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> wala\'a<u>dzaa</u>bu l-<u>aa</u>khirati akhz<u>aa</u> wahum l<u>aa</u> yun<u>sh</u>aruun\r\r\n');
INSERT INTO quranLatin VALUES (4235, '41:17', 41, 17, 'wa-amm<u>aa</u> tsamuudu fahadayn<u>aa</u>hum fasta<u>h</u>abbuu l\'am<u>aa</u> \'al<u>aa</u> lhud<u>aa</u> fa-akha<u>dz</u>at-hum <u>shaa</u>\'iqatu l\'a<u>dzaa</u>bi lhuuni bim<u>aa</u> k<u>aa</u>nuu yaksibuun\r\r\n');
INSERT INTO quranLatin VALUES (4236, '41:18', 41, 18, 'wanajjayn<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu wak<u>aa</u>nuu yattaquun\r\r\n');
INSERT INTO quranLatin VALUES (4237, '41:19', 41, 19, 'wayawma yu<u>h</u>syaru a\'d<u>aa</u>ull<u>aa</u>hi il<u>aa</u> nn<u>aa</u>ri fahum yuuza\'uun\r\r\n');
INSERT INTO quranLatin VALUES (4238, '41:20', 41, 20, '<u>h</u>att<u>aa</u> i<u>dzaa</u> m<u>aa</u> j<u>aa</u>uuh<u>aa</u> syahida \'alayhim sam\'uhum wa-ab<u>shaa</u>ruhum wajuluuduhum bim<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (4239, '41:21', 41, 21, 'waq<u>aa</u>luu lijuluudihim lima syahidtum \'alayn<u>aa</u> q<u>aa</u>luu an<u>th</u>aqan<u>aa</u>ll<u>aa</u>hulla<u>dz</u>ii an<u>th</u>aqa kulla syay-in wahuwa khalaqakum awwala marratin wa-ilayhi turja\'uun\r\r\n');
INSERT INTO quranLatin VALUES (4240, '41:22', 41, 22, 'wam<u>aa</u> kuntum tastatiruuna an yasyhada \'alaykum sam\'ukum wal<u>aa</u> ab<u>shaa</u>rukum wal<u>aa</u> juluudukum wal<u>aa</u>kin <u>zh</u>anantum annall<u>aa</u>ha l<u>aa</u> ya\'lamu katsiiran mimm<u>aa</u> ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (4241, '41:23', 41, 23, 'wa<u>dzaa</u>likum <u>zh</u>annukumulla<u>dz</u>ii <u>zh</u>anantum birabbikum ard<u>aa</u>kum fa-a<u>sh</u>ba<u>h</u>tum mina lkh<u>aa</u>siriin\r\r\n');
INSERT INTO quranLatin VALUES (4242, '41:24', 41, 24, 'fa-in ya<u>sh</u>biruu fann<u>aa</u>ru matswan lahum wa-in yasta\'tibuu fam<u>aa</u> hum mina lmu\'tabiin\r\r\n');
INSERT INTO quranLatin VALUES (4243, '41:25', 41, 25, 'waqayya<u>dh</u>n<u>aa</u> lahum quran<u>aa</u>-a fazayyanuu lahum m<u>aa</u> bayna aydiihim wam<u>aa</u> khalfahum wa<u>h</u>aqqa \'alayhimu lqawlu fii umamin qad khalat min qablihim mina ljinni wal-insi innahum k<u>aa</u>nuu kh<u>aa</u>siriin\r\r\n');
INSERT INTO quranLatin VALUES (4244, '41:26', 41, 26, 'waq<u>aa</u>lalla<u>dz</u>iina kafaruu l<u>aa</u> tasma\'uu lih<u>aadzaa</u> lqur-<u>aa</u>ni walghaw fiihi la\'allakum taghlibuun\r\r\n');
INSERT INTO quranLatin VALUES (4245, '41:27', 41, 27, 'falanu<u>dz</u>iiqannalla<u>dz</u>iina kafaruu \'a<u>dzaa</u>ban syadiidan walanajziyannahum aswa-alla<u>dz</u>ii k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (4246, '41:28', 41, 28, '<u>dzaa</u>lika jaz<u>aa</u>u a\'d<u>aa</u>-ill<u>aa</u>hi nn<u>aa</u>ru lahum fiih<u>aa</u> d<u>aa</u>ru lkhuldi jaz<u>aa</u>-an bim<u>aa</u> k<u>aa</u>nuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> yaj<u>h</u>aduun\r\r\n');
INSERT INTO quranLatin VALUES (4247, '41:29', 41, 29, 'waq<u>aa</u>lalla<u>dz</u>iina kafaruu rabban<u>aa</u> arin<u>aa</u>lla<u>dz</u>ayni a<u>dh</u>all<u>aa</u>n<u>aa</u> mina ljinni wal-insi naj\'alhum<u>aa</u> ta<u>h</u>ta aqd<u>aa</u>min<u>aa</u> liyakuun<u>aa</u> mina l-asfaliin\r\r\n');
INSERT INTO quranLatin VALUES (4248, '41:30', 41, 30, 'innalla<u>dz</u>iina q<u>aa</u>luu rabbun<u>aa</u>ll<u>aa</u>hu tsumma istaq<u>aa</u>muu tatanazzalu \'alayhimu lmal<u>aa</u>-ikatull<u>aa</u> takh<u>aa</u>fuu wal<u>aa</u> ta<u>h</u>zanuu wa-absyiruu biljannatillatii kuntum tuu\'aduun\r\r\n');
INSERT INTO quranLatin VALUES (4249, '41:31', 41, 31, 'na<u>h</u>nu awliy<u>aa</u>ukum fii l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> wafii l-<u>aa</u>khirati walakum fiih<u>aa</u> m<u>aa</u> tasytahii anfusukum walakum fiih<u>aa</u> m<u>aa</u> tadda\'uun\r\r\n');
INSERT INTO quranLatin VALUES (4250, '41:32', 41, 32, 'nuzulan min ghafuurin ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (4251, '41:33', 41, 33, 'waman a<u>h</u>sanu qawlan mimman da\'<u>aa</u> il<u>aa</u>ll<u>aa</u>hi wa\'amila <u>shaa</u>li<u>h</u>an waq<u>aa</u>la innanii mina lmuslimiin\r\r\n');
INSERT INTO quranLatin VALUES (4252, '41:34', 41, 34, 'wal<u>aa</u> tastawii l<u>h</u>asanatu wal<u>aa</u> ssayyi-atu idfa\'billatii hiya a<u>h</u>sanu fa-i<u>dzaa</u>lla<u>dz</u>ii baynaka wabaynahu \'ad<u>aa</u>watun ka-annahu waliyyun <u>h</u>amiim\r\r\n');
INSERT INTO quranLatin VALUES (4253, '41:35', 41, 35, 'wam<u>aa</u> yulaqq<u>aa</u>h<u>aa</u> ill<u>aa</u>lla<u>dz</u>iina <u>sh</u>abaruu wam<u>aa</u> yulaqq<u>aa</u>h<u>aa</u> ill<u>aa</u> <u>dz</u>uu <u>h</u>a<u>zhzh</u>in \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (4254, '41:36', 41, 36, 'wa-imm<u>aa</u> yanzaghannaka mina sysyay<u>th</u><u>aa</u>ni nazghun fasta\'i<u>dz</u> bil<u>aa</u>hi innahu huwa ssamii\'u l\'aliim<b>u </b>\r\r\n');
INSERT INTO quranLatin VALUES (4255, '41:37', 41, 37, 'wamin <u>aa</u>y<u>aa</u>tihillaylu wannah<u>aa</u>ru wasysyamsu walqamaru l<u>aa</u> tasjuduu lisysyamsi wal<u>aa</u> lilqamari wasjuduu lill<u>aa</u>hi ala<u>dz</u>ii khalaqahunna in kuntum iyy<u>aa</u>hu ta\'buduun\r\r\n');
INSERT INTO quranLatin VALUES (4256, '41:38', 41, 38, 'fa-ini istakbaruu falla<u>dz</u>iina \'inda rabbika yusabbi<u>h</u>uuna lahu billayli wannah<u>aa</u>ri wahum l<u>aa</u> yas-amuun\r\r\n');
INSERT INTO quranLatin VALUES (4257, '41:39', 41, 39, 'wamin <u>aa</u>y<u>aa</u>tihi annaka tar<u>aa</u> l-ar<u>dh</u>a kh<u>aa</u>syi\'atan fa-i<u>dzaa</u> anzaln<u>aa</u> \'alayh<u>aa</u> lm<u>aa</u>-a ihtazzat warabat innalla<u>dz</u>ii a<u>h</u>y<u>aa</u>h<u>aa</u> lamu<u>h</u>yii lmawt<u>aa</u> innahu \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (4258, '41:40', 41, 40, 'innalla<u>dz</u>iina yul<u>h</u>iduuna fii <u>aa</u>y<u>aa</u>tin<u>aa</u> l<u>aa</u> yakhfawna \'alayn<u>aa</u> afaman yulq<u>aa</u> fii nn<u>aa</u>ri khayrun amman ya/tii <u>aa</u>minan yawma lqiy<u>aa</u>mati i\'maluu m<u>aa</u> syi/tum innahu bim<u>aa</u> ta\'maluuna ba<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (4259, '41:41', 41, 41, 'innalla<u>dz</u>iina kafaruu bi<u>dzdz</u>ikri lamm<u>aa</u> j<u>aa</u>-ahum wa-innahu lakit<u>aa</u>bun \'aziiz\r\r\n');
INSERT INTO quranLatin VALUES (4260, '41:42', 41, 42, 'l<u>aa</u> ya/tiihi lb<u>aath</u>ilu min bayni yadayhi wal<u>aa</u> min khalfihi tanziilun min <u>h</u>akiimin <u>h</u>amiid\r\r\n');
INSERT INTO quranLatin VALUES (4261, '41:43', 41, 43, 'm<u>aa</u> yuq<u>aa</u>lu laka ill<u>aa</u> m<u>aa</u> qad qiila lirrusuli min qablika inna rabbaka la<u>dz</u>uu maghfiratin wa<u>dz</u>uu \'iq<u>aa</u>bin liim\r\r\n');
INSERT INTO quranLatin VALUES (4262, '41:44', 41, 44, 'walaw ja\'aln<u>aa</u>hu qur-<u>aa</u>nan a\'jamiyyan laq<u>aa</u>luu lawl<u>aa</u> fu<u>shsh</u>ilat <u>aa</u>y<u>aa</u>tuhu a-a\'jamiyyun wa\'arabiyyun qul huwa lilla<u>dz</u>iina <u>aa</u>manuu hudan wasyif<u>aa</u>un walla<u>dz</u>iina l<u>aa</u> yu/minuuna fii <u>aatsaa</u>nihim waqrun wahuwa \'alayhim \'aman ul<u>aa</u>-ika yun<u>aa</u>dawna min mak<u>aa</u>nin ba\'iid\r\r\n');
INSERT INTO quranLatin VALUES (4263, '41:45', 41, 45, 'walaqad <u>aa</u>tayn<u>aa</u> muus<u>aa</u> lkit<u>aa</u>ba fakhtulifa fiihi walawl<u>aa</u> kalimatun sabaqat min rabbika laqu<u>dh</u>iya baynahum wa-innahum lafii syakkin minhu muriib\r\r\n');
INSERT INTO quranLatin VALUES (4264, '41:46', 41, 46, 'man \'amila <u>shaa</u>li<u>h</u>an falinafsihi waman as<u>aa</u>-a fa\'alayh<u>aa</u> wam<u>aa</u> rabbuka bi<u>zh</u>all<u>aa</u>min lil\'abiid\r\r\n');
INSERT INTO quranLatin VALUES (4265, '41:47', 41, 47, 'ilayhi yuraddu \'ilmu ss<u>aa</u>\'ati wam<u>aa</u> takhruju min tsamar<u>aa</u>tin min akm<u>aa</u>mih<u>aa</u> wam<u>aa</u> ta<u>h</u>milu min unts<u>aa</u> wal<u>aa</u> ta<u>dh</u>a\'u ill<u>aa</u> bi\'ilmihi wayawma yun<u>aa</u>diihim ayna syurak<u>aa</u>-ii q<u>aa</u>luu <u>aadz</u>ann<u>aa</u>ka m<u>aa</u> minn<u>aa</u> min syahiid\r\r\n');
INSERT INTO quranLatin VALUES (4266, '41:48', 41, 48, 'wa<u>dh</u>alla \'anhum m<u>aa</u> k<u>aa</u>nuu yad\'uuna min qablu wa<u>zh</u>annuu m<u>aa</u> lahum min ma<u>h</u>ii<u>sh</u>\r\r\n');
INSERT INTO quranLatin VALUES (4267, '41:49', 41, 49, 'l<u>aa</u> yas-amu l-ins<u>aa</u>nu min du\'<u>aa</u>-i lkhayri wa-in massahu sysyarru fayauusun qanuu<u>th</u>\r\r\n');
INSERT INTO quranLatin VALUES (4268, '41:50', 41, 50, 'wala-in a<u>dz</u>aqn<u>aa</u>hu ra<u>h</u>matan minn<u>aa</u> min ba\'di <u>dh</u>arr<u>aa</u>-a massat-hu layaquulanna h<u>aadzaa</u> lii wam<u>aa</u> a<u>zh</u>unnu ss<u>aa</u>\'ata q<u>aa</u>-imatan wala-in ruji\'tu il<u>aa</u> rabbii inna lii \'indahu lal<u>h</u>usn<u>aa</u> falanunabbi-annalla<u>dz</u>iina kafaruu bim<u>aa</u> \'amiluu walanu<u>dz</u>iiqannahum min \'a<u>dzaa</u>bin ghalii<u>zh</u>\r\r\n');
INSERT INTO quranLatin VALUES (4269, '41:51', 41, 51, 'wa-i<u>dzaa</u> an\'amn<u>aa</u> \'al<u>aa</u> l-ins<u>aa</u>ni a\'ra<u>dh</u>a wana<u>aa</u> bij<u>aa</u>nibihi wa-i<u>dzaa</u> massahu sysyarru fa<u>dz</u>uu du\'<u>aa</u>-in \'arii<u>dh</u>\r\r\n');
INSERT INTO quranLatin VALUES (4270, '41:52', 41, 52, 'qul ara-aytum in k<u>aa</u>na min \'indill<u>aa</u>hi tsumma kafartum bihi man a<u>dh</u>allu mimman huwa fii syiq<u>aa</u>qin ba\'iid\r\r\n');
INSERT INTO quranLatin VALUES (4271, '41:53', 41, 53, 'sanuriihim <u>aa</u>y<u>aa</u>tin<u>aa</u> fii l-<u>aa</u>f<u>aa</u>qi wafii anfusihim <u>h</u>att<u>aa</u> yatabayyana lahum annahu l<u>h</u>aqqu awa lam yakfi birabbika annahu \'al<u>aa</u> kulli syay-in syahiid\r\r\n');
INSERT INTO quranLatin VALUES (4272, '41:54', 41, 54, 'al<u>aa</u> innahum fii miryatin min liq<u>aa</u>-i rabbihim l<u>aa</u> innahu bikulli syay-in mu<u>h</u>ii<u>th</u>\r\r\n');
INSERT INTO quranLatin VALUES (4273, '42:1', 42, 1, '<u>haa</u>-miim</u>\r\r\n');
INSERT INTO quranLatin VALUES (4274, '42:2', 42, 2, '\'ayn-siin-q<u>aa</u>f</u>\r\r\n');
INSERT INTO quranLatin VALUES (4275, '42:3', 42, 3, 'ka<u>dzaa</u>lika yuu<u>h</u>ii ilayka wa-il<u>aa</u>lla<u>dz</u>iina min qablikall<u>aa</u>hu l\'aziizu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (4276, '42:4', 42, 4, 'lahu m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii l-ar<u>dh</u>i wahuwa l\'aliyyu l\'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (4277, '42:5', 42, 5, 'tak<u>aa</u>du ssam<u>aa</u>w<u>aa</u>tu yatafa<u>ththh</u>arna min fawqihinna walmal<u>aa</u>-ikatu yusabbi<u>h</u>uuna bi<u>h</u>amdi rabbihim wayastaghfiruuna liman fii l-ar<u>dh</u>i l<u>aa</u> innall<u>aa</u>ha huwa lghafuuru rra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (4278, '42:6', 42, 6, 'walla<u>dz</u>iina ittakha<u>dz</u>uu min duunihi awliy<u>aa</u>-all<u>aa</u>hu <u>h</u>afii<u>zh</u>un \'alayhim wam<u>aa</u> anta \'alayhim biwakiil\r\r\n');
INSERT INTO quranLatin VALUES (4279, '42:7', 42, 7, 'waka<u>dzaa</u>lika aw<u>h</u>ayn<u>aa</u> ilayka qur-<u>aa</u>nan \'arabiyyan litun<u>dz</u>ira umma lqur<u>aa</u> waman <u>h</u>awlah<u>aa</u> watun<u>dz</u>ira yawma ljam\'i l<u>aa</u> rayba fiihi fariiqun fii ljannati wafariiqun fii ssa\'iir\r\r\n');
INSERT INTO quranLatin VALUES (4280, '42:8', 42, 8, 'walaw sy<u>aa</u>-all<u>aa</u>hu laja\'alahum ummatan w<u>aah</u>idatan wal<u>aa</u>kin yudkhilu man yasy<u>aa</u>u fii ra<u>h</u>matihi wa<u>zhzhaa</u>limuuna m<u>aa</u> lahum min waliyyin wal<u>aa</u> na<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (4281, '42:9', 42, 9, 'ami ittakha<u>dz</u>uu min duunihi awliy<u>aa</u>-a fal<u>aa</u>hu huwa lwaliyyu wahuwa yu<u>h</u>yii lmawt<u>aa</u> wahuwa \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (4282, '42:10', 42, 10, 'wam<u>aa</u> ikhtalaftum fiihi min syay-in fa<u>h</u>ukmuhu il<u>aa</u>ll<u>aa</u>hi <u>dzaa</u>likumull<u>aa</u>hu rabbii \'alayhi tawakkaltu wa-ilayhi uniib\r\r\n');
INSERT INTO quranLatin VALUES (4283, '42:11', 42, 11, 'f<u>aath</u>iru ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i ja\'ala lakum min anfusikum azw<u>aa</u>jan wamina l-an\'<u>aa</u>mi azw<u>aa</u>jan ya<u>dz</u>raukum fiihi laysa kamitslihi syay-un wahuwa ssamii\'u lba<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (4284, '42:12', 42, 12, 'lahu maq<u>aa</u>liidu ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i yabsu<u>th</u>u rrizqa liman yasy<u>aa</u>u wayaqdiru innahu bikulli syay-in \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (4285, '42:13', 42, 13, 'syara\'a lakum mina ddiini m<u>aa</u> wa<u>shshaa</u> bihi nuu<u>h</u>an walla<u>dz</u>ii aw<u>h</u>ayn<u>aa</u> ilayka wam<u>aa</u> wa<u>shsh</u>ayn<u>aa</u> bihi ibr<u>aa</u>hiima wamuus<u>aa</u> wa\'iis<u>aa</u> an aqiimuu ddiina wal<u>aa</u> tatafarraquu fiihi kabura \'al<u>aa</u> lmusyrikiina m<u>aa</u> tad\'uuhum ilayhill<u>aa</u>hu yajtabii ilayhi man yasy<u>aa</u>u wayahdii ilayhi man yuniib\r\r\n');
INSERT INTO quranLatin VALUES (4286, '42:14', 42, 14, 'wam<u>aa</u> tafarraquu ill<u>aa</u> min ba\'di m<u>aa</u> j<u>aa</u>-ahumu l\'ilmu baghyan baynahum walawl<u>aa</u> kalimatun sabaqat min rabbika il<u>aa</u> ajalin musamman laqu<u>dh</u>iya baynahum wa-innalla<u>dz</u>iina uuritsuu lkit<u>aa</u>ba min ba\'dihim lafii syakkin minhu muriib\r\r\n');
INSERT INTO quranLatin VALUES (4287, '42:15', 42, 15, 'fali<u>dzaa</u>lika fad\'u wastaqim kam<u>aa</u> umirta wal<u>aa</u> tattabi\'ahw<u>aa</u>-ahum waqul <u>aa</u>mantu bim<u>aa</u> anzalall<u>aa</u>hu min kit<u>aa</u>bin waumirtu li-a\'dila baynakumll<u>aa</u>hu rabbun<u>aa</u> warabbukum lan<u>aa</u> a\'m<u>aa</u>lun<u>aa</u> walakum a\'m<u>aa</u>lukum l<u>aa</u> <u>h</u>ujjata baynan<u>aa</u> wabaynakumull<u>aa</u>hu yajma\'u baynan<u>aa</u> wa-ilayhi lma<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (4288, '42:16', 42, 16, 'walla<u>dz</u>iina yu<u>haa</u>jjuuna fiill<u>aa</u>hi min ba\'di m<u>aa</u> istujiiba lahu <u>h</u>ujjatuhum d<u>aah</u>i<u>dh</u>atun \'inda rabbihim wa\'alayhim gha<u>dh</u>abun walahum \'a<u>dzaa</u>bun syadiid\r\r\n');
INSERT INTO quranLatin VALUES (4289, '42:17', 42, 17, '<B>al</b>l<u>aa</u>hulla<u>dz</u>ii anzala lkit<u>aa</u>ba bil<u>h</u>aqqi walmiiz<u>aa</u>ni wam<u>aa</u> yudriika la\'alla ss<u>aa</u>\'ata qariib\r\r\n');
INSERT INTO quranLatin VALUES (4290, '42:18', 42, 18, 'yasta\'jilu bih<u>aa</u>lla<u>dz</u>iina l<u>aa</u> yu/minuuna bih<u>aa</u> walla<u>dz</u>iina <u>aa</u>manuu musyfiquuna minh<u>aa</u> waya\'lamuuna annah<u>aa</u> l<u>h</u>aqqu l<u>aa</u> innalla<u>dz</u>iina yum<u>aa</u>ruuna fii ss<u>aa</u>\'ati lafii <u>dh</u>al<u>aa</u>lin ba\'iid\r\r\n');
INSERT INTO quranLatin VALUES (4291, '42:19', 42, 19, '<B>al</b>l<u>aa</u>hu la<u>th</u>iifun bi\'ib<u>aa</u>dihi yarzuqu man yasy<u>aa</u>u wahuwa lqawiyyu l\'aziiz\r\r\n');
INSERT INTO quranLatin VALUES (4292, '42:20', 42, 20, 'man k<u>aa</u>na yuriidu <u>h</u>artsa l-<u>aa</u>khirati nazid lahu fii <u>h</u>artsihi waman k<u>aa</u>na yuriidu <u>h</u>artsa dduny<u>aa</u> nu/tihi minh<u>aa</u> wam<u>aa</u> lahu fii l-<u>aa</u>khirati min na<u>sh</u>iib\r\r\n');
INSERT INTO quranLatin VALUES (4293, '42:21', 42, 21, 'am lahum syurak<u>aa</u>u syara\'uu lahum mina ddiini m<u>aa</u> lam ya/<u>dz</u>an bihill<u>aa</u>hu walawl<u>aa</u> kalimatu lfa<u>sh</u>li laqu<u>dh</u>iya baynahum wa-inna <u>zhzhaa</u>limiina lahum \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (4294, '42:22', 42, 22, 'tar<u>aa</u> <u>zhzhaa</u>limiina musyfiqiina mimm<u>aa</u> kasabuu wahuwa w<u>aa</u>qi\'un bihim walla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti fii raw<u>daa</u>ti ljann<u>aa</u>ti lahum m<u>aa</u> yasy<u>aa</u>uuna \'inda rabbihim <u>dzaa</u>lika huwa lfa<u>dh</u>lu lkabiir\r\r\n');
INSERT INTO quranLatin VALUES (4295, '42:23', 42, 23, '<u>dzaa</u>likalla<u>dz</u>ii yubasysyirull<u>aa</u>hu \'ib<u>aa</u>dahulla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti qul l<u>aa</u> as-alukum \'alayhi ajran ill<u>aa</u> lmawaddata fii lqurb<u>aa</u> waman yaqtarif <u>h</u>asanatan nazid lahu fiih<u>aa</u> <u>h</u>usnan innall<u>aa</u>ha ghafuurun syakuur\r\r\n');
INSERT INTO quranLatin VALUES (4296, '42:24', 42, 24, 'am yaquuluuna iftar<u>aa</u> \'al<u>aa</u>ll<u>aa</u>hi ka<u>dz</u>iban fa-in yasya-ill<u>aa</u>hu yakhtim \'al<u>aa</u> qalbika wayam<u>h</u>ull<u>aa</u>hu lb<u>aath</u>ila wayu<u>h</u>iqqu l<u>h</u>aqqa bikalim<u>aa</u>tihi innahu \'aliimun bi<u>dzaa</u>ti <u>shsh</u>uduur\r\r\n');
INSERT INTO quranLatin VALUES (4297, '42:25', 42, 25, 'wahuwalla<u>dz</u>ii yaqbalu ttawbata \'an \'ib<u>aa</u>dihi waya\'fuu \'ani ssayyi-ati waya\'lamu m<u>aa</u> taf\'aluun\r\r\n');
INSERT INTO quranLatin VALUES (4298, '42:26', 42, 26, 'wayastajiibulla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti wayaziiduhum min fa<u>dh</u>lihi walk<u>aa</u>firuuna lahum \'a<u>dzaa</u>bun syadiid\r\r\n');
INSERT INTO quranLatin VALUES (4299, '42:27', 42, 27, 'walaw basa<u>th</u>all<u>aa</u>hu rrizqa li\'ib<u>aa</u>dihi labaghaw fii l-ar<u>dh</u>i wal<u>aa</u>kin yunazzilu biqadarin m<u>aa</u> yasy<u>aa</u>u innahu bi\'ib<u>aa</u>dihi khabiirun ba<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (4300, '42:28', 42, 28, 'wahuwalla<u>dz</u>ii yunazzilu lghaytsa min ba\'di m<u>aa</u> qana<u>th</u>uu wayansyuru ra<u>h</u>matahu wahuwa lwaliyyu l<u>h</u>amiid\r\r\n');
INSERT INTO quranLatin VALUES (4301, '42:29', 42, 29, 'wamin <u>aa</u>y<u>aa</u>tihi khalqu ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wam<u>aa</u> batstsa fiihim<u>aa</u> min d<u>aa</u>bbatin wahuwa \'al<u>aa</u> jam\'ihim i<u>dzaa</u> yasy<u>aa</u>u qadiir\r\r\n');
INSERT INTO quranLatin VALUES (4302, '42:30', 42, 30, 'wam<u>aa</u> a<u>shaa</u>bakum min mu<u>sh</u>iibatin fabim<u>aa</u> kasabat aydiikum waya\'fuu \'an katsiir\r\r\n');
INSERT INTO quranLatin VALUES (4303, '42:31', 42, 31, 'wam<u>aa</u> antum bimu\'jiziina fii l-ar<u>dh</u>i wam<u>aa</u> lakum min duunill<u>aa</u>hi min waliyyin wal<u>aa</u> na<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (4304, '42:32', 42, 32, 'wamin <u>aa</u>y<u>aa</u>tihi ljaw<u>aa</u>ri fii lba<u>h</u>ri kal-a\'l<u>aa</u>m\r\r\n');
INSERT INTO quranLatin VALUES (4305, '42:33', 42, 33, 'in yasya/ yuskini rrii<u>h</u>a faya<u>zh</u>lalna raw<u>aa</u>kida \'al<u>aa</u> <u>zh</u>ahrihi inna fii <u>dzaa</u>lika la<u>aa</u>y<u>aa</u>tin likulli <u>sh</u>abb<u>aa</u>rin syakuur\r\r\n');
INSERT INTO quranLatin VALUES (4306, '42:34', 42, 34, 'aw yuubiqhunna bim<u>aa</u> kasabuu waya\'fu \'an katsiir\r\r\n');
INSERT INTO quranLatin VALUES (4307, '42:35', 42, 35, 'waya\'lamalla<u>dz</u>iina yuj<u>aa</u>diluuna fii <u>aa</u>y<u>aa</u>tin<u>aa</u> m<u>aa</u> lahum min ma<u>h</u>ii<u>sh</u>\r\r\n');
INSERT INTO quranLatin VALUES (4308, '42:36', 42, 36, 'fam<u>aa</u> uutiitum min syay-in famat<u>aa</u>\'u l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> wam<u>aa</u> \'indall<u>aa</u>hi khayrun wa-abq<u>aa</u> lilla<u>dz</u>iina <u>aa</u>manuu wa\'al<u>aa</u> rabbihim yatawakkaluun\r\r\n');
INSERT INTO quranLatin VALUES (4309, '42:37', 42, 37, 'walla<u>dz</u>iina yajtanibuuna kab<u>aa</u>-ira l-itsmi walfaw<u>aah</u>isya wa-i<u>dzaa</u> m<u>aa</u> gha<u>dh</u>ibuu hum yaghfiruun\r\r\n');
INSERT INTO quranLatin VALUES (4310, '42:38', 42, 38, 'walla<u>dz</u>iina istaj<u>aa</u>buu lirabbihim wa-aq<u>aa</u>muu <u>shsh</u>al<u>aa</u>ta wa-amruhum syuur<u>aa</u> baynahum wamimm<u>aa</u> razaqn<u>aa</u>hum yunfiquun\r\r\n');
INSERT INTO quranLatin VALUES (4311, '42:39', 42, 39, 'walla<u>dz</u>iina i<u>dzaa</u> a<u>shaa</u>bahumu lbaghyu hum yanta<u>sh</u>iruun\r\r\n');
INSERT INTO quranLatin VALUES (4312, '42:40', 42, 40, 'wajaz<u>aa</u>u sayyi-atin sayyi-atun mitsluh<u>aa</u> faman \'af<u>aa</u> wa-a<u>sh</u>la<u>h</u>a fa-ajruhu \'al<u>aa</u>ll<u>aa</u>hi innahu l<u>aa</u> yu<u>h</u>ibbu <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (4313, '42:41', 42, 41, 'walamani inta<u>sh</u>ara ba\'da <u>zh</u>ulmihi faul<u>aa</u>-ika m<u>aa</u> \'alayhim min sabiil\r\r\n');
INSERT INTO quranLatin VALUES (4314, '42:42', 42, 42, 'innam<u>aa</u> ssabiilu \'al<u>aa</u>lla<u>dz</u>iina ya<u>zh</u>limuuna nn<u>aa</u>sa wayabghuuna fii l-ar<u>dh</u>i bighayri l<u>h</u>aqqi ul<u>aa</u>-ika lahum \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (4315, '42:43', 42, 43, 'walaman <u>sh</u>abara waghafara inna <u>dzaa</u>lika lamin \'azmi l-umuur\r\r\n');
INSERT INTO quranLatin VALUES (4316, '42:44', 42, 44, 'waman yu<u>dh</u>lilill<u>aa</u>hu fam<u>aa</u> lahu min waliyyin min ba\'dihi watar<u>aa</u> <u>zhzhaa</u>limiina lamm<u>aa</u> ra-awuu l\'a<u>dzaa</u>ba yaquuluuna hal il<u>aa</u> maraddin min sabiil\r\r\n');
INSERT INTO quranLatin VALUES (4317, '42:45', 42, 45, 'watar<u>aa</u>hum yu\'ra<u>dh</u>uuna \'alayh<u>aa</u> kh<u>aa</u>syi\'iina mina <u>dzdz</u>ulli yan<u>zh</u>uruuna min <u>th</u>arfin khafiyyin waq<u>aa</u>lalla<u>dz</u>iina <u>aa</u>manuu inna lkh<u>aa</u>siriinalla<u>dz</u>iina khasiruu anfusahum wa-ahliihim yawma lqiy<u>aa</u>mati l<u>aa</u> inna <u>zhzhaa</u>limiina fii \'a<u>dzaa</u>bin muqiim\r\r\n');
INSERT INTO quranLatin VALUES (4318, '42:46', 42, 46, 'wam<u>aa</u> k<u>aa</u>na lahum min awliy<u>aa</u>-a yan<u>sh</u>uruunahum min duunill<u>aa</u>hi waman yu<u>dh</u>lilill<u>aa</u>hu fam<u>aa</u> lahu min sabiil\r\r\n');
INSERT INTO quranLatin VALUES (4319, '42:47', 42, 47, 'istajiibuu lirabbikum min qabli an ya/tiya yawmun l<u>aa</u> maradda lahu minall<u>aa</u>hi m<u>aa</u> lakum min malja-in yawma-i<u>dz</u>in wam<u>aa</u> lakum min nakiir\r\r\n');
INSERT INTO quranLatin VALUES (4320, '42:48', 42, 48, 'fa-in a\'ra<u>dh</u>uu fam<u>aa</u> arsaln<u>aa</u>ka \'alayhim <u>h</u>afii<u>zh</u>an in \'alayka ill<u>aa</u> lbal<u>aa</u>ghu wa-inn<u>aa</u> i<u>dzaa</u> a<u>dz</u>aqn<u>aa</u> l-ins<u>aa</u>na minn<u>aa</u> ra<u>h</u>matan fari<u>h</u>a bih<u>aa</u> wa-in tu<u>sh</u>ibhum sayyi-atun bim<u>aa</u> qaddamat aydiihim fa-inna l-ins<u>aa</u>na kafuur\r\r\n');
INSERT INTO quranLatin VALUES (4321, '42:49', 42, 49, 'lill<u>aa</u>hi mulku ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i yakhluqu m<u>aa</u> yasy<u>aa</u>u yahabu liman yasy<u>aa</u>u in<u>aa</u>tsan wayahabu liman yasy<u>aa</u>u <u>dzdz</u>ukuur\r\r\n');
INSERT INTO quranLatin VALUES (4322, '42:50', 42, 50, 'aw yuzawwijuhum <u>dz</u>ukr<u>aa</u>nan wa-in<u>aa</u>tsan wayaj\'alu man yasy<u>aa</u>u \'aqiiman innahu \'aliimun qadiir\r\r\n');
INSERT INTO quranLatin VALUES (4323, '42:51', 42, 51, 'wam<u>aa</u> k<u>aa</u>na libasyarin an yukallimahull<u>aa</u>hu ill<u>aa</u> wa<u>h</u>yan aw min war<u>aa</u>-i <u>h</u>ij<u>aa</u>bin aw yursila rasuulan fayuu<u>h</u>iya bi-i<u>dz</u>nihi m<u>aa</u> yasy<u>aa</u>u innahu \'aliyyun <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (4324, '42:52', 42, 52, 'waka<u>dzaa</u>lika aw<u>h</u>ayn<u>aa</u> ilayka ruu<u>h</u>an min amrin<u>aa</u> m<u>aa</u> kunta tadrii m<u>aa</u> lkit<u>aa</u>bu wal<u>aa</u> l-iim<u>aa</u>nu wal<u>aa</u>kin ja\'aln<u>aa</u>hu nuuran nahdii bihi man nasy<u>aa</u>u min \'ib<u>aa</u>din<u>aa</u> wa-innaka latahdii il<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim\r\r\n');
INSERT INTO quranLatin VALUES (4325, '42:53', 42, 53, '<u>sh</u>ir<u>aath</u>ill<u>aa</u>hilla<u>dz</u>ii lahu m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii l-ar<u>dh</u>i l<u>aa</u> il<u>aa</u>ll<u>aa</u>hi ta<u>sh</u>iiru l-umuur\r\r\n');
INSERT INTO quranLatin VALUES (4326, '43:1', 43, 1, '<u>haa</u>-miim</u>\r\r\n');
INSERT INTO quranLatin VALUES (4327, '43:2', 43, 2, 'walkit<u>aa</u>bi lmubiin\r\r\n');
INSERT INTO quranLatin VALUES (4328, '43:3', 43, 3, 'inn<u>aa</u> ja\'aln<u>aa</u>hu qur-<u>aa</u>nan \'arabiyyan la\'allakum ta\'qiluunaa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4329, '43:4', 43, 4, 'wa-innahu fii ummi lkit<u>aa</u>bi ladayn<u>aa</u> la\'aliyyun <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (4330, '43:5', 43, 5, 'afana<u>dh</u>ribu \'ankumu <u>dzdz</u>ikra <u>sh</u>af<u>h</u>an an kuntum qawman musrifiin\r\r\n');
INSERT INTO quranLatin VALUES (4331, '43:6', 43, 6, 'wakam arsaln<u>aa</u> min nabiyyin fii l-awwaliin\r\r\n');
INSERT INTO quranLatin VALUES (4332, '43:7', 43, 7, 'wam<u>aa</u> ya/tiihim min nabiyyin ill<u>aa</u> k<u>aa</u>nuu bihi yastahzi-uun\r\r\n');
INSERT INTO quranLatin VALUES (4333, '43:8', 43, 8, 'fa-ahlakn<u>aa</u> asyadda minhum ba<u>th</u>syan wama<u>daa</u> matsalu l-awwaliin\r\r\n');
INSERT INTO quranLatin VALUES (4334, '43:9', 43, 9, 'wala-in sa-altahum man khalaqa ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a layaquulunna khalaqahunna l\'aziizu l\'aliim\r\r\n');
INSERT INTO quranLatin VALUES (4335, '43:10', 43, 10, '<B>al</b>la<u>dz</u>ii ja\'ala lakumu l-ar<u>dh</u>a mahdan waja\'ala lakum fiih<u>aa</u> subulan la\'allakum tahtaduun\r\r\n');
INSERT INTO quranLatin VALUES (4336, '43:11', 43, 11, 'walla<u>dz</u>ii nazzala mina ssam<u>aa</u>-i m<u>aa</u>-an biqadarin fa-ansyarn<u>aa</u> bihi baldatan maytan ka<u>dzaa</u>lika tukhrajuun\r\r\n');
INSERT INTO quranLatin VALUES (4337, '43:12', 43, 12, 'walla<u>dz</u>ii khalaqa l-azw<u>aa</u>ja kullah<u>aa</u> waja\'ala lakum mina lfulki wal-an\'<u>aa</u>mi m<u>aa</u> tarkabuun\r\r\n');
INSERT INTO quranLatin VALUES (4338, '43:13', 43, 13, 'litastawuu \'al<u>aa</u> <u>zh</u>uhuurihi tsumma ta<u>dz</u>kuruu ni\'mata rabbikum i<u>dzaa</u> istawaytum \'alayhi wataquuluu sub<u>haa</u>nalla<u>dz</u>ii sakhkhara lan<u>aa</u> h<u>aadzaa</u> wam<u>aa</u> kunn<u>aa</u> lahu muqriniin\r\r\n');
INSERT INTO quranLatin VALUES (4339, '43:14', 43, 14, 'wa-inn<u>aa</u> il<u>aa</u> rabbin<u>aa</u> lamunqalibuun\r\r\n');
INSERT INTO quranLatin VALUES (4340, '43:15', 43, 15, 'waja\'aluu lahu min \'ib<u>aa</u>dihi juz-an inna l-ins<u>aa</u>na lakafuurun mubiin\r\r\n');
INSERT INTO quranLatin VALUES (4341, '43:16', 43, 16, 'ami ittakha<u>dz</u>a mimm<u>aa</u> yakhluqu ban<u>aa</u>tin wa-a<u>sh</u>f<u>aa</u>kum bilbaniin\r\r\n');
INSERT INTO quranLatin VALUES (4342, '43:17', 43, 17, 'wa-i<u>dzaa</u> busysyira a<u>h</u>aduhum bim<u>aa</u> <u>dh</u>araba lirra<u>h</u>m<u>aa</u>ni matsalan <u>zh</u>alla wajhuhu muswaddan wahuwa ka<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (4343, '43:18', 43, 18, 'awaman yunasysyau fii l<u>h</u>ilyati wahuwa fii lkhi<u>shaa</u>mi ghayru mubiin\r\r\n');
INSERT INTO quranLatin VALUES (4344, '43:19', 43, 19, 'waja\'aluu lmal<u>aa</u>-ikatalla<u>dz</u>iina hum \'ib<u>aa</u>du rra<u>h</u>m<u>aa</u>ni in<u>aa</u>tsan asyahiduu khalqahum satuktabu syah<u>aa</u>datuhum wayus-aluun\r\r\n');
INSERT INTO quranLatin VALUES (4345, '43:20', 43, 20, 'waq<u>aa</u>luu law sy<u>aa</u>-a rra<u>h</u>m<u>aa</u>nu m<u>aa</u> \'abadn<u>aa</u>hum m<u>aa</u> lahum bi<u>dzaa</u>lika min \'ilmin in hum ill<u>aa</u> yakhru<u>sh</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (4346, '43:21', 43, 21, 'am <u>aa</u>tayn<u>aa</u>hum kit<u>aa</u>ban min qablihi fahum bihi mustamsikuun\r\r\n');
INSERT INTO quranLatin VALUES (4347, '43:22', 43, 22, 'bal q<u>aa</u>luu inn<u>aa</u> wajadn<u>aa</u> <u>aa</u>b<u>aa</u>-an<u>aa</u> \'al<u>aa</u> ummatin wa-inn<u>aa</u> \'al<u>aa</u> <u>aa</u>ts<u>aa</u>rihim muhtaduun\r\r\n');
INSERT INTO quranLatin VALUES (4348, '43:23', 43, 23, 'waka<u>dzaa</u>lika m<u>aa</u> arsaln<u>aa</u> min qablika fii qaryatin min na<u>dz</u>iirin ill<u>aa</u> q<u>aa</u>la mutrafuuh<u>aa</u> inn<u>aa</u> wajadn<u>aa</u> <u>aa</u>b<u>aa</u>-an<u>aa</u> \'al<u>aa</u> ummatin wa-inn<u>aa</u> \'al<u>aa</u> <u>aa</u>ts<u>aa</u>rihim muqtaduun\r\r\n');
INSERT INTO quranLatin VALUES (4349, '43:24', 43, 24, 'q<u>aa</u>la awa law ji/tukum bi-ahd<u>aa</u> mimm<u>aa</u> wajadtum \'alayhi <u>aa</u>b<u>aa</u>-akum q<u>aa</u>luu inn<u>aa</u> bim<u>aa</u> ursiltum bihi k<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (4350, '43:25', 43, 25, 'fantaqamn<u>aa</u> minhum fan<u>zh</u>ur kayfa k<u>aa</u>na \'<u>aa</u>qibatu lmuka<u>dzdz</u>ibiin\r\r\n');
INSERT INTO quranLatin VALUES (4351, '43:26', 43, 26, 'wa-i<u>dz</u> q<u>aa</u>la ibr<u>aa</u>hiimu li-abiihi waqawmihi innanii bar<u>aa</u>un mimm<u>aa</u> ta\'buduun\r\r\n');
INSERT INTO quranLatin VALUES (4352, '43:27', 43, 27, 'ill<u>aa</u>lla<u>dz</u>ii fa<u>th</u>aranii fa-innahu sayahdiin\r\r\n');
INSERT INTO quranLatin VALUES (4353, '43:28', 43, 28, 'waja\'alah<u>aa</u> kalimatan b<u>aa</u>qiyatan fii \'aqibihi la\'allahum yarji\'uun\r\r\n');
INSERT INTO quranLatin VALUES (4354, '43:29', 43, 29, 'bal matta\'tu h<u>aa</u>ul<u>aa</u>-i wa<u>aa</u>b<u>aa</u>-ahum <u>h</u>att<u>aa</u> j<u>aa</u>-ahumu l<u>h</u>aqqu warasuulun mubiin\r\r\n');
INSERT INTO quranLatin VALUES (4355, '43:30', 43, 30, 'walamm<u>aa</u> j<u>aa</u>-ahumu l<u>h</u>aqqu q<u>aa</u>luu h<u>aadzaa</u> si<u>h</u>run wa-inn<u>aa</u> bihi k<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (4356, '43:31', 43, 31, 'waq<u>aa</u>luu lawl<u>aa</u> nuzzila h<u>aadzaa</u> lqur-<u>aa</u>nu \'al<u>aa</u> rajulin mina lqaryatayni \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (4357, '43:32', 43, 32, 'ahum yaqsimuuna ra<u>h</u>mata rabbika na<u>h</u>nu qasamn<u>aa</u> baynahum ma\'iisyatahum fii l<u>h</u>ay<u>aa</u>ti dduny<u>aa</u> warafa\'n<u>aa</u> ba\'<u>dh</u>ahum fawqa ba\'<u>dh</u>in daraj<u>aa</u>tin liyattakhi<u>dz</u>a ba\'<u>dh</u>uhum ba\'<u>dh</u>an sukhriyyan wara<u>h</u>matu rabbika khayrun mimm<u>aa</u> yajma\'uun\r\r\n');
INSERT INTO quranLatin VALUES (4358, '43:33', 43, 33, 'walawl<u>aa</u> an yakuuna nn<u>aa</u>su ummatan w<u>aah</u>idatan laja\'aln<u>aa</u> liman yakfuru birra<u>h</u>m<u>aa</u>ni libuyuutihim suqufan min fi<u>dhdh</u>atin wama\'<u>aa</u>rija \'alayh<u>aa</u> ya<u>zh</u>haruun\r\r\n');
INSERT INTO quranLatin VALUES (4359, '43:34', 43, 34, 'walibuyuutihim abw<u>aa</u>ban wasururan \'alayh<u>aa</u> yattaki-uun\r\r\n');
INSERT INTO quranLatin VALUES (4360, '43:35', 43, 35, 'wazukhrufan wa-in kullu <u>dzaa</u>lika lamm<u>aa</u> mat<u>aa</u>\'u l<u>h</u>ay<u>aa</u>ti dduny<u>aa </u>wal-<u>aa</u>khiratu \'inda rabbika lilmuttaqiin\r\r\n');
INSERT INTO quranLatin VALUES (4361, '43:36', 43, 36, 'waman ya\'syu \'an <u>dz</u>ikri rra<u>h</u>m<u>aa</u>ni nuqayyi<u>dh</u> lahu syay<u>th</u><u>aa</u>nan fahuwa lahu qariin\r\r\n');
INSERT INTO quranLatin VALUES (4362, '43:37', 43, 37, 'wa-innahum laya<u>sh</u>udduunahum \'ani ssabiili waya<u>h</u>sabuuna annahum muhtaduun\r\r\n');
INSERT INTO quranLatin VALUES (4363, '43:38', 43, 38, '<u>h</u>att<u>aa</u> i<u>dzaa</u> j<u>aa</u>-an<u>aa</u> q<u>aa</u>la y<u>aa</u> layta baynii wabaynaka bu\'da lmasyriqayni fabi/sa lqariin\r\r\n');
INSERT INTO quranLatin VALUES (4364, '43:39', 43, 39, 'walan yanfa\'akumu lyawma i<u>dz</u> <u>zh</u>alamtum annakum fii l\'a<u>dzaa</u>bi musytarikuun\r\r\n');
INSERT INTO quranLatin VALUES (4365, '43:40', 43, 40, 'afa-anta tusmi\'u <u>shsh</u>umma aw tahdii l\'umya waman k<u>aa</u>na fii <u>dh</u>al<u>aa</u>lin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (4366, '43:41', 43, 41, 'fa-imm<u>aa</u> na<u>dz</u>habanna bika fa-inn<u>aa</u> minhum muntaqimuun\r\r\n');
INSERT INTO quranLatin VALUES (4367, '43:42', 43, 42, 'aw nuriyannakalla<u>dz</u>ii wa\'adn<u>aa</u>hum fa-inn<u>aa</u> \'alayhim muqtadiruun\r\r\n');
INSERT INTO quranLatin VALUES (4368, '43:43', 43, 43, 'fastamsik billa<u>dz</u>ii uu<u>h</u>iya ilayka innaka \'al<u>aa</u> <u>sh</u>ir<u>aath</u>in mustaqiim\r\r\n');
INSERT INTO quranLatin VALUES (4369, '43:44', 43, 44, 'wa-innahu la<u>dz</u>ikrun laka waliqawmika wasawfa tus-aluun\r\r\n');
INSERT INTO quranLatin VALUES (4370, '43:45', 43, 45, 'was-al man arsaln<u>aa</u> min qablika min rusulin<u>aa</u> aja\'aln<u>aa</u> min duuni rra<u>h</u>m<u>aa</u>ni <u>aa</u>lihatan yu\'baduun\r\r\n');
INSERT INTO quranLatin VALUES (4371, '43:46', 43, 46, 'walaqad arsaln<u>aa</u> muus<u>aa</u> bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> il<u>aa</u> fir\'awna wamala-ihi faq<u>aa</u>la innii rasuulu rabbi l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (4372, '43:47', 43, 47, 'falamm<u>aa</u> j<u>aa</u>-ahum bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> i<u>dzaa</u> hum minh<u>aa</u> ya<u>dh</u>akuun\r\r\n');
INSERT INTO quranLatin VALUES (4373, '43:48', 43, 48, 'wam<u>aa</u> nuriihim min <u>aa</u>yatin ill<u>aa</u> hiya akbaru min ukhtih<u>aa</u> wa-akha<u>dz</u>n<u>aa</u>hum bil\'a<u>dzaa</u>bi la\'allahum yarji\'uun\r\r\n');
INSERT INTO quranLatin VALUES (4374, '43:49', 43, 49, 'waq<u>aa</u>luu y<u>aa</u> ayyuh<u>aa</u> s<u>aah</u>iru ud\'u lan<u>aa</u> rabbaka bim<u>aa</u> \'ahida \'indaka innan<u>aa</u> lamuhtaduun\r\r\n');
INSERT INTO quranLatin VALUES (4375, '43:50', 43, 50, 'falamm<u>aa</u> kasyafn<u>aa</u> \'anhumu l\'a<u>dzaa</u>ba i<u>dzaa</u> hum yankutsuun\r\r\n');
INSERT INTO quranLatin VALUES (4376, '43:51', 43, 51, 'wan<u>aa</u>d<u>aa</u> fir\'awnu fii qawmihi q<u>aa</u>la y<u>aa</u> qawmi alaysa lii mulku mi<u>sh</u>ra wah<u>aadz</u>ihi al-anh<u>aa</u>ru tajrii min ta<u>h</u>tii afal<u>aa</u> tub<u>sh</u>iruun\r\r\n');
INSERT INTO quranLatin VALUES (4377, '43:52', 43, 52, 'am an<u>aa</u> khayrun min h<u>aadzaa</u>lla<u>dz</u>ii huwa mahiinun wal<u>aa</u> yak<u>aa</u>du yubiin\r\r\n');
INSERT INTO quranLatin VALUES (4378, '43:53', 43, 53, 'falawl<u>aa</u> ulqiya \'alayhi aswiratun min <u>dz</u>ahabin aw j<u>aa</u>-a ma\'ahu lmal<u>aa</u>-ikatu muqtariniin\r\r\n');
INSERT INTO quranLatin VALUES (4379, '43:54', 43, 54, 'fastakhaffa qawmahu fa-a<u>th</u><u>aa</u>\'uuhu innahum k<u>aa</u>nuu qawman f<u>aa</u>siqiin\r\r\n');
INSERT INTO quranLatin VALUES (4380, '43:55', 43, 55, 'falamm<u>aa</u> <u>aa</u>safuun<u>aa</u> intaqamn<u>aa</u> minhum fa-aghraqn<u>aa</u>hum ajma\'iin\r\r\n');
INSERT INTO quranLatin VALUES (4381, '43:56', 43, 56, 'faja\'aln<u>aa</u>hum salafan wamatsalan lil-<u>aa</u>khiriin\r\r\n');
INSERT INTO quranLatin VALUES (4382, '43:57', 43, 57, 'walamm<u>aa</u> <u>dh</u>uriba ibnu maryama matsalan i<u>dzaa</u> qawmuka minhu ya<u>sh</u>idduun\r\r\n');
INSERT INTO quranLatin VALUES (4383, '43:58', 43, 58, 'waq<u>aa</u>luu a<u>aa</u>lihatun<u>aa</u> khayrun am huwa m<u>aa</u> <u>dh</u>arabuuhu laka ill<u>aa</u> jadalan bal hum qawmun kha<u>sh</u>imuun\r\r\n');
INSERT INTO quranLatin VALUES (4384, '43:59', 43, 59, 'in huwa ill<u>aa</u> \'abdun an\'amn<u>aa</u> \'alayhi waja\'aln<u>aa</u>hu matsalan libanii isr<u>aa</u>-iil\r\r\n');
INSERT INTO quranLatin VALUES (4385, '43:60', 43, 60, 'walaw nasy<u>aa</u>u laja\'aln<u>aa</u> minkum mal<u>aa</u>-ikatan fii l-ar<u>dh</u>i yakhlufuun\r\r\n');
INSERT INTO quranLatin VALUES (4386, '43:61', 43, 61, 'wa-innahu la\'ilmun liss<u>aa</u>\'ati fal<u>aa</u> tamtarunna bih<u>aa</u> wattabi\'uuni h<u>aadzaa</u> <u>sh</u>ir<u>aath</u>un mustaqiim\r\r\n');
INSERT INTO quranLatin VALUES (4387, '43:62', 43, 62, 'wal<u>aa</u> ya<u>sh</u>uddannakumu sysyay<u>th</u><u>aa</u>nu innahu lakum \'aduwwun mubiin\r\r\n');
INSERT INTO quranLatin VALUES (4388, '43:63', 43, 63, 'walamm<u>aa</u> j<u>aa</u>-a \'iis<u>aa</u> bilbayyin<u>aa</u>ti q<u>aa</u>la qad ji/tukum bil<u>h</u>ikmati wali-ubayyina lakum ba\'<u>dh</u>alla<u>dz</u>ii takhtalifuuna fiihi fattaquull<u>aa</u>ha wa-a<u>th</u>ii\'uun\r\r\n');
INSERT INTO quranLatin VALUES (4389, '43:64', 43, 64, 'innall<u>aa</u>ha huwa rabbii warabbukum fa\'buduuhu h<u>aadzaa</u> <u>sh</u>ir<u>aath</u>un mustaqiim\r\r\n');
INSERT INTO quranLatin VALUES (4390, '43:65', 43, 65, 'fakhtalafa l-a<u>h</u>z<u>aa</u>bu min baynihim fawaylun lilla<u>dz</u>iina <u>zh</u>alamuu min \'a<u>dzaa</u>bi yawmin liim\r\r\n');
INSERT INTO quranLatin VALUES (4391, '43:66', 43, 66, 'hal yan<u>zh</u>uruuna ill<u>aa</u> ss<u>aa</u>\'ata an ta/tiyahum baghtatan wahum l<u>aa</u> yasy\'uruun\r\r\n');
INSERT INTO quranLatin VALUES (4392, '43:67', 43, 67, 'al-akhill<u>aa</u>u yawma-i<u>dz</u>in ba\'<u>dh</u>uhum liba\'<u>dh</u>in \'aduwwun ill<u>aa</u> lmuttaqiin\r\r\n');
INSERT INTO quranLatin VALUES (4393, '43:68', 43, 68, 'y<u>aa</u> \'ib<u>aa</u>di l<u>aa</u> khawfun \'alaykumu lyawma wal<u>aa</u> antum ta<u>h</u>zanuun\r\r\n');
INSERT INTO quranLatin VALUES (4394, '43:69', 43, 69, '<B>al</b>la<u>dz</u>iina <u>aa</u>manuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> wak<u>aa</u>nuu muslimiin\r\r\n');
INSERT INTO quranLatin VALUES (4395, '43:70', 43, 70, 'udkhuluu ljannata antum wa-azw<u>aa</u>jukum tu<u>h</u>baruun\r\r\n');
INSERT INTO quranLatin VALUES (4396, '43:71', 43, 71, 'yu<u>th</u><u>aa</u>fu \'alayhim bi<u>sh</u>i<u>haa</u>fin min <u>dz</u>ahabin wa-akw<u>aa</u>bin wafiih<u>aa</u> m<u>aa</u> tasytahiihi l-anfusu watala<u>dzdz</u>u l-a\'yunu wa-antum fiih<u>aa</u> kh<u>aa</u>liduun\r\r\n');
INSERT INTO quranLatin VALUES (4397, '43:72', 43, 72, 'watilka ljannatullatii uuritstumuuh<u>aa</u> bim<u>aa</u> kuntum ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (4398, '43:73', 43, 73, 'lakum fiih<u>aa</u> f<u>aa</u>kihatun katsiiratun minh<u>aa</u> ta/kuluun\r\r\n');
INSERT INTO quranLatin VALUES (4399, '43:74', 43, 74, 'inna lmujrimiina fii \'a<u>dzaa</u>bi jahannama kh<u>aa</u>liduun\r\r\n');
INSERT INTO quranLatin VALUES (4400, '43:75', 43, 75, 'l<u>aa</u> yufattaru \'anhum wahum fiihi mublisuun\r\r\n');
INSERT INTO quranLatin VALUES (4401, '43:76', 43, 76, 'wam<u>aa</u> <u>zh</u>alamn<u>aa</u>hum wal<u>aa</u>kin k<u>aa</u>nuu humu <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (4402, '43:77', 43, 77, 'wan<u>aa</u>daw y<u>aa</u> m<u>aa</u>liku liyaq<u>dh</u>i \'alayn<u>aa</u> rabbuka q<u>aa</u>la innakum m<u>aa</u>kitsuun\r\r\n');
INSERT INTO quranLatin VALUES (4403, '43:78', 43, 78, 'laqad ji/n<u>aa</u>kum bil<u>h</u>aqqi wal<u>aa</u>kinna aktsarakum lil<u>h</u>aqqi k<u>aa</u>rihuun\r\r\n');
INSERT INTO quranLatin VALUES (4404, '43:79', 43, 79, 'am abramuu amran fa-inn<u>aa</u> mubrimuun\r\r\n');
INSERT INTO quranLatin VALUES (4405, '43:80', 43, 80, 'am ya<u>h</u>sabuuna ann<u>aa</u> l<u>aa</u> nasma\'u sirrahum wanajw<u>aa</u>hum bal<u>aa</u> warusulun<u>aa</u> ladayhim yaktubuun\r\r\n');
INSERT INTO quranLatin VALUES (4406, '43:81', 43, 81, 'qul in k<u>aa</u>na lirra<u>h</u>m<u>aa</u>ni waladun fa-an<u>aa</u> awwalu l\'<u>aa</u>bidiin\r\r\n');
INSERT INTO quranLatin VALUES (4407, '43:82', 43, 82, 'sub<u>haa</u>na rabbi ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i rabbi l\'arsyi \'amm<u>aa</u> ya<u>sh</u>ifuun\r\r\n');
INSERT INTO quranLatin VALUES (4408, '43:83', 43, 83, 'fa<u>dz</u>arhum yakhuu<u>dh</u>uu wayal\'abuu <u>h</u>att<u>aa</u> yul<u>aa</u>quu yawmahumulla<u>dz</u>ii yuu\'aduun\r\r\n');
INSERT INTO quranLatin VALUES (4409, '43:84', 43, 84, 'wahuwalla<u>dz</u>ii fii ssam<u>aa</u>-i il<u>aa</u>hun wafii l-ar<u>dh</u>i il<u>aa</u>hun wahuwa l<u>h</u>akiimu l\'aliim\r\r\n');
INSERT INTO quranLatin VALUES (4410, '43:85', 43, 85, 'watab<u>aa</u>rakalla<u>dz</u>ii lahu mulku ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wam<u>aa</u> baynahum<u>aa</u> wa\'indahu \'ilmu ss<u>aa</u>\'ati wa-ilayhi turja\'uun\r\r\n');
INSERT INTO quranLatin VALUES (4411, '43:86', 43, 86, 'wal<u>aa</u> yamlikulla<u>dz</u>iina yad\'uuna min duunihi sysyaf<u>aa</u>\'ata ill<u>aa</u> man syahida bil<u>h</u>aqqi wahum ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (4412, '43:87', 43, 87, 'wala-in sa-altahum man khalaqahum layaquulunnall<u>aa</u>hu fa-ann<u>aa</u> yu/fakuun\r\r\n');
INSERT INTO quranLatin VALUES (4413, '43:88', 43, 88, 'waqiilihi y<u>aa</u> rabbi inna h<u>aa</u>ul<u>aa</u>-i qawmun l<u>aa</u> yu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (4414, '43:89', 43, 89, 'fa<u>sh</u>fa<u>h</u> \'anhum waqul sal<u>aa</u>mun fasawfa ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (4415, '44:1', 44, 1, '<u>haa</u>-miim</u>\r\r\n');
INSERT INTO quranLatin VALUES (4416, '44:2', 44, 2, 'walkit<u>aa</u>bi lmubiin\r\r\n');
INSERT INTO quranLatin VALUES (4417, '44:3', 44, 3, 'inn<u>aa</u> anzaln<u>aa</u>hu fii laylatin mub<u>aa</u>rakatin inn<u>aa</u> kunn<u>aa</u> mun<u>dz</u>iriin\r\r\n');
INSERT INTO quranLatin VALUES (4418, '44:4', 44, 4, 'fiih<u>aa</u> yufraqu kullu amrin <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (4419, '44:5', 44, 5, 'amran min \'indin<u>aa</u> inn<u>aa</u> kunn<u>aa</u> mursiliin\r\r\n');
INSERT INTO quranLatin VALUES (4420, '44:6', 44, 6, 'ra<u>h</u>matan min rabbika innahu huwa ssamii\'u l\'aliim\r\r\n');
INSERT INTO quranLatin VALUES (4421, '44:7', 44, 7, 'rabbi ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wam<u>aa</u> baynahum<u>aa</u> in kuntum muuqiniin\r\r\n');
INSERT INTO quranLatin VALUES (4422, '44:8', 44, 8, 'l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa yu<u>h</u>yii wayumiitu rabbukum warabbu <u>aa</u>b<u>aa</u>-ikumu l-awwaliin\r\r\n');
INSERT INTO quranLatin VALUES (4423, '44:9', 44, 9, 'bal hum fii syakkin yal\'abuun\r\r\n');
INSERT INTO quranLatin VALUES (4424, '44:10', 44, 10, 'fartaqib yawma ta/tii ssam<u>aa</u>u bidukh<u>aa</u>nin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (4425, '44:11', 44, 11, 'yaghsy<u>aa</u> nn<u>aa</u>sa h<u>aadzaa</u> \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (4426, '44:12', 44, 12, 'rabban<u>aa</u> iksyif \'ann<u>aa</u> l\'a<u>dzaa</u>ba inn<u>aa</u> mu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (4427, '44:13', 44, 13, 'ann<u>aa</u> lahumu <u>dzdz</u>ikr<u>aa</u> waqad j<u>aa</u>-ahum rasuulun mubiin\r\r\n');
INSERT INTO quranLatin VALUES (4428, '44:14', 44, 14, 'tsumma tawallaw \'anhu waq<u>aa</u>luu mu\'allamun majnuun\r\r\n');
INSERT INTO quranLatin VALUES (4429, '44:15', 44, 15, 'inn<u>aa</u> k<u>aa</u>syifuu l\'a<u>dzaa</u>bi qaliilan innakum \'<u>aa</u>-iduun\r\r\n');
INSERT INTO quranLatin VALUES (4430, '44:16', 44, 16, 'yawma nab<u>th</u>isyu lba<u>th</u>syata lkubr<u>aa</u> inn<u>aa</u> muntaqimuun\r\r\n');
INSERT INTO quranLatin VALUES (4431, '44:17', 44, 17, 'walaqad fatann<u>aa</u> qablahum qawma fir\'awna waj<u>aa</u>-ahum rasuulun kariim\r\r\n');
INSERT INTO quranLatin VALUES (4432, '44:18', 44, 18, 'an adduu ilayya \'ib<u>aa</u>dall<u>aa</u>hi innii lakum rasuulun amiin\r\r\n');
INSERT INTO quranLatin VALUES (4433, '44:19', 44, 19, 'wa-an l<u>aa</u> ta\'luu \'al<u>aa</u>ll<u>aa</u>hi innii <u>aa</u>tiikum bisul<u>th</u><u>aa</u>nin mubiin\r\r\n');
