INSERT INTO quranLatin VALUES (4515, '46:5', 46, 5, 'waman a<u>dh</u>allu mimman yad\'uu min duunill<u>aa</u>hi man l<u>aa</u> yastajiibu lahu il<u>aa</u> yawmi lqiy<u>aa</u>mati wahum \'an du\'<u>aa</u>-ihim gh<u>aa</u>filuun\r\r\n');
INSERT INTO quranLatin VALUES (4516, '46:6', 46, 6, 'wa-i<u>dzaa</u> <u>h</u>usyira nn<u>aa</u>su k<u>aa</u>nuu lahum a\'d<u>aa</u>-an wak<u>aa</u>nuu bi\'ib<u>aa</u>datihim k<u>aa</u>firiin\r\r\n');
INSERT INTO quranLatin VALUES (4517, '46:7', 46, 7, 'wa-i<u>dzaa</u> tutl<u>aa</u> \'alayhim <u>aa</u>y<u>aa</u>tun<u>aa</u> bayyin<u>aa</u>tin q<u>aa</u>lalla<u>dz</u>iina kafaruu lil<u>h</u>aqqi lamm<u>aa</u> j<u>aa</u>-ahum h<u>aadzaa</u> si<u>h</u>run mubiin\r\r\n');
INSERT INTO quranLatin VALUES (4518, '46:8', 46, 8, 'am yaquuluuna iftar<u>aa</u>hu qul ini iftaraytuhu fal<u>aa</u> tamlikuuna lii minall<u>aa</u>hi syay-an huwa a\'lamu bim<u>aa</u> tufii<u>dh</u>uuna fiihi kaf<u>aa</u> bihi syahiidan baynii wabaynakum wahuwa lghafuuru rra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (4519, '46:9', 46, 9, 'qul m<u>aa</u> kuntu bid\'an mina rrusuli wam<u>aa</u> adrii m<u>aa</u> yuf\'alu bii wal<u>aa</u> bikum in attabi\'u ill<u>aa</u> m<u>aa</u> yuu<u>haa</u> ilayya wam<u>aa</u> an<u>aa</u> ill<u>aa</u> na<u>dz</u>iirun mubiin\r\r\n');
INSERT INTO quranLatin VALUES (4520, '46:10', 46, 10, 'qul ara-aytum in k<u>aa</u>na min \'indill<u>aa</u>hi wakafartum bihi wasyahida sy<u>aa</u>hidun min banii isr<u>aa</u>-iila \'al<u>aa</u> mitslihi fa<u>aa</u>mana wastakbartum innall<u>aa</u>ha l<u>aa</u> yahdii lqawma <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (4521, '46:11', 46, 11, 'waq<u>aa</u>lalla<u>dz</u>iina kafaruu lilla<u>dz</u>iina <u>aa</u>manuu law k<u>aa</u>na khayran m<u>aa</u> sabaquun<u>aa</u> ilayhi wa-i<u>dz</u> lam yahtaduu bihi fasayaquuluuna h<u>aadzaa</u> ifkun qadiim\r\r\n');
INSERT INTO quranLatin VALUES (4522, '46:12', 46, 12, 'wamin qablihi kit<u>aa</u>bu muus<u>aa</u> im<u>aa</u>man wara<u>h</u>matan wah<u>aadzaa</u> kit<u>aa</u>bun mu<u>sh</u>addiqun lis<u>aa</u>nan \'arabiyyan liyun<u>dz</u>iralla<u>dz</u>iina <u>zh</u>alamuu wabusyr<u>aa</u> lilmu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (4523, '46:13', 46, 13, 'innalla<u>dz</u>iina q<u>aa</u>luu rabbun<u>aa</u>ll<u>aa</u>hu tsumma istaq<u>aa</u>muu fal<u>aa</u> khawfun \'alayhim wal<u>aa</u> hum ya<u>h</u>zanuun\r\r\n');
INSERT INTO quranLatin VALUES (4524, '46:14', 46, 14, 'ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu ljannati kh<u>aa</u>lidiina fiih<u>aa</u> jaz<u>aa</u>-an bim<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (4525, '46:15', 46, 15, 'wawa<u>shsh</u>ayn<u>aa</u> l-ins<u>aa</u>na biw<u>aa</u>lidayhi i<u>h</u>s<u>aa</u>nan <u>h</u>amalat-hu ummuhu kurhan wawa<u>dh</u>a\'at-hu kurhan wa<u>h</u>amluhu wafi<u>shaa</u>luhu tsal<u>aa</u>tsuuna syahran <u>h</u>att<u>aa</u> i<u>dzaa</u> balagha asyuddahu wabalagha arba\'iina sanatan q<u>aa</u>la rabbi awzi\'nii an asykura ni\'matakallatii an\'amta \'alayya wa\'al<u>aa</u> w<u>aa</u>lidayya wa-an a\'mala <u>shaa</u>li<u>h</u>an tar<u>daa</u>hu wa-a<u>sh</u>li<u>h</u> lii fii <u>dz</u>urriyyatii innii tubtu ilayka wa-innii mina lmuslimiin\r\r\n');
INSERT INTO quranLatin VALUES (4526, '46:16', 46, 16, 'ul<u>aa</u>-ikalla<u>dz</u>iina nataqabbalu \'anhum a<u>h</u>sana m<u>aa</u> \'amiluu wanataj<u>aa</u>wazu \'an sayyi-<u>aa</u>tihim fii a<u>sh</u>-<u>haa</u>bi ljannati wa\'da <u>shsh</u>idqilla<u>dz</u>ii k<u>aa</u>nuu yuu\'aduun\r\r\n');
INSERT INTO quranLatin VALUES (4527, '46:17', 46, 17, 'walla<u>dz</u>ii q<u>aa</u>la liw<u>aa</u>lidayhi uffin lakum<u>aa</u> ata\'id<u>aa</u>ninii an ukhraja waqad khalati lquruunu min qablii wahum<u>aa</u> yastaghiits<u>aa</u>nill<u>aa</u>ha waylaka <u>aa</u>min inna wa\'dall<u>aa</u>hi <u>h</u>aqqun fayaquulu m<u>aa</u> h<u>aadzaa</u> ill<u>aa</u> as<u>aath</u>iiru l-awwaliin\r\r\n');
INSERT INTO quranLatin VALUES (4528, '46:18', 46, 18, 'ul<u>aa</u>-ikalla<u>dz</u>iina <u>h</u>aqqa \'alayhimu lqawlu fii umamin qad khalat min qablihim mina ljinni wal-insi innahum k<u>aa</u>nuu kh<u>aa</u>siriin\r\r\n');
INSERT INTO quranLatin VALUES (4529, '46:19', 46, 19, 'walikullin daraj<u>aa</u>tun mimm<u>aa</u> \'amiluu waliyuwaffiyahum a\'m<u>aa</u>lahum wahum l<u>aa</u> yu<u>zh</u>lamuun\r\r\n');
INSERT INTO quranLatin VALUES (4530, '46:20', 46, 20, 'wayawma yu\'ra<u>dh</u>ulla<u>dz</u>iina kafaruu \'al<u>aa</u> nn<u>aa</u>ri a<u>dz</u>habtum <u>th</u>ayyib<u>aa</u>tikum fii <u>h</u>ay<u>aa</u>tikumu dduny<u>aa</u> wastamta\'tum bih<u>aa</u> falyawma tujzawna \'a<u>dzaa</u>ba lhuuni bim<u>aa</u> kuntum tastakbiruuna fii l-ar<u>dh</u>i bighayri l<u>h</u>aqqi wabim<u>aa</u> kuntum tafsuquun\r\r\n');
INSERT INTO quranLatin VALUES (4531, '46:21', 46, 21, 'wa<u>dz</u>kur akh<u>aa</u> \'<u>aa</u>din i<u>dz</u> an<u>dz</u>ara qawmahu bil-a<u>h</u>q<u>aa</u>fi waqad khalati nnu<u>dz</u>uru min bayni yadayhi wamin khalfihill<u>aa</u> ta\'buduu ill<u>aa</u>ll<u>aa</u>ha innii akh<u>aa</u>fu \'alaykum \'a<u>dzaa</u>ba yawmin \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (4532, '46:22', 46, 22, 'q<u>aa</u>luu aji/tan<u>aa</u> lita/fikan<u>aa</u> \'an <u>aa</u>lihatin<u>aa</u> fa/tin<u>aa</u> bim<u>aa</u> ta\'idun<u>aa</u> in kunta mina <u>shshaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (4533, '46:23', 46, 23, 'q<u>aa</u>la innam<u>aa</u> l\'ilmu \'indall<u>aa</u>hi wauballighukum m<u>aa</u> ursiltu bihi wal<u>aa</u>kinnii ar<u>aa</u>kum qawman tajhaluun\r\r\n');
INSERT INTO quranLatin VALUES (4534, '46:24', 46, 24, 'falamm<u>aa</u> ra-awhu \'<u>aa</u>ri<u>dh</u>an mustaqbila awdiyatihim q<u>aa</u>luu h<u>aadzaa</u> \'<u>aa</u>ri<u>dh</u>un mum<u>th</u>irun<u>aa</u> bal huwa m<u>aa</u> ista\'jaltum bihi rii<u>h</u>un fiih<u>aa</u> \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (4535, '46:25', 46, 25, 'tudammiru kulla syay-in bi-amri rabbih<u>aa</u> fa-a<u>sh</u>ba<u>h</u>uu l<u>aa</u> yur<u>aa</u> ill<u>aa</u> mas<u>aa</u>kinuhum ka<u>dzaa</u>lika najzii lqawma lmujrimiin\r\r\n');
INSERT INTO quranLatin VALUES (4536, '46:26', 46, 26, 'walaqad makkann<u>aa</u>hum fiim<u>aa</u> in makkann<u>aa</u>kum fiihi waja\'aln<u>aa</u> lahum sam\'an wa-ab<u>shaa</u>ran wa-af-idatan fam<u>aa</u> aghn<u>aa</u> \'anhum sam\'uhum wal<u>aa</u> ab<u>shaa</u>ruhum wal<u>aa</u> af-idatuhum min syay-in i<u>dz</u> k<u>aa</u>nuu yaj<u>h</u>aduuna bi-<u>aa</u>y<u>aa</u>till<u>aa</u>hi wa<u>haa</u>qa bihim m<u>aa</u> k<u>aa</u>nuu bihi yastahzi-uun\r\r\n');
INSERT INTO quranLatin VALUES (4537, '46:27', 46, 27, 'walaqad ahlakn<u>aa</u> m<u>aa</u> <u>h</u>awlakum mina lqur<u>aa</u> wa<u>sh</u>arrafn<u>aa</u> l-<u>aa</u>y<u>aa</u>ti la\'allahum yarji\'uun\r\r\n');
INSERT INTO quranLatin VALUES (4538, '46:28', 46, 28, 'falawl<u>aa</u> na<u>sh</u>arahumulla<u>dz</u>iina ittakha<u>dz</u>uu min duunill<u>aa</u>hi qurb<u>aa</u>nan <u>aa</u>lihatan bal <u>dh</u>alluu \'anhum wa<u>dzaa</u>lika ifkuhum wam<u>aa</u> k<u>aa</u>nuu yaftaruun\r\r\n');
INSERT INTO quranLatin VALUES (4539, '46:29', 46, 29, 'wa-i<u>dz</u> <u>sh</u>arafn<u>aa</u> ilayka nafaran mina ljinni yastami\'uuna lqur-<u>aa</u>na falamm<u>aa</u> <u>h</u>a<u>dh</u>aruuhu q<u>aa</u>luu an<u>sh</u>ituu falamm<u>aa</u> qu<u>dh</u>iya wallaw il<u>aa</u> qawmihim mun<u>dz</u>iriin\r\r\n');
INSERT INTO quranLatin VALUES (4540, '46:30', 46, 30, 'q<u>aa</u>luu y<u>aa</u> qawman<u>aa</u> inn<u>aa</u> sami\'n<u>aa</u> kit<u>aa</u>ban unzila min ba\'di muus<u>aa</u> mu<u>sh</u>addiqan lim<u>aa</u> bayna yadayhi yahdii il<u>aa</u> l<u>h</u>aqqi wa-il<u>aa</u> <u>th</u>ariiqin mustaqiim\r\r\n');
INSERT INTO quranLatin VALUES (4541, '46:31', 46, 31, 'y<u>aa</u> qawman<u>aa</u> ajiibuu d<u>aa</u>\'iyall<u>aa</u>hi wa<u>aa</u>minuu bihi yaghfir lakum min <u>dz</u>unuubikum wayujirkum min \'a<u>dzaa</u>bin liim\r\r\n');
INSERT INTO quranLatin VALUES (4542, '46:32', 46, 32, 'waman l<u>aa</u> yujib d<u>aa</u>\'iyall<u>aa</u>hi falaysa bimu\'jizin fii l-ar<u>dh</u>i walaysa lahu min duunihi awliy<u>aa</u>-a ul<u>aa</u>-ika fii <u>dh</u>al<u>aa</u>lin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (4543, '46:33', 46, 33, 'awa lam yaraw annall<u>aa</u>halla<u>dz</u>ii khalaqa ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a walam ya\'ya bikhalqihinna biq<u>aa</u>dirin \'al<u>aa</u> an yu<u>h</u>yiya lmawt<u>aa</u> bal<u>aa</u> innahu \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (4544, '46:34', 46, 34, 'wayawma yu\'ra<u>dh</u>ulla<u>dz</u>iina kafaruu \'al<u>aa</u> nn<u>aa</u>ri alaysa h<u>aadzaa</u> bil<u>h</u>aqqi q<u>aa</u>luu bal<u>aa</u> warabbin<u>aa</u> q<u>aa</u>la fa<u>dz</u>uuquu al\'a<u>dzaa</u>ba bim<u>aa</u> kuntum takfuruun\r\r\n');
INSERT INTO quranLatin VALUES (4545, '46:35', 46, 35, 'fa<u>sh</u>bir kam<u>aa</u> <u>sh</u>abara uluu l\'azmi mina rrusuli wal<u>aa</u> tasta\'jil lahum ka-annahum yawma yarawna m<u>aa</u> yuu\'aduuna lam yalbatsuu ill<u>aa</u> s<u>aa</u>\'atan min nah<u>aa</u>rin bal<u>aa</u>ghun fahal yuhlaku ill<u>aa</u> lqawmu lf<u>aa</u>siquun\r\r\n');
INSERT INTO quranLatin VALUES (4546, '47:1', 47, 1, '<B>al</b>la<u>dz</u>iina kafaruu wa<u>sh</u>adduu \'an sabiilill<u>aa</u>hi a<u>dh</u>alla a\'m<u>aa</u>lahum </u>\r\r\n');
INSERT INTO quranLatin VALUES (4547, '47:2', 47, 2, 'walla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti wa<u>aa</u>manuu bim<u>aa</u> nuzzila \'al<u>aa</u> mu<u>h</u>ammadin wahuwa l<u>h</u>aqqu min rabbihim kaffara \'anhum sayyi-<u>aa</u>tihim wa-a<u>sh</u>la<u>h</u>a b<u>aa</u>lahum </u>\r\r\n');
INSERT INTO quranLatin VALUES (4548, '47:3', 47, 3, '<u>dzaa</u>lika bi-annalla<u>dz</u>iina kafaruu ittaba\'uu lb<u>aath</u>ila wa-annalla<u>dz</u>iina <u>aa</u>manuu ittaba\'uu l<u>h</u>aqqa min rabbihim ka<u>dzaa</u>lika ya<u>dh</u>ribull<u>aa</u>hu linn<u>aa</u>si amts<u>aa</u>lahum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4549, '47:4', 47, 4, 'fa-i<u>dzaa</u> laqiitumulla<u>dz</u>iina kafaruu fa<u>dh</u>arba rriq<u>aa</u>bi <u>h</u>att<u>aa</u> i<u>dzaa</u> atskhantumuuhum fasyudduu lwats<u>aa</u>qa fa-imm<u>aa</u> mannan ba\'du wa-imm<u>aa</u> fid<u>aa</u>-an <u>h</u>att<u>aa</u> ta<u>dh</u>a\'a l<u>h</u>arbu awz<u>aa</u>rah<u>aa</u> <u>dzaa</u>lika walaw yasy<u>aa</u>ull<u>aa</u>hu lanta<u>sh</u>ara minhum wal<u>aa</u>kin liyabluwa ba\'<u>dh</u>akum biba\'<u>dh</u>in walla<u>dz</u>iina qutiluu fii sabiilill<u>aa</u>hi falan yu<u>dh</u>illa a\'m<u>aa</u>lahum sayahdiihim wayu<u>sh</u>li<u>h</u>u b<u>aa</u>lahum </u>\r\r\n');
INSERT INTO quranLatin VALUES (4550, '47:5', 47, 5, 'sayahd<u>i</u>him wa yu<u>sh</u>li<u>h</u>u b<u>aa</u> lahum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4551, '47:6', 47, 6, 'wayudkhiluhumu ljannata \'arrafah<u>aa</u> lahum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4552, '47:7', 47, 7, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu in tan<u>sh</u>uruull<u>aa</u>ha yan<u>sh</u>urkum wayutsabbit aqd<u>aa</u>makum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4553, '47:8', 47, 8, 'walla<u>dz</u>iina kafaruu fata\'san lahum wa-a<u>dh</u>alla a\'m<u>aa</u>lahum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4554, '47:9', 47, 9, '<u>dzaa</u>lika bi-annahum karihuu m<u>aa</u> anzalall<u>aa</u>hu fa-a<u>h</u>ba<u>th</u>a a\'m<u>aa</u>lahum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4555, '47:10', 47, 10, 'afalam yasiiruu fii l-ar<u>dh</u>i fayan<u>zh</u>uruu kayfa k<u>aa</u>na \'<u>aa</u>qibatulla<u>dz</u>iina min qablihim dammarall<u>aa</u>hu \'alayhim walilk<u>aa</u>firiina amts<u>aa</u>luh<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4556, '47:11', 47, 11, '<u>dzaa</u>lika bi-annall<u>aa</u>ha mawl<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu wa-anna lk<u>aa</u>firiina l<u>aa</u> mawl<u>aa</u> lahum </u>\r\r\n');
INSERT INTO quranLatin VALUES (4557, '47:12', 47, 12, 'innall<u>aa</u>ha yudkhilulla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru walla<u>dz</u>iina kafaruu yatamatta\'uuna waya/kuluuna kam<u>aa</u> ta/kulu l-an\'<u>aa</u>mu wann<u>aa</u>ru matswan lahum </u>\r\r\n');
INSERT INTO quranLatin VALUES (4558, '47:13', 47, 13, 'waka-ayyin min qaryatin hiya asyaddu quwwatan min qaryatikallatii akhrajatka ahlakn<u>aa</u>hum fal<u>aa</u> n<u>aas</u>ira lahum </u>\r\r\n');
INSERT INTO quranLatin VALUES (4559, '47:14', 47, 14, 'afaman k<u>aa</u>na \'al<u>aa</u> bayyinatin min rabbihi kaman zuyyina lahu suu-u \'amalihi wattaba\'uu ahw<u>aa</u>-ahum </u>\r\r\n');
INSERT INTO quranLatin VALUES (4560, '47:15', 47, 15, 'matsalu ljannatillatii wu\'ida lmuttaquuna fiih<u>aa</u> anh<u>aa</u>run min m<u>aa</u>-in ghayri <u>aa</u>sinin wa-anh<u>aa</u>run min labanin lam yataghayyar <u>th</u>a\'muhu wa-anh<u>aa</u>run min khamrin la<u>dzdz</u>atin lisysy<u>aa</u>ribiina wa-anh<u>aa</u>run min \'asalin mu<u>sh</u>affan walahum fiih<u>aa</u> min kulli tstsamar<u>aa</u>ti wamaghfiratun min rabbihim kaman huwa kh<u>aa</u>lidun fii nn<u>aa</u>ri wasuquu m<u>aa</u>-an <u>h</u>amiiman faqa<u>ththh</u>a\'a am\'<u>aa</u>-ahum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4561, '47:16', 47, 16, 'waminhum man yastami\'u ilayka <u>h</u>att<u>aa</u> i<u>dzaa</u> kharajuu min \'indika q<u>aa</u>luu lilla<u>dz</u>iina uutuu l\'ilma m<u>aatsaa</u> q<u>aa</u>la <u>aa</u>nifan ul<u>aa</u>-ikalla<u>dz</u>iina <u>th</u>aba\'all<u>aa</u>hu \'al<u>aa</u> quluubihim wattaba\'uu ahw<u>aa</u>-ahum </u>\r\r\n');
INSERT INTO quranLatin VALUES (4562, '47:17', 47, 17, 'walla<u>dz</u>iina ihtadaw z<u>aa</u>dahum hudan wa<u>aa</u>t<u>aa</u>hum taqw<u>aa</u>hum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4563, '47:18', 47, 18, 'fahal yan<u>zh</u>uruuna ill<u>aa</u> ss<u>aa</u>\'ata an ta/tiyahum baghtatan faqad j<u>aa</u>-a asyr<u>aath</u>uh<u>aa</u> fa-ann<u>aa</u> lahum i<u>dzaa</u> j<u>aa</u>-at-hum <u>dz</u>ikr<u>aa</u>hum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4564, '47:19', 47, 19, 'fa\'lam annahu l<u>aa</u> il<u>aa</u>ha ill<u>aa</u>ll<u>aa</u>hu wastaghfir li<u>dz</u>anbika walilmu/miniina walmu/min<u>aa</u>ti wal<u>aa</u>hu ya\'lamu mutaqallabakum wamatsw<u>aa</u>kum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4565, '47:20', 47, 20, 'wayaquululla<u>dz</u>iina <u>aa</u>manuu lawl<u>aa</u> nuzzilat suuratun fa-i<u>dzaa</u> unzilat suuratun mu<u>h</u>kamatun wa<u>dz</u>ukira fiih<u>aa</u> lqit<u>aa</u>lu ra-aytalla<u>dz</u>iina fii quluubihim mara<u>dh</u>un yan<u>zh</u>uruuna ilayka na<u>zh</u>ara lmaghsyiyyi \'alayhi mina lmawti fa-awl<u>aa</u> lahum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4566, '47:21', 47, 21, '<u>th</u><u>aa</u>\'atun waqawlun ma\'ruufun fa-i<u>dzaa</u> \'azama l-amru falaw <u>sh</u>adaquull<u>aa</u>ha lak<u>aa</u>na khayran lahum </u>\r\r\n');
INSERT INTO quranLatin VALUES (4567, '47:22', 47, 22, 'fahal \'asaytum in tawallaytum an tufsiduu fii l-ar<u>dh</u>i watuqa<u>ththh</u>i\'uu ar<u>haa</u>makum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4568, '47:23', 47, 23, 'ul<u>aa</u>-ikalla<u>dz</u>iina la\'anahumull<u>aa</u>hu fa-a<u>sh</u>ammahum wa-a\'m<u>aa</u> ab<u>shaa</u>rahum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4569, '47:24', 47, 24, 'afal<u>aa</u> yatadabbaruuna lqur-<u>aa</u>na am \'al<u>aa</u> quluubin aqf<u>aa</u>luh<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4570, '47:25', 47, 25, 'innalla<u>dz</u>iina irtadduu \'al<u>aa</u> adb<u>aa</u>rihim min ba\'di m<u>aa</u> tabayyana lahumu lhud<u>aa</u> sysyay<u>th</u><u>aa</u>nu sawwala lahum wa-aml<u>aa</u> lahum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4571, '47:26', 47, 26, '<u>dzaa</u>lika bi-annahum q<u>aa</u>luu lilla<u>dz</u>iina karihuu m<u>aa</u> nazzalall<u>aa</u>hu sanu<u>th</u>ii\'ukum fii ba\'<u>dh</u>i l-amri wal<u>aa</u>hu ya\'lamu isr<u>aa</u>rahum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4572, '47:27', 47, 27, 'fakayfa i<u>dzaa</u> tawaffat-humu lmal<u>aa</u>-ikatu ya<u>dh</u>ribuuna wujuuhahum wa-adb<u>aa</u>rahum </u>\r\r\n');
INSERT INTO quranLatin VALUES (4573, '47:28', 47, 28, '<u>dzaa</u>lika bi-annahumu ittaba\'uu m<u>aa</u> askha<u>th</u>all<u>aa</u>ha wakarihuu ri<u>dh</u>w<u>aa</u>nahu fa-a<u>h</u>ba<u>th</u>a a\'m<u>aa</u>lahum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4574, '47:29', 47, 29, 'am <u>h</u>asiballa<u>dz</u>iina fii quluubihim mara<u>dh</u>un an lan yukhrijall<u>aa</u>hu a<u>dh</u>gh<u>aa</u>nahum </u>\r\r\n');
INSERT INTO quranLatin VALUES (4575, '47:30', 47, 30, 'walaw nasy<u>aa</u>u la-arayn<u>aa</u>kahum fala\'araftahum bisiim<u>aa</u>hum walata\'rifannahum fii la<u>h</u>ni lqawli wal<u>aa</u>hu ya\'lamu a\'m<u>aa</u>lakum </u>\r\r\n');
INSERT INTO quranLatin VALUES (4576, '47:31', 47, 31, 'walanabluwannakum <u>h</u>att<u>aa</u> na\'lama lmuj<u>aa</u>hidiina minkum wa<u>shshaa</u>biriina wanabluwa akhb<u>aa</u>rakum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4577, '47:32', 47, 32, 'innalla<u>dz</u>iina kafaruu wa<u>sh</u>adduu \'an sabiilill<u>aa</u>hi wasy<u>aa</u>qquu rrasuula min ba\'di m<u>aa</u> tabayyana lahumu lhud<u>aa</u> lan ya<u>dh</u>uruull<u>aa</u>ha syay-an wasayu<u>h</u>bi<u>th</u>u a\'m<u>aa</u>lahum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4578, '47:33', 47, 33, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu a<u>th</u>ii\'uull<u>aa</u>ha wa-a<u>th</u>ii\'uu rrasuula wal<u>aa</u> tub<u>th</u>iluu a\'m<u>aa</u>lakum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4579, '47:34', 47, 34, 'innalla<u>dz</u>iina kafaruu wa<u>sh</u>adduu \'an sabiilill<u>aa</u>hi tsumma m<u>aa</u>tuu wahum kuff<u>aa</u>run falan yaghfirall<u>aa</u>hu lahum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4580, '47:35', 47, 35, 'fal<u>aa</u> tahinuu watad\'uu il<u>aa</u> ssalmi wa-antumu l-a\'lawna wal<u>aa</u>hu ma\'akum walan yatirakum a\'m<u>aa</u>lakum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4581, '47:36', 47, 36, 'innam<u>aa</u> l<u>h</u>ay<u>aa</u>tu dduny<u>aa</u> la\'ibun walahwun wa-in tu/minuu watattaquu yu/tikum ujuurakum wal<u>aa</u> yas-alkum amw<u>aa</u>lakum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4582, '47:37', 47, 37, 'in yas-alkumuuh<u>aa</u> fayu<u>h</u>fikum tabkhaluu wayukhrij a<u>dh</u>gh<u>aa</u>nakum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4583, '47:38', 47, 38, 'h<u>aa</u> antum h<u>aa</u>ul<u>aa</u>-i tud\'awna litunfiquu fii sabiilill<u>aa</u>hi faminkum man yabkhalu waman yabkhal fa-innam<u>aa</u> yabkhalu \'an nafsihi wal<u>aa</u>hu lghaniyyu wa-antumu lfuqar<u>aa</u>u wa-in tatawallaw yastabdil qawman ghayrakum tsumma l<u>aa</u> yakuunuu amts<u>aa</u>lakum</u>\r\r\n');
INSERT INTO quranLatin VALUES (4584, '48:1', 48, 1, 'inn<u>aa</u> fata<u>h</u>n<u>aa</u> laka fat<u>h</u>an mubiin<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4585, '48:2', 48, 2, 'liyaghfira lakall<u>aa</u>hu m<u>aa</u> taqaddama min <u>dz</u>anbika wam<u>aa</u> ta-akhkhara wayutimma ni\'matahu \'alayka wayahdiyaka <u>sh</u>ir<u>aath</u>an mustaqiim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4586, '48:3', 48, 3, 'wayan<u>sh</u>urakall<u>aa</u>hu na<u>sh</u>ran \'aziiz<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4587, '48:4', 48, 4, 'huwalla<u>dz</u>ii anzala ssakiinata fii quluubi lmu/miniina liyazd<u>aa</u>duu iim<u>aa</u>nan ma\'a iim<u>aa</u>nihim walill<u>aa</u>hi junuudu ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wak<u>aa</u>nall<u>aa</u>hu \'aliiman <u>h</u>akiim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4588, '48:5', 48, 5, 'liyudkhila lmu/miniina walmu/min<u>aa</u>ti jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> wayukaffira \'anhum sayyi-<u>aa</u>tihim wak<u>aa</u>na <u>dzaa</u>lika \'indall<u>aa</u>hi fawzan \'a<u>zh</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4589, '48:6', 48, 6, 'wayu\'a<u>dzdz</u>iba lmun<u>aa</u>fiqiina walmun<u>aa</u>fiq<u>aa</u>ti walmusyrikiina walmusyrik<u>aa</u>ti <u>zhzhaa</u>nniina bil<u>aa</u>hi <u>zh</u>anna ssaw-i \'alayhim d<u>aa</u>-iratu ssaw-i wagha<u>dh</u>iball<u>aa</u>hu \'alayhim wala\'anahum wa-a\'adda lahum jahannama was<u>aa</u>-at ma<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4590, '48:7', 48, 7, 'walill<u>aa</u>hi junuudu ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wak<u>aa</u>nall<u>aa</u>hu \'aziizan <u>h</u>akiim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4591, '48:8', 48, 8, 'inn<u>aa</u> arsaln<u>aa</u>ka sy<u>aa</u>hidan wamubasysyiran wana<u>dz</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4592, '48:9', 48, 9, 'litu/minuu bil<u>aa</u>hi warasuulihi watu\'azziruuhu watuwaqqiruuhu watusabbi<u>h</u>uuhu bukratan wa-a<u>sh</u>iil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4593, '48:10', 48, 10, 'innalla<u>dz</u>iina yub<u>aa</u>yi\'uunaka innam<u>aa</u> yub<u>aa</u>yi\'uunall<u>aa</u>ha yadull<u>aa</u>hi fawqa aydiihim faman nakatsa fa-innam<u>aa</u> yankutsu \'al<u>aa</u> nafsihi waman awf<u>aa</u> bim<u>aa</u> \'<u>aa</u>hada \'alayhull<u>aa</u>ha fasayu/tiihi ajran \'a<u>zh</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4594, '48:11', 48, 11, 'sayaquulu laka lmukhallafuuna mina l-a\'r<u>aa</u>bi syaghalatn<u>aa</u> amw<u>aa</u>lun<u>aa</u> wa-ahluun<u>aa</u> fastaghfir lan<u>aa</u> yaquuluuna bi-alsinatihim m<u>aa</u> laysa fii quluubihim qul faman yamliku lakum minall<u>aa</u>hi syay-an in ar<u>aa</u>da bikum <u>dh</u>arran aw ar<u>aa</u>da bikum naf\'an bal k<u>aa</u>nall<u>aa</u>hu bim<u>aa</u> ta\'maluuna khabiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4595, '48:12', 48, 12, 'bal <u>zh</u>anantum an lan yanqaliba rrasuulu walmu/minuuna il<u>aa</u> ahliihim abadan wazuyyina <u>dzaa</u>lika fii quluubikum wa<u>zh</u>anantum <u>zh</u>anna ssaw-i wakuntum qawman buur<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4596, '48:13', 48, 13, 'waman lam yu/min bil<u>aa</u>hi warasuulihi fa-inn<u>aa</u> a\'tadn<u>aa</u> lilk<u>aa</u>firiina sa\'iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4597, '48:14', 48, 14, 'walill<u>aa</u>hi mulku ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i yaghfiru liman yasy<u>aa</u>u wayu\'a<u>dzdz</u>ibu man yasy<u>aa</u>u wak<u>aa</u>nall<u>aa</u>hu ghafuuran ra<u>h</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4598, '48:15', 48, 15, 'sayaquulu lmukhallafuuna i<u>dzaa</u> in<u>th</u>alaqtum il<u>aa</u> magh<u>aa</u>nima lita/khu<u>dz</u>uuh<u>aa</u> <u>dz</u>aruun<u>aa</u> nattabi\'kum yuriiduuna an yubaddiluu kal<u>aa</u>mall<u>aa</u>hi qul lan tattabi\'uun<u>aa</u> ka<u>dzaa</u>likum q<u>aa</u>lall<u>aa</u>hu min qablu fasayaquuluuna bal ta<u>h</u>suduunan<u>aa</u> bal k<u>aa</u>nuu l<u>aa</u> yafqahuuna ill<u>aa</u> qaliil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4599, '48:16', 48, 16, 'qul lilmukhallafiina mina l-a\'r<u>aa</u>bi satud\'awna il<u>aa</u> qawmin ulii ba/sin syadiidin tuq<u>aa</u>tiluunahum aw yuslimuuna fa-in tu<u>th</u>ii\'uu yu/tikumull<u>aa</u>hu ajran <u>h</u>asanan wa-in tatawallaw kam<u>aa</u> tawallaytum min qablu yu\'a<u>dzdz</u>ibkum \'a<u>dzaa</u>ban liim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4600, '48:17', 48, 17, 'laysa \'al<u>aa</u> l-a\'m<u>aa</u> <u>h</u>arajun wal<u>aa</u> \'al<u>aa</u> l-a\'raji <u>h</u>arajun wal<u>aa</u> \'al<u>aa</u> lmarii<u>dh</u>i <u>h</u>arajun waman yu<u>th</u>i\'ill<u>aa</u>ha warasuulahu yudkhilhu jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru waman yatawalla yu\'a<u>dzdz</u>ibhu \'a<u>dzaa</u>ban liim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4601, '48:18', 48, 18, 'laqad ra<u>dh</u>iyall<u>aa</u>hu \'ani lmu/miniina i<u>dz</u> yub<u>aa</u>yi\'uunaka ta<u>h</u>ta sysyajarati fa\'alima m<u>aa</u> fii quluubihim fa-anzala sakiinata \'alayhim wa-ats<u>aa</u>bahum fat<u>h</u>an qariib<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4602, '48:19', 48, 19, 'wamagh<u>aa</u>nima katsiiratan ya/khu<u>dz</u>uunah<u>aa</u> wak<u>aa</u>nall<u>aa</u>hu \'aziizan <u>h</u>akiim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4603, '48:20', 48, 20, 'wa\'adakumull<u>aa</u>hu magh<u>aa</u>nima katsiiratan ta/khu<u>dz</u>uunah<u>aa</u> fa\'ajjala lakum h<u>aadz</u>ihi wakaffa aydiya nn<u>aa</u>si \'ankum walitakuuna <u>aa</u>yatan lilmu/miniina wayahdiyakum <u>sh</u>ir<u>aath</u>an mustaqiim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4604, '48:21', 48, 21, 'waukhr<u>aa</u> lam taqdiruu \'alayh<u>aa</u> qad a<u>hath</u>all<u>aa</u>hu bih<u>aa</u> wak<u>aa</u>nall<u>aa</u>hu \'al<u>aa</u> kulli syay-in qadiir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4605, '48:22', 48, 22, 'walaw q<u>aa</u>talakumulla<u>dz</u>iina kafaruu lawallawuu l-adb<u>aa</u>ra tsumma l<u>aa</u> yajiduuna waliyyan wal<u>aa</u> na<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4606, '48:23', 48, 23, 'sunnatall<u>aa</u>hillatii qad khalat min qablu walan tajida lisunnatill<u>aa</u>hi tabdiil<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4607, '48:24', 48, 24, 'wahuwalla<u>dz</u>ii kaffa aydiyahum \'ankum wa-aydiyakum \'anhum biba<u>th</u>ni makkata min ba\'di an a<u>zh</u>farakum \'alayhim wak<u>aa</u>nall<u>aa</u>hu bim<u>aa</u> ta\'maluuna ba<u>sh</u>iir<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4608, '48:25', 48, 25, 'humulla<u>dz</u>iina kafaruu wa<u>sh</u>adduukum \'ani lmasjidi l<u>h</u>ar<u>aa</u>mi walhadya ma\'kuufan an yablugha ma<u>h</u>illahu walawl<u>aa</u> rij<u>aa</u>lun mu/minuuna wanis<u>aa</u>un mu/min<u>aa</u>tun lam ta\'lamuuhum an ta<u>th</u>auuhum fatu<u>sh</u>iibakum minhum ma\'arratun bighayri \'ilmin liyudkhilall<u>aa</u>hu fii ra<u>h</u>matihi man yasy<u>aa</u>u law tazayyaluu la\'a<u>dzdz</u>abn<u>aa</u>lla<u>dz</u>iina kafaruu minhum \'a<u>dzaa</u>ban liim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4609, '48:26', 48, 26, 'i<u>dz</u> ja\'alalla<u>dz</u>iina kafaruu fii quluubihimu l<u>h</u>amiyyata <u>h</u>amiyyata lj<u>aa</u>hiliyyati fa-anzalall<u>aa</u>hu sakiinatahu \'al<u>aa</u> rasuulihi wa\'al<u>aa</u> lmu/miniina wa-alzamahum kalimata ttaqw<u>aa</u> wak<u>aa</u>nuu a<u>h</u>aqqa bih<u>aa</u> wa-ahlah<u>aa</u> wak<u>aa</u>nall<u>aa</u>hu bikulli syay-in \'aliim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4610, '48:27', 48, 27, 'laqad <u>sh</u>adaqall<u>aa</u>hu rasuulahu rru/y<u>aa</u> bil<u>h</u>aqqi latadkhulunna lmasjida l<u>h</u>ar<u>aa</u>ma in sy<u>aa</u>-all<u>aa</u>hu <u>aa</u>miniina mu<u>h</u>alliqiina ruuusakum wamuqa<u>shsh</u>iriina l<u>aa</u> takh<u>aa</u>fuuna fa\'alima m<u>aa</u> lam ta\'lamuu faja\'ala min duuni <u>dzaa</u>lika fat<u>h</u>an qariib<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4611, '48:28', 48, 28, 'huwalla<u>dz</u>ii arsala rasuulahu bilhud<u>aa</u> wadiini l<u>h</u>aqqi liyu<u>zh</u>hirahu \'al<u>aa</u> ddiini kullihi wakaf<u>aa</u> bil<u>aa</u>hi syahiid<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4612, '48:29', 48, 29, 'mu<u>h</u>ammadun rasuulull<u>aa</u>hi walla<u>dz</u>iina ma\'ahu asyidd<u>aa</u>u \'al<u>aa</u> lkuff<u>aa</u>ri ru<u>h</u>am<u>aa</u>u baynahum tar<u>aa</u>hum rukka\'an sujjadan yabtaghuuna fa<u>dh</u>lan minall<u>aa</u>hi wari<u>dh</u>w<u>aa</u>nan siim<u>aa</u>hum fii wujuuhihim min atsari ssujuudi <u>dzaa</u>lika matsaluhum fii ttawr<u>aa</u>ti wamatsaluhum fii l-injiili kazar\'in akhraja sya<u>th</u>-ahu fa<u>aa</u>zarahu fastaghla<u>zh</u>a fastaw<u>aa</u> \'al<u>aa</u> suuqihi yu\'jibu zzurr<u>aa</u>\'a liyaghii<u>zh</u>a bihimu lkuff<u>aa</u>ra wa\'adall<u>aa</u>hulla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti minhum maghfiratan wa-ajran \'a<u>zh</u>iim<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4613, '49:1', 49, 1, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tuqaddimuu bayna yadayill<u>aa</u>hi warasuulihi wattaquull<u>aa</u>ha innall<u>aa</u>ha samii\'un \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (4614, '49:2', 49, 2, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tarfa\'uu a<u>sh</u>w<u>aa</u>takum fawqa <u>sh</u>awti nnabiyyi wal<u>aa</u> tajharuu lahu bilqawli kajahri ba\'<u>dh</u>ikum liba\'<u>dh</u>in an ta<u>h</u>ba<u>th</u>a a\'m<u>aa</u>lukum wa-antum l<u>aa</u> tasy\'uruun\r\r\n');
INSERT INTO quranLatin VALUES (4615, '49:3', 49, 3, 'innalla<u>dz</u>iina yaghu<u>dhdh</u>uuna a<u>sh</u>w<u>aa</u>tahum \'inda rasuulill<u>aa</u>hi ul<u>aa</u>-ikalla<u>dz</u>iina imta<u>h</u>anall<u>aa</u>hu quluubahum littaqw<u>aa</u> lahum maghfiratun wa-ajrun \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (4616, '49:4', 49, 4, 'innalla<u>dz</u>iina yun<u>aa</u>duunaka min war<u>aa</u>-i l<u>h</u>ujur<u>aa</u>ti aktsaruhum l<u>aa</u> ya\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (4617, '49:5', 49, 5, 'walaw annahum <u>sh</u>abaruu <u>h</u>att<u>aa</u> takhruja ilayhim lak<u>aa</u>na khayran lahum wal<u>aa</u>hu ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (4618, '49:6', 49, 6, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu in j<u>aa</u>-akum f<u>aa</u>siqun binaba-in fatabayyanuu an tu<u>sh</u>iibuu qawman bijah<u>aa</u>latin fatu<u>sh</u>bi<u>h</u>uu \'al<u>aa</u> m<u>aa</u> fa\'altum n<u>aa</u>dimiin\r\r\n');
INSERT INTO quranLatin VALUES (4619, '49:7', 49, 7, 'wa\'lamuu anna fiikum rasuulall<u>aa</u>hi law yu<u>th</u>ii\'ukum fii katsiirin mina l-amri la\'anittum wal<u>aa</u>kinnall<u>aa</u>ha <u>h</u>abbaba ilaykumu l-iim<u>aa</u>na wazayyanahu fii quluubikum wakarraha ilaykumu lkufra walfusuuqa wal\'i<u>sh</u>y<u>aa</u>na ul<u>aa</u>-ika humu rr<u>aa</u>syiduun\r\r\n');
INSERT INTO quranLatin VALUES (4620, '49:8', 49, 8, 'fa<u>dh</u>lan minall<u>aa</u>hi wani\'matan wal<u>aa</u>hu \'aliimun <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (4621, '49:9', 49, 9, 'wa-in <u>th</u><u>aa</u>-ifat<u>aa</u>ni mina lmu/miniina iqtataluu fa-a<u>sh</u>li<u>h</u>uu baynahum<u>aa</u> fa-in baghat i<u>h</u>d<u>aa</u>hum<u>aa</u> \'al<u>aa</u> l-ukhr<u>aa</u> faq<u>aa</u>tiluullatii tabghii <u>h</u>att<u>aa</u> tafii-a il<u>aa</u> amrill<u>aa</u>hi fa-in f<u>aa</u>-at fa-a<u>sh</u>li<u>h</u>uu baynahum<u>aa</u> bil\'adli wa-aqsi<u>th</u>uu innall<u>aa</u>ha yu<u>h</u>ibbu lmuqsi<u>th</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (4622, '49:10', 49, 10, 'innam<u>aa</u> lmu/minuuna ikhwatun fa-a<u>sh</u>li<u>h</u>uu bayna akhawaykum wattaquull<u>aa</u>ha la\'allakum tur<u>h</u>amuun\r\r\n');
INSERT INTO quranLatin VALUES (4623, '49:11', 49, 11, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> yaskhar qawmun min qawmin \'as<u>aa</u> an yakuunuu khayran minhum wal<u>aa</u> nis<u>aa</u>un min nis<u>aa</u>-in \'as<u>aa</u> an yakunna khayran minhunna wal<u>aa</u> talmizuu anfusakum wal<u>aa</u> tan<u>aa</u>bazuu bil-alq<u>aa</u>bi bi/sa l-ismu lfusuuqu ba\'da l-iim<u>aa</u>ni waman lam yatub faul<u>aa</u>-ika humu <u>zhzhaa</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (4624, '49:12', 49, 12, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu ijtanibuu katsiiran mina <u>zhzh</u>anni inna ba\'<u>dh</u>a <u>zhzh</u>anni itsmun wal<u>aa</u> tajassasuu wal<u>aa</u> yaghtab ba\'<u>dh</u>ukum ba\'<u>dh</u>an ayu<u>h</u>ibbu a<u>h</u>adukum an ya/kula la<u>h</u>ma akhiihi maytan fakarihtumuuhu wattaquull<u>aa</u>ha innall<u>aa</u>ha taww<u>aa</u>bun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (4625, '49:13', 49, 13, 'y<u>aa</u> ayyuh<u>aa</u> nn<u>aa</u>su inn<u>aa</u> khalaqn<u>aa</u>kum min <u>dz</u>akarin waunts<u>aa</u> waja\'aln<u>aa</u>kum syu\'uuban waqab<u>aa</u>-ila lita\'<u>aa</u>rafuu inna akramakum \'indall<u>aa</u>hi atq<u>aa</u>kum innall<u>aa</u>ha \'aliimun khabiir\r\r\n');
INSERT INTO quranLatin VALUES (4626, '49:14', 49, 14, 'q<u>aa</u>lati l-a\'r<u>aa</u>bu <u>aa</u>mann<u>aa</u> qul lam tu/minuu wal<u>aa</u>kin quuluu aslamn<u>aa</u> walamm<u>aa</u> yadkhuli l-iim<u>aa</u>nu fii quluubikum wa-in tu<u>th</u>ii\'uull<u>aa</u>ha warasuulahu l<u>aa</u> yalitkum min a\'m<u>aa</u>likum syay-an innall<u>aa</u>ha ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (4627, '49:15', 49, 15, 'innam<u>aa</u> lmu/minuunalla<u>dz</u>iina <u>aa</u>manuu bil<u>aa</u>hi warasuulihi tsumma lam yart<u>aa</u>buu waj<u>aa</u>haduu bi-amw<u>aa</u>lihim wa-anfusihim fii sabiilill<u>aa</u>hi ul<u>aa</u>-ika humu <u>shshaa</u>diquun\r\r\n');
INSERT INTO quranLatin VALUES (4628, '49:16', 49, 16, 'qul atu\'allimuunall<u>aa</u>ha bidiinikum wal<u>aa</u>hu ya\'lamu m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii l-ar<u>dh</u>i wal<u>aa</u>hu bikulli syay-in \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (4629, '49:17', 49, 17, 'yamunnuuna \'alayka an aslamuu qul l<u>aa</u> tamunnuu \'alayya isl<u>aa</u>makum balill<u>aa</u>hu yamunnu \'alaykum an had<u>aa</u>kum lil-iim<u>aa</u>ni in kuntum <u>shaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (4630, '49:18', 49, 18, 'innall<u>aa</u>ha ya\'lamu ghayba ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wal<u>aa</u>hu ba<u>sh</u>iirun bim<u>aa</u> ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (4631, '50:1', 50, 1, 'q<u>aa</u>f walqur-<u>aa</u>ni lmajiid\r\r\n');
INSERT INTO quranLatin VALUES (4632, '50:2', 50, 2, 'bal \'ajibuu an j<u>aa</u>-ahum mun<u>dz</u>irun minhum faq<u>aa</u>la lk<u>aa</u>firuuna h<u>aadzaa</u> syay-un \'ajiib\r\r\n');
INSERT INTO quranLatin VALUES (4633, '50:3', 50, 3, 'a-i<u>dzaa</u> mitn<u>aa</u> wakunn<u>aa</u> tur<u>aa</u>ban <u>dzaa</u>lika raj\'un ba\'iid\r\r\n');
INSERT INTO quranLatin VALUES (4634, '50:4', 50, 4, 'qad \'alimn<u>aa</u> m<u>aa</u> tanqu<u>sh</u>u l-ar<u>dh</u>u minhum wa\'indan<u>aa</u> kit<u>aa</u>bun <u>h</u>afii<u>zh</u>\r\r\n');
INSERT INTO quranLatin VALUES (4635, '50:5', 50, 5, 'bal ka<u>dzdz</u>abuu bil<u>h</u>aqqi lamm<u>aa</u> j<u>aa</u>-ahum fahum fii amrin mariij\r\r\n');
INSERT INTO quranLatin VALUES (4636, '50:6', 50, 6, 'afalam yan<u>zh</u>uruu il<u>aa</u> ssam<u>aa</u>-i fawqahum kayfa banayn<u>aa</u>h<u>aa</u> wazayyann<u>aa</u>h<u>aa</u> wam<u>aa</u> lah<u>aa</u> min furuuj\r\r\n');
INSERT INTO quranLatin VALUES (4637, '50:7', 50, 7, 'wal-ar<u>dh</u>a madadn<u>aa</u>h<u>aa</u> wa-alqayn<u>aa</u> fiih<u>aa</u> raw<u>aa</u>siya wa-anbatn<u>aa</u> fiih<u>aa</u> min kulli zawjin bahiij\r\r\n');
INSERT INTO quranLatin VALUES (4638, '50:8', 50, 8, 'tab<u>sh</u>iratan wa<u>dz</u>ikr<u>aa</u> likulli \'abdin muniib\r\r\n');
INSERT INTO quranLatin VALUES (4639, '50:9', 50, 9, 'wanazzaln<u>aa</u> mina ssam<u>aa</u>-i m<u>aa</u>-an mub<u>aa</u>rakan fa-anbatn<u>aa</u> bihi jann<u>aa</u>tin wa<u>h</u>abba l<u>h</u>a<u>sh</u>iid\r\r\n');
INSERT INTO quranLatin VALUES (4640, '50:10', 50, 10, 'wannakhla b<u>aa</u>siq<u>aa</u>tin lah<u>aa</u> <u>th</u>al\'un na<u>dh</u>iid\r\r\n');
INSERT INTO quranLatin VALUES (4641, '50:11', 50, 11, 'rizqan lil\'ib<u>aa</u>di wa-a<u>h</u>yayn<u>aa</u> bihi baldatan maytan ka<u>dzaa</u>lika lkhuruuj\r\r\n');
INSERT INTO quranLatin VALUES (4642, '50:12', 50, 12, 'ka<u>dzdz</u>abat qablahum qawmu nuu<u>h</u>in wa-a<u>sh</u>-<u>haa</u>bu rrassi watsamuud\r\r\n');
INSERT INTO quranLatin VALUES (4643, '50:13', 50, 13, 'wa\'<u>aa</u>dun wafir\'awnu wa-ikhw<u>aa</u>nu luu<u>th</u>\r\r\n');
INSERT INTO quranLatin VALUES (4644, '50:14', 50, 14, 'wa-a<u>sh</u>-<u>haa</u>bu l-aykati waqawmu tubba\'in kullun ka<u>dzdz</u>aba rrusula fa<u>h</u>aqqa wa\'iid\r\r\n');
INSERT INTO quranLatin VALUES (4645, '50:15', 50, 15, 'afa\'ayiin<u>aa</u> bilkhalqi l-awwali bal hum fii labsin min khalqin jadiid\r\r\n');
INSERT INTO quranLatin VALUES (4646, '50:16', 50, 16, 'walaqad khalaqn<u>aa</u> l-ins<u>aa</u>na wana\'lamu m<u>aa</u> tuwaswisu bihi nafsuhu wana<u>h</u>nu aqrabu ilayhi min <u>h</u>abli lwariid\r\r\n');
INSERT INTO quranLatin VALUES (4647, '50:17', 50, 17, 'i<u>dz</u> yatalaqq<u>aa</u> lmutalaqqiy<u>aa</u>ni \'ani lyamiini wa\'ani sysyim<u>aa</u>li qa\'iid\r\r\n');
INSERT INTO quranLatin VALUES (4648, '50:18', 50, 18, 'm<u>aa</u> yalfi<u>zh</u>u min qawlin ill<u>aa</u> ladayhi raqiibun \'atiid\r\r\n');
INSERT INTO quranLatin VALUES (4649, '50:19', 50, 19, 'waj<u>aa</u>-at sakratu lmawti bil<u>h</u>aqqi <u>dzaa</u>lika m<u>aa</u> kunta minhu ta<u>h</u>iid\r\r\n');
INSERT INTO quranLatin VALUES (4650, '50:20', 50, 20, 'wanufikha fii <u>shsh</u>uuri <u>dzaa</u>lika yawmu lwa\'iid\r\r\n');
INSERT INTO quranLatin VALUES (4651, '50:21', 50, 21, 'waj<u>aa</u>-at kullu nafsin ma\'ah<u>aa</u> s<u>aa</u>-iqun wasyahiid\r\r\n');
INSERT INTO quranLatin VALUES (4652, '50:22', 50, 22, 'laqad kunta fii ghaflatin min h<u>aadzaa</u> fakasyafn<u>aa</u> \'anka ghi<u>th</u><u>aa</u>-aka faba<u>sh</u>aruka lyawma <u>h</u>adiid\r\r\n');
INSERT INTO quranLatin VALUES (4653, '50:23', 50, 23, 'waq<u>aa</u>la qariinuhu h<u>aadzaa</u> m<u>aa</u> ladayya \'atiid\r\r\n');
INSERT INTO quranLatin VALUES (4654, '50:24', 50, 24, 'alqiy<u>aa</u> fii jahannama kulla kaff<u>aa</u>rin \'aniid\r\r\n');
INSERT INTO quranLatin VALUES (4655, '50:25', 50, 25, 'mann<u>aa</u>\'in lilkhayri mu\'tadin muriib\r\r\n');
INSERT INTO quranLatin VALUES (4656, '50:26', 50, 26, '<B>al</b>la<u>dz</u>ii ja\'ala ma\'all<u>aa</u>hi il<u>aa</u>han <u>aa</u>khara fa-alqiy<u>aa</u>hu fii l\'a<u>dzaa</u>bi sysyadiid\r\r\n');
INSERT INTO quranLatin VALUES (4657, '50:27', 50, 27, 'q<u>aa</u>la qariinuhu rabban<u>aa</u> m<u>aa</u> a<u>th</u>ghaytuhu wal<u>aa</u>kin k<u>aa</u>na fii <u>dh</u>al<u>aa</u>lin ba\'iid\r\r\n');
INSERT INTO quranLatin VALUES (4658, '50:28', 50, 28, 'q<u>aa</u>la l<u>aa</u> takhta<u>sh</u>imuu ladayya waqad qaddamtu ilaykum bilwa\'iid\r\r\n');
INSERT INTO quranLatin VALUES (4659, '50:29', 50, 29, 'm<u>aa</u> yubaddalu lqawlu ladayya wam<u>aa</u> an<u>aa</u> bi<u>zh</u>all<u>aa</u>min lil\'abiid\r\r\n');
INSERT INTO quranLatin VALUES (4660, '50:30', 50, 30, 'yawma naquulu lijahannama hali imtala/ti wataquulu hal min maziid\r\r\n');
INSERT INTO quranLatin VALUES (4661, '50:31', 50, 31, 'wauzlifati ljannatu lilmuttaqiina ghayra ba\'iid\r\r\n');
INSERT INTO quranLatin VALUES (4662, '50:32', 50, 32, 'h<u>aadzaa</u> m<u>aa</u> tuu\'aduuna likulli aww<u>aa</u>bin <u>h</u>afii<u>zh</u>\r\r\n');
INSERT INTO quranLatin VALUES (4663, '50:33', 50, 33, 'man khasyiya rra<u>h</u>m<u>aa</u>na bilghaybi waj<u>aa</u>-a biqalbin muniib\r\r\n');
INSERT INTO quranLatin VALUES (4664, '50:34', 50, 34, 'udkhuluuh<u>aa</u> bisal<u>aa</u>min <u>dzaa</u>lika yawmu lkhuluud\r\r\n');
INSERT INTO quranLatin VALUES (4665, '50:35', 50, 35, 'lahum m<u>aa</u> yasy<u>aa</u>uuna fiih<u>aa</u> waladayn<u>aa</u> maziid\r\r\n');
INSERT INTO quranLatin VALUES (4666, '50:36', 50, 36, 'wakam ahlakn<u>aa</u> qablahum min qarnin hum asyaddu minhum ba<u>th</u>syan fanaqqabuu fii lbil<u>aa</u>di hal min ma<u>h</u>ii<u>sh</u>\r\r\n');
INSERT INTO quranLatin VALUES (4667, '50:37', 50, 37, 'inna fii <u>dzaa</u>lika la<u>dz</u>ikr<u>aa</u> liman k<u>aa</u>na lahu qalbun aw lq<u>aa</u> ssam\'a wahuwa syahiid\r\r\n');
INSERT INTO quranLatin VALUES (4668, '50:38', 50, 38, 'walaqad khalaqn<u>aa</u> ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a wam<u>aa</u> baynahum<u>aa</u> fii sittati ayy<u>aa</u>min wam<u>aa</u> massan<u>aa</u> min lughuub\r\r\n');
INSERT INTO quranLatin VALUES (4669, '50:39', 50, 39, 'fa<u>sh</u>bir \'al<u>aa</u> m<u>aa</u> yaquuluuna wasabbi<u>h</u> bi<u>h</u>amdi rabbika qabla <u>th</u>uluu\'i sysyamsi waqabla lghuruub\r\r\n');
INSERT INTO quranLatin VALUES (4670, '50:40', 50, 40, 'waminallayli fasabbi<u>h</u>hu wa-adb<u>aa</u>ra ssujuud\r\r\n');
INSERT INTO quranLatin VALUES (4671, '50:41', 50, 41, 'wastami\'yawma yun<u>aa</u>di lmun<u>aa</u>di min mak<u>aa</u>nin qariib\r\r\n');
INSERT INTO quranLatin VALUES (4672, '50:42', 50, 42, 'yawma yasma\'uuna <u>shsh</u>ay<u>h</u>ata bil<u>h</u>aqqi <u>dzaa</u>lika yawmu lkhuruuj\r\r\n');
INSERT INTO quranLatin VALUES (4673, '50:43', 50, 43, 'inn<u>aa</u> na<u>h</u>nu nu<u>h</u>yii wanumiitu wa-ilayn<u>aa</u> lma<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (4674, '50:44', 50, 44, 'yawma tasyaqqaqu l-ar<u>dh</u>u \'anhum sir<u>aa</u>\'an <u>dzaa</u>lika <u>h</u>asyrun \'alayn<u>aa</u> yasiir\r\r\n');
INSERT INTO quranLatin VALUES (4675, '50:45', 50, 45, 'na<u>h</u>nu a\'lamu bim<u>aa</u> yaquuluuna wam<u>aa</u> anta \'alayhim bijabb<u>aa</u>rin fa<u>dz</u>akkir bilqur-<u>aa</u>ni man yakh<u>aa</u>fu wa\'iid\r\r\n');
INSERT INTO quranLatin VALUES (4676, '51:1', 51, 1, 'wa<u>dzdzaa</u>riy<u>aa</u>ti <u>dz</u>arw<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4677, '51:2', 51, 2, 'fal<u>haa</u>mil<u>aa</u>ti wiqr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4678, '51:3', 51, 3, 'falj<u>aa</u>riy<u>aa</u>ti yusr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4679, '51:4', 51, 4, 'falmuqassim<u>aa</u>ti amr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (4680, '51:5', 51, 5, 'innam<u>aa</u> tuu\'aduuna la<u>shaa</u>diq\r\r\n');
INSERT INTO quranLatin VALUES (4681, '51:6', 51, 6, 'wa-inna ddiina law<u>aa</u>qi\'<B>un</b>\r\r\n');
INSERT INTO quranLatin VALUES (4682, '51:7', 51, 7, 'wassam<u>aa</u>-i <u>dzaa</u>ti l<u>h</u>ubuk\r\r\n');
INSERT INTO quranLatin VALUES (4683, '51:8', 51, 8, 'innakum lafii qawlin mukhtalif\r\r\n');
INSERT INTO quranLatin VALUES (4684, '51:9', 51, 9, 'yu/faku \'anhu man ufik\r\r\n');
INSERT INTO quranLatin VALUES (4685, '51:10', 51, 10, 'qutila lkharr<u>aas</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (4686, '51:11', 51, 11, '<B>al</b>la<u>dz</u>iina hum fii ghamratin s<u>aa</u>huun\r\r\n');
INSERT INTO quranLatin VALUES (4687, '51:12', 51, 12, 'yas-aluuna ayy<u>aa</u>na yawmu ddiin\r\r\n');
INSERT INTO quranLatin VALUES (4688, '51:13', 51, 13, 'yawma hum \'al<u>aa</u> nn<u>aa</u>ri yuftanuun\r\r\n');
INSERT INTO quranLatin VALUES (4689, '51:14', 51, 14, '<u>dz</u>uuquu fitnatakum h<u>aadzaa</u>lla<u>dz</u>ii kuntum bihi tasta\'jiluun\r\r\n');
INSERT INTO quranLatin VALUES (4690, '51:15', 51, 15, 'inna lmuttaqiina fii jann<u>aa</u>tin wa\'uyuun\r\r\n');
INSERT INTO quranLatin VALUES (4691, '51:16', 51, 16, '<u>aa</u>khi<u>dz</u>iina m<u>aa</u> <u>aa</u>t<u>aa</u>hum rabbuhum innahum k<u>aa</u>nuu qabla <u>dzaa</u>lika mu<u>h</u>siniin\r\r\n');
INSERT INTO quranLatin VALUES (4692, '51:17', 51, 17, 'k<u>aa</u>nuu qaliilan minallayli m<u>aa</u> yahja\'uun\r\r\n');
INSERT INTO quranLatin VALUES (4693, '51:18', 51, 18, 'wabil-as<u>haa</u>ri hum yastaghfiruun\r\r\n');
INSERT INTO quranLatin VALUES (4694, '51:19', 51, 19, 'wafii amw<u>aa</u>lihim <u>h</u>aqqun liss<u>aa</u>-ili walma<u>h</u>ruum\r\r\n');
INSERT INTO quranLatin VALUES (4695, '51:20', 51, 20, 'wafii l-ar<u>dh</u>i <u>aa</u>y<u>aa</u>tun lilmuuqiniin\r\r\n');
INSERT INTO quranLatin VALUES (4696, '51:21', 51, 21, 'wafii anfusikum afal<u>aa</u> tub<u>sh</u>iruun\r\r\n');
INSERT INTO quranLatin VALUES (4697, '51:22', 51, 22, 'wafii ssam<u>aa</u>-i rizqukum wam<u>aa</u> tuu\'aduun\r\r\n');
INSERT INTO quranLatin VALUES (4698, '51:23', 51, 23, 'fawarabbi ssam<u>aa</u>-i wal-ar<u>dh</u>i innahu la<u>h</u>aqqun mitsla m<u>aa</u> annakum tan<u>th</u>iquun\r\r\n');
INSERT INTO quranLatin VALUES (4699, '51:24', 51, 24, 'hal at<u>aa</u>ka <u>h</u>adiitsu <u>dh</u>ayfi ibr<u>aa</u>hiima lmukramiin\r\r\n');
INSERT INTO quranLatin VALUES (4700, '51:25', 51, 25, 'i<u>dz</u> dakhaluu \'alayhi faq<u>aa</u>luu sal<u>aa</u>man q<u>aa</u>la sal<u>aa</u>mun qawmun munkaruun\r\r\n');
INSERT INTO quranLatin VALUES (4701, '51:26', 51, 26, 'far<u>aa</u>gha il<u>aa</u> ahlihi faj<u>aa</u>-a bi\'ijlin samiin\r\r\n');
INSERT INTO quranLatin VALUES (4702, '51:27', 51, 27, 'faqarrabahu ilayhim q<u>aa</u>la l<u>aa</u> ta/kuluun\r\r\n');
INSERT INTO quranLatin VALUES (4703, '51:28', 51, 28, 'fa-awjasa minhum khiifatan q<u>aa</u>luu l<u>aa</u> takhaf wabasysyaruuhu bighul<u>aa</u>min \'aliim\r\r\n');
