INSERT INTO quranLatin VALUES (5079, '57:4', 57, 4, 'huwalla<u>dz</u>ii khalaqa ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a fii sittati ayy<u>aa</u>min tsumma istaw<u>aa</u> \'al<u>aa</u> l\'arsyi ya\'lamu m<u>aa</u> yaliju fii l-ar<u>dh</u>i wam<u>aa</u> yakhruju minh<u>aa</u> wam<u>aa</u> yanzilu mina ssam<u>aa</u>-i wam<u>aa</u> ya\'ruju fiih<u>aa</u> wahuwa ma\'akum ayna m<u>aa</u> kuntum wal<u>aa</u>hu bim<u>aa</u> ta\'maluuna ba<u>sh</u>iir\r\r\n');
