INSERT INTO quranLatin VALUES (5082, '57:7', 57, 7, '<u>aa</u>minuu bil<u>aa</u>hi warasuulihi wa-anfiquu mimm<u>aa</u> ja\'alakum mustakhlafiina fiihi falla<u>dz</u>iina <u>aa</u>manuu minkum wa-anfaquu lahum ajrun kabiir\r\r\n');
