INSERT INTO quranLatin VALUES (5084, '57:9', 57, 9, 'huwalla<u>dz</u>ii yunazzilu \'al<u>aa</u> \'abdihi <u>aa</u>y<u>aa</u>tin bayyin<u>aa</u>tin liyukhrijakum mina <u>zhzh</u>ulum<u>aa</u>ti il<u>aa</u> nnuuri wa-innall<u>aa</u>ha bikum larauufun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (5085, '57:10', 57, 10, 'wam<u>aa</u> lakumll<u>aa</u> tunfiquu fii sabiilill<u>aa</u>hi walill<u>aa</u>hi miir<u>aa</u>tsu ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i l<u>aa</u> yastawii minkum man anfaqa min qabli lfat<u>h</u>i waq<u>aa</u>tala ul<u>aa</u>-ika a\'<u>zh</u>amu darajatan minalla<u>dz</u>iina anfaquu min ba\'du waq<u>aa</u>taluu wakullan wa\'adall<u>aa</u>hu l<u>h</u>usn<u>aa</u> wal<u>aa</u>hu bim<u>aa</u> ta\'maluuna khabiir\r\r\n');
