INSERT INTO quranLatin VALUES (5087, '57:12', 57, 12, 'yawma tar<u>aa</u> lmu/miniina walmu/min<u>aa</u>ti yas\'<u>aa</u> nuuruhum bayna aydiihim wabi-aym<u>aa</u>nihim busyr<u>aa</u>kumu lyawma jann<u>aa</u>tun tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> <u>dzaa</u>lika huwa lfawzu l\'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (5088, '57:13', 57, 13, 'yawma yaquulu lmun<u>aa</u>fiquuna walmun<u>aa</u>fiq<u>aa</u>tu lilla<u>dz</u>iina <u>aa</u>manuu un<u>zh</u>uruun<u>aa</u> naqtabis min nuurikum qiila irji\'uu war<u>aa</u>-akum faltamisuu nuuran fa<u>dh</u>uriba baynahum bisuurin lahu b<u>aa</u>bun b<u>aath</u>inuhu fiihi rra<u>h</u>matu wa<u>zhaa</u>hiruhu min qibalihi l\'a<u>dzaa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (5089, '57:14', 57, 14, 'yun<u>aa</u>duunahum alam nakun ma\'akum q<u>aa</u>luu bal<u>aa</u> wal<u>aa</u>kinnakum fatantum anfusakum watarabba<u>sh</u>tum wartabtum wagharratkumu al-am<u>aa</u>niyyu <u>h</u>att<u>aa</u> j<u>aa</u>-a amrull<u>aa</u>hi wagharrakum bil<u>aa</u>hi algharuur\r\r\n');
INSERT INTO quranLatin VALUES (5090, '57:15', 57, 15, 'falyawma l<u>aa</u> yu/kha<u>dz</u>u minkum fidyatun wal<u>aa</u> minalla<u>dz</u>iina kafaruu ma/w<u>aa</u>kumu nn<u>aa</u>ru hiya mawl<u>aa</u>kum wabi/sa lma<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (5091, '57:16', 57, 16, 'alam ya/ni lilla<u>dz</u>iina <u>aa</u>manuu an takhsya\'a quluubuhum li<u>dz</u>ikrill<u>aa</u>hi wam<u>aa</u> nazala mina l<u>h</u>aqqi wal<u>aa</u> yakuunuu kalla<u>dz</u>iina uutuu lkit<u>aa</u>ba min qablu fa<u>th</u><u>aa</u>la \'alayhimu l-amadu faqasat quluubuhum wakatsiirun minhum f<u>aa</u>siquun\r\r\n');
INSERT INTO quranLatin VALUES (5092, '57:17', 57, 17, 'i\'lamuu annall<u>aa</u>ha yu<u>h</u>yii l-ar<u>dh</u>a ba\'da mawtih<u>aa</u> qad bayyann<u>aa</u> lakumu l-<u>aa</u>y<u>aa</u>ti la\'allakum ta\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (5093, '57:18', 57, 18, 'inna lmu<u>shsh</u>addiqiina walmu<u>shsh</u>addiq<u>aa</u>ti wa-aqra<u>dh</u>uull<u>aa</u>ha qar<u>dh</u>an <u>h</u>asanan yu<u>daa</u>\'afu lahum walahum ajrun kariim\r\r\n');
INSERT INTO quranLatin VALUES (5094, '57:19', 57, 19, 'walla<u>dz</u>iina <u>aa</u>manuu bil<u>aa</u>hi warusulihi ul<u>aa</u>-ika humu <u>shsh</u>iddiiquuna wasysyuhad<u>aa</u>u \'inda rabbihim lahum ajruhum wanuuruhum walla<u>dz</u>iina kafaruu waka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu lja<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (5095, '57:20', 57, 20, 'i\'lamuu annam<u>aa</u> l<u>h</u>ay<u>aa</u>tu dduny<u>aa</u> la\'ibun walahwun waziinatun wataf<u>aa</u>khurun baynakum watak<u>aa</u>tsurun fii l-amw<u>aa</u>li wal-awl<u>aa</u>di kamatsali ghaytsin a\'jaba lkuff<u>aa</u>ra nab<u>aa</u>tuhu tsumma yahiiju fatar<u>aa</u>hu mu<u>sh</u>farran tsumma yakuunu <u>h</u>u<u>th</u><u>aa</u>man wafii l-<u>aa</u>khirati \'a<u>dzaa</u>bun syadiidun wamaghfiratun minall<u>aa</u>hi wari<u>dh</u>w<u>aa</u>nun wam<u>aa</u> l<u>h</u>ay<u>aa</u>tu dduny<u>aa</u> ill<u>aa</u> mat<u>aa</u>\'u lghuruur\r\r\n');
INSERT INTO quranLatin VALUES (5096, '57:21', 57, 21, 's<u>aa</u>biquu il<u>aa</u> maghfiratin min rabbikum wajannatin \'ar<u>dh</u>uh<u>aa</u> ka\'ar<u>dh</u>i ssam<u>aa</u>-i wal-ar<u>dh</u>i u\'iddat lilla<u>dz</u>iina <u>aa</u>manuu bil<u>aa</u>hi warusulihi <u>dzaa</u>lika fa<u>dh</u>lull<u>aa</u>hi yu/tiihi man yasy<u>aa</u>u wal<u>aa</u>hu <u>dz</u>uu lfa<u>dh</u>li l\'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (5097, '57:22', 57, 22, 'm<u>aa</u> a<u>shaa</u>ba min mu<u>sh</u>iibatin fii l-ar<u>dh</u>i wal<u>aa</u> fii anfusikum ill<u>aa</u> fii kit<u>aa</u>bin min qabli an nabra-ah<u>aa</u> inna <u>dzaa</u>lika \'al<u>aa</u>ll<u>aa</u>hi yasiir\r\r\n');
INSERT INTO quranLatin VALUES (5098, '57:23', 57, 23, 'likayl<u>aa</u> ta/saw \'al<u>aa</u> m<u>aa</u> f<u>aa</u>takum wal<u>aa</u> tafra<u>h</u>uu bim<u>aa</u> <u>aa</u>t<u>aa</u>kum wal<u>aa</u>hu l<u>aa</u> yu<u>h</u>ibbu kulla mukht<u>aa</u>lin fakhuur\r\r\n');
INSERT INTO quranLatin VALUES (5099, '57:24', 57, 24, '<B>al</b>la<u>dz</u>iina yabkhaluuna waya/muruuna nn<u>aa</u>sa bilbukhli waman yatawalla fa-innall<u>aa</u>ha huwa lghaniyyu l<u>h</u>amiid\r\r\n');
INSERT INTO quranLatin VALUES (5100, '57:25', 57, 25, 'laqad arsaln<u>aa</u> rusulan<u>aa</u> bilbayyin<u>aa</u>ti wa-anzaln<u>aa</u> ma\'ahumu lkit<u>aa</u>ba walmiiz<u>aa</u>na liyaquuma nn<u>aa</u>su bilqis<u>th</u>i wa-anzaln<u>aa</u> l<u>h</u>adiida fiihi ba/sun syadiidun waman<u>aa</u>fi\'u linn<u>aa</u>si waliya\'lamall<u>aa</u>hu man yan<u>sh</u>uruhu warusulahu bilghaybi innall<u>aa</u>ha qawiyyun \'aziiz\r\r\n');
INSERT INTO quranLatin VALUES (5101, '57:26', 57, 26, 'walaqad arsaln<u>aa</u> nuu<u>h</u>an wa-ibr<u>aa</u>hiima waja\'aln<u>aa</u> fii <u>dz</u>urriyyatihim<u>aa</u> nnubuwwata walkit<u>aa</u>ba faminhum muhtadin wakatsiirun minhum f<u>aa</u>siquun\r\r\n');
INSERT INTO quranLatin VALUES (5102, '57:27', 57, 27, 'tsumma qaffayn<u>aa</u> \'al<u>aa</u> <u>aa</u>ts<u>aa</u>rihim birusulin<u>aa</u> waqaffayn<u>aa</u> bi\'iis<u>aa</u> ibni maryama wa<u>aa</u>tayn<u>aa</u>hu l-injiila waja\'aln<u>aa</u> fii quluubilla<u>dz</u>iina ittaba\'uuhu ra/fatan wara<u>h</u>matan warahb<u>aa</u>niyyatan ibtada\'uuh<u>aa</u> m<u>aa</u> katabn<u>aa</u>h<u>aa</u> \'alayhim ill<u>aa</u> ibtigh<u>aa</u>-a ri<u>dh</u>w<u>aa</u>nill<u>aa</u>hi fam<u>aa</u> ra\'awh<u>aa</u> <u>h</u>aqqa ri\'<u>aa</u>yatih<u>aa</u> fa<u>aa</u>tayn<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu minhum ajrahum wakatsiirun minhum f<u>aa</u>siquun\r\r\n');
INSERT INTO quranLatin VALUES (5103, '57:28', 57, 28, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu ittaquull<u>aa</u>ha wa<u>aa</u>minuu birasuulihi yu/tikum kiflayni min ra<u>h</u>matihi wayaj\'al lakum nuuran tamsyuuna bihi wayaghfir lakum wal<u>aa</u>hu ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (5104, '57:29', 57, 29, 'li-all<u>aa</u> ya\'lama ahlu lkit<u>aa</u>bill<u>aa</u> yaqdiruuna \'al<u>aa</u> syay-in min fa<u>dh</u>lill<u>aa</u>hi wa-anna lfa<u>dh</u>la biyadill<u>aa</u>hi yu/tiihi man yasy<u>aa</u>u wal<u>aa</u>hu <u>dz</u>uu lfa<u>dh</u>li l\'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (5105, '58:1', 58, 1, 'qad sami\'all<u>aa</u>hu qawlallatii tuj<u>aa</u>diluka fii zawjih<u>aa</u> watasytakii il<u>aa</u>ll<u>aa</u>hi wal<u>aa</u>hu yasma\'u ta<u>haa</u>wurakum<u>aa</u> innall<u>aa</u>ha samii\'un ba<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (5106, '58:2', 58, 2, '<B>al</b>la<u>dz</u>iina yu<u>zhaa</u>hiruuna minkum min nis<u>aa</u>-ihim m<u>aa</u> hunna ummah<u>aa</u>tihim in ummah<u>aa</u>tuhum ill<u>aa</u>ll<u>aa</u>-ii waladnahum wa-innahum layaquuluuna munkaran mina lqawli wazuuran wa-innall<u>aa</u>ha la\'afuwwun ghafuur\r\r\n');
INSERT INTO quranLatin VALUES (5107, '58:3', 58, 3, 'walla<u>dz</u>iina yu<u>zhaa</u>hiruuna min nis<u>aa</u>-ihim tsumma ya\'uuduuna lim<u>aa</u> q<u>aa</u>luu fata<u>h</u>riiru raqabatin min qabli an yatam<u>aa</u>ss<u>aa</u> <u>dzaa</u>likum tuu\'a<u>zh</u>uuna bihi wal<u>aa</u>hu bim<u>aa</u> ta\'maluuna khabiir\r\r\n');
INSERT INTO quranLatin VALUES (5108, '58:4', 58, 4, 'faman lam yajid fa<u>sh</u>iy<u>aa</u>mu syahrayni mutat<u>aa</u>bi\'ayni min qabli an yatam<u>aa</u>ss<u>aa</u> faman lam yasta<u>th</u>i\'fa-i<u>th</u>\'<u>aa</u>mu sittiina miskiinan <u>dzaa</u>lika litu/minuu bil<u>aa</u>hi warasuulihi watilka <u>h</u>uduudull<u>aa</u>hi walilk<u>aa</u>firiina \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (5109, '58:5', 58, 5, 'innalla<u>dz</u>iina yu<u>haa</u>dduunall<u>aa</u>ha warasuulahu kubituu kam<u>aa</u> kubitalla<u>dz</u>iina min qablihim waqad anzaln<u>aa</u> <u>aa</u>y<u>aa</u>tin bayyin<u>aa</u>tin walilk<u>aa</u>firiina \'a<u>dzaa</u>bun muhiin\r\r\n');
INSERT INTO quranLatin VALUES (5110, '58:6', 58, 6, 'yawma yab\'atsuhumull<u>aa</u>hu jamii\'an fayunabbi-uhum bim<u>aa</u> \'amiluu a<u>hsaa</u>hull<u>aa</u>hu wanasuuhu wal<u>aa</u>hu \'al<u>aa</u> kulli syay-in syahiid\r\r\n');
INSERT INTO quranLatin VALUES (5111, '58:7', 58, 7, 'alam tara annall<u>aa</u>ha ya\'lamu m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii l-ar<u>dh</u>i m<u>aa</u> yakuunu min najw<u>aa</u> tsal<u>aa</u>tsatin ill<u>aa</u> huwa r<u>aa</u>bi\'uhum wal<u>aa</u> khamsatin ill<u>aa</u> huwa s<u>aa</u>disuhum wal<u>aa</u> adn<u>aa</u> min <u>dzaa</u>lika wal<u>aa</u> aktsara ill<u>aa</u> huwa ma\'ahum ayna m<u>aa</u> k<u>aa</u>nuu tsumma yunabbi-uhum bim<u>aa</u> \'amiluu yawma lqiy<u>aa</u>mati innall<u>aa</u>ha bikulli syay-in \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (5112, '58:8', 58, 8, 'alam tara il<u>aa</u>lla<u>dz</u>iina nuhuu \'ani nnajw<u>aa</u> tsumma ya\'uuduuna lim<u>aa</u> nuhuu \'anhu wayatan<u>aa</u>jawna bil-itsmi wal\'udw<u>aa</u>ni wama\'<u>sh</u>iyati rrasuuli wa-i<u>dzaa</u> j<u>aa</u>uuka <u>h</u>ayyawka bim<u>aa</u> lam yu<u>h</u>ayyika bihill<u>aa</u>hu wayaquuluuna fii anfusihim lawl<u>aa</u> yu\'a<u>dzdz</u>ibun<u>aa</u>ll<u>aa</u>hu bim<u>aa</u> naquulu <u>h</u>asbuhum jahannamu ya<u>sh</u>lawnah<u>aa</u> fabi/sa lma<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (5113, '58:9', 58, 9, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu i<u>dzaa</u> tan<u>aa</u>jaytum fal<u>aa</u> tatan<u>aa</u>jaw bil-itsmi wal\'udw<u>aa</u>ni wama\'<u>sh</u>iyati rrasuuli watan<u>aa</u>jaw bilbirri wattaqw<u>aa</u> wattaquull<u>aa</u>halla<u>dz</u>ii ilayhi tu<u>h</u>syaruun\r\r\n');
INSERT INTO quranLatin VALUES (5114, '58:10', 58, 10, 'innam<u>aa</u> nnajw<u>aa</u> mina sysyay<u>th</u><u>aa</u>ni liya<u>h</u>zunalla<u>dz</u>iina <u>aa</u>manuu walaysa bi<u>daa</u>rrihim syay-an ill<u>aa</u> bi-i<u>dz</u>nill<u>aa</u>hi wa\'al<u>aa</u>ll<u>aa</u>hi falyatawakkali lmu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (5115, '58:11', 58, 11, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu i<u>dzaa</u> qiila lakum tafassa<u>h</u>uu fii lmaj<u>aa</u>lisi fafsa<u>h</u>uu yafsa<u>h</u>ill<u>aa</u>hu lakum wa-i<u>dzaa</u> qiila unsyuzuu fansyuzuu yarfa\'ill<u>aa</u>hulla<u>dz</u>iina <u>aa</u>manuu minkum walla<u>dz</u>iina uutuu l\'ilma daraj<u>aa</u>tin wal<u>aa</u>hu bim<u>aa</u> ta\'maluuna khabiir\r\r\n');
INSERT INTO quranLatin VALUES (5116, '58:12', 58, 12, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu i<u>dzaa</u> n<u>aa</u>jaytumu rrasuula faqaddimuu bayna yaday najw<u>aa</u>kum <u>sh</u>adaqatan <u>dzaa</u>lika khayrun lakum wa-a<u>th</u>haru fa-in lam tajiduu fa-innall<u>aa</u>ha ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (5117, '58:13', 58, 13, 'a-asyfaqtum an tuqaddimuu bayna yaday najw<u>aa</u>kum <u>sh</u>adaq<u>aa</u>tin fa-i<u>dz</u> lam taf\'aluu wat<u>aa</u>ball<u>aa</u>hu \'alaykum fa-aqiimuu <u>shsh</u>al<u>aa</u>ta wa<u>aa</u>tuu zzak<u>aa</u>ta wa-a<u>th</u>ii\'uull<u>aa</u>ha warasuulahu wal<u>aa</u>hu khabiirun bim<u>aa</u> ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (5118, '58:14', 58, 14, 'alam tara il<u>aa</u>lla<u>dz</u>iina tawallaw qawman gha<u>dh</u>iball<u>aa</u>hu \'alayhim m<u>aa</u> hum minkum wal<u>aa</u> minhum waya<u>h</u>lifuuna \'al<u>aa</u> lka<u>dz</u>ibi wahum ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (5119, '58:15', 58, 15, 'a\'addall<u>aa</u>hu lahum \'a<u>dzaa</u>ban syadiidan innahum s<u>aa</u>-a m<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (5120, '58:16', 58, 16, 'ittakha<u>dz</u>uu aym<u>aa</u>nahum junnatan fa<u>sh</u>adduu \'an sabiilill<u>aa</u>hi falahum \'a<u>dzaa</u>bun muhiin\r\r\n');
INSERT INTO quranLatin VALUES (5121, '58:17', 58, 17, 'lan tughniya \'anhum amw<u>aa</u>luhum wal<u>aa</u> awl<u>aa</u>duhum minall<u>aa</u>hi syay-an ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu nn<u>aa</u>ri hum fiih<u>aa</u> kh<u>aa</u>liduun\r\r\n');
INSERT INTO quranLatin VALUES (5122, '58:18', 58, 18, 'yawma yab\'atsuhumull<u>aa</u>hu jamii\'an faya<u>h</u>lifuuna lahu kam<u>aa</u> ya<u>h</u>lifuuna lakum waya<u>h</u>sabuuna annahum \'al<u>aa</u> syay-in l<u>aa</u> innahum humu lk<u>aadz</u>ibuun\r\r\n');
INSERT INTO quranLatin VALUES (5123, '58:19', 58, 19, 'ista<u>h</u>wa<u>dz</u>a \'alayhimu sysyay<u>th</u><u>aa</u>nu fa-ans<u>aa</u>hum <u>dz</u>ikrall<u>aa</u>hi ul<u>aa</u>-ika <u>h</u>izbu sysyay<u>th</u><u>aa</u>ni l<u>aa</u> inna <u>h</u>izba sysyay<u>th</u><u>aa</u>ni humu lkh<u>aa</u>siruun\r\r\n');
INSERT INTO quranLatin VALUES (5124, '58:20', 58, 20, 'innalla<u>dz</u>iina yu<u>haa</u>dduunall<u>aa</u>ha warasuulahu ul<u>aa</u>-ika fii l-a<u>dz</u>alliin\r\r\n');
INSERT INTO quranLatin VALUES (5125, '58:21', 58, 21, 'kataball<u>aa</u>hu la-aghlibanna an<u>aa</u> warusulii innall<u>aa</u>ha qawiyyun \'aziiz\r\r\n');
INSERT INTO quranLatin VALUES (5126, '58:22', 58, 22, 'l<u>aa</u> tajidu qawman yu/minuuna bil<u>aa</u>hi walyawmi l-<u>aa</u>khiri yuw<u>aa</u>dduuna man <u>haa</u>ddall<u>aa</u>ha warasuulahu walaw k<u>aa</u>nuu <u>aa</u>b<u>aa</u>-ahum aw abn<u>aa</u>-ahum aw ikhw<u>aa</u>nahum aw \'asyiiratahum ul<u>aa</u>-ika kataba fii quluubihimu l-iim<u>aa</u>na wa-ayyadahum biruu<u>h</u>in minhu wayudkhiluhum jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> ra<u>dh</u>iyall<u>aa</u>hu \'anhum wara<u>dh</u>uu \'anhu ul<u>aa</u>-ika <u>h</u>izbull<u>aa</u>hi l<u>aa</u> inna <u>h</u>izball<u>aa</u>hi humu lmufli<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (5127, '59:1', 59, 1, 'sabba<u>h</u>a lill<u>aa</u>hi m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii l-ar<u>dh</u>i wahuwa l\'aziizu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (5128, '59:2', 59, 2, 'huwalla<u>dz</u>ii akhrajalla<u>dz</u>iina kafaruu min ahli lkit<u>aa</u>bi min diy<u>aa</u>rihim li-awwali l<u>h</u>asyri m<u>aa</u> <u>zh</u>anantum an yakhrujuu wa<u>zh</u>annuu annahum m<u>aa</u>ni\'atuhum <u>h</u>u<u>sh</u>uunuhum minall<u>aa</u>hi fa-at<u>aa</u>humull<u>aa</u>hu min <u>h</u>aytsu lam ya<u>h</u>tasibuu waqa<u>dz</u>afa fii quluubihimu rru\'ba yukhribuuna buyuutahum bi-aydiihim wa-aydii lmu/miniina fa\'tabiruu y<u>aa</u> ulii l-ab<u>shaa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (5129, '59:3', 59, 3, 'walawl<u>aa</u> an kataball<u>aa</u>hu \'alayhimu ljal<u>aa</u>-a la\'a<u>dzdz</u>abahum fii dduny<u>aa</u> walahum fii l-<u>aa</u>khirati \'a<u>dzaa</u>bu nn<u>aa</u>r\r\r\n');
INSERT INTO quranLatin VALUES (5130, '59:4', 59, 4, '<u>dzaa</u>lika bi-annahum sy<u>aa</u>qquull<u>aa</u>ha warasuulahu waman yusy<u>aa</u>qqill<u>aa</u>ha fa-innall<u>aa</u>ha syadiidu l\'iq<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (5131, '59:5', 59, 5, 'm<u>aa</u> qa<u>th</u>a\'tum min liinatin aw taraktumuuh<u>aa</u> q<u>aa</u>-imatan \'al<u>aa</u> u<u>sh</u>uulih<u>aa</u> fabi-i<u>dz</u>nill<u>aa</u>hi waliyukhziya lf<u>aa</u>siqiin\r\r\n');
INSERT INTO quranLatin VALUES (5132, '59:6', 59, 6, 'wam<u>aa</u> af<u>aa</u>-all<u>aa</u>hu \'al<u>aa</u> rasuulihi minhum fam<u>aa</u> awjaftum \'alayhi min khaylin wal<u>aa</u> rik<u>aa</u>bin wal<u>aa</u>kinnall<u>aa</u>ha yusalli<u>th</u>u rusulahu \'al<u>aa</u> man yasy<u>aa</u>u wal<u>aa</u>hu \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (5133, '59:7', 59, 7, 'm<u>aa</u> af<u>aa</u>-all<u>aa</u>hu \'al<u>aa</u> rasuulihi min ahli lqur<u>aa</u> falill<u>aa</u>hi walirrasuuli wali<u>dz</u>ii lqurb<u>aa</u> walyat<u>aa</u>m<u>aa</u> walmas<u>aa</u>kiini wabni ssabiili kay l<u>aa</u> yakuuna duulatan bayna l-aghniy<u>aa</u>-i minkum wam<u>aa</u> <u>aa</u>t<u>aa</u>kumu rrasuulu fakhu<u>dz</u>uuhu wam<u>aa</u> nah<u>aa</u>kum \'anhu fantahuu wattaquull<u>aa</u>ha innall<u>aa</u>ha syadiidu l\'iq<u>aa</u>b\r\r\n');
INSERT INTO quranLatin VALUES (5134, '59:8', 59, 8, 'lilfuqar<u>aa</u>-i lmuh<u>aa</u>jiriinalla<u>dz</u>iina ukhrijuu min diy<u>aa</u>rihim wa-amw<u>aa</u>lihim yabtaghuuna fa<u>dh</u>lan minall<u>aa</u>hi wari<u>dh</u>w<u>aa</u>nan wayan<u>sh</u>uruunall<u>aa</u>ha warasuulahu ul<u>aa</u>-ika humu <u>shshaa</u>diquun\r\r\n');
INSERT INTO quranLatin VALUES (5135, '59:9', 59, 9, 'walla<u>dz</u>iina tabawwauu dd<u>aa</u>ra wal-iim<u>aa</u>na min qablihim yu<u>h</u>ibbuuna man h<u>aa</u>jara ilayhim wal<u>aa</u> yajiduuna fii <u>sh</u>uduurihim <u>haa</u>jatan mimm<u>aa</u> uutuu wayu/tsiruuna \'al<u>aa</u> anfusihim walaw k<u>aa</u>na bihim kha<u>sas</u>atun waman yuuqa syu<u>hh</u>a nafsihi faul<u>aa</u>-ika humu lmufli<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (5136, '59:10', 59, 10, 'walla<u>dz</u>iina j<u>aa</u>uu min ba\'dihim yaquuluuna rabban<u>aa</u> ighfir lan<u>aa</u> wali-ikhw<u>aa</u>nin<u>aa</u>lla<u>dz</u>iina sabaquun<u>aa</u> bil-iim<u>aa</u>ni wal<u>aa</u> taj\'al fii quluubin<u>aa</u> ghillan lilla<u>dz</u>iina <u>aa</u>manuu rabban<u>aa</u> innaka rauufun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (5137, '59:11', 59, 11, 'alam tara il<u>aa</u>lla<u>dz</u>iina n<u>aa</u>faquu yaquuluuna li-ikhw<u>aa</u>nihimulla<u>dz</u>iina kafaruu min ahli lkit<u>aa</u>bi la-in ukhrijtum lanakhrujanna ma\'akum wal<u>aa</u> nu<u>th</u>ii\'u fiikum a<u>h</u>adan abadan wa-in quutiltum lanan<u>sh</u>urannakum wal<u>aa</u>hu yasyhadu innahum lak<u>aadz</u>ibuun\r\r\n');
INSERT INTO quranLatin VALUES (5138, '59:12', 59, 12, 'la-in ukhrijuu l<u>aa</u> yakhrujuuna ma\'ahum wala-in quutiluu l<u>aa</u> yan<u>sh</u>uruunahum wala-in na<u>sh</u>aruuhum layuwallunna l-adb<u>aa</u>ra tsumma l<u>aa</u> yun<u>sh</u>aruun\r\r\n');
INSERT INTO quranLatin VALUES (5139, '59:13', 59, 13, 'la-antum asyaddu rahbatan fii <u>sh</u>uduurihim minall<u>aa</u>hi <u>dzaa</u>lika bi-annahum qawmun l<u>aa</u> yafqahuun\r\r\n');
INSERT INTO quranLatin VALUES (5140, '59:14', 59, 14, 'l<u>aa</u> yuq<u>aa</u>tiluunakum jamii\'an ill<u>aa</u> fii quran mu<u>h</u>a<u>shsh</u>anatin aw min war<u>aa</u>-i judurin ba/suhum baynahum syadiidun ta<u>h</u>sabuhum jamii\'an waquluubuhum syatt<u>aa</u> <u>dzaa</u>lika bi-annahum qawmun l<u>aa</u> ya\'qiluun\r\r\n');
INSERT INTO quranLatin VALUES (5141, '59:15', 59, 15, 'kamatsalilla<u>dz</u>iina min qablihim qariiban <u>dzaa</u>quu wab<u>aa</u>la amrihim walahum \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (5142, '59:16', 59, 16, 'kamatsali sysyay<u>th</u><u>aa</u>ni i<u>dz</u> q<u>aa</u>la lil-ins<u>aa</u>ni ukfur falamm<u>aa</u> kafara q<u>aa</u>la innii barii-un minka innii akh<u>aa</u>full<u>aa</u>ha rabba l\'<u>aa</u>lamiin\r\r\n');
INSERT INTO quranLatin VALUES (5143, '59:17', 59, 17, 'fak<u>aa</u>na \'<u>aa</u>qibatahum<u>aa</u> annahum<u>aa</u> fii nn<u>aa</u>ri kh<u>aa</u>lidayni fiih<u>aa</u> wa<u>dzaa</u>lika jaz<u>aa</u>u <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (5144, '59:18', 59, 18, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu ittaquull<u>aa</u>ha waltan<u>zh</u>ur nafsun m<u>aa</u> qaddamat lighadin wattaquull<u>aa</u>ha innall<u>aa</u>ha khabiirun bim<u>aa</u> ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (5145, '59:19', 59, 19, 'wal<u>aa</u> takuunuu kalla<u>dz</u>iina nasuull<u>aa</u>ha fa-ans<u>aa</u>hum anfusahum ul<u>aa</u>-ika humu lf<u>aa</u>siquun\r\r\n');
INSERT INTO quranLatin VALUES (5146, '59:20', 59, 20, 'l<u>aa</u> yastawii a<u>sh</u>-<u>haa</u>bu nn<u>aa</u>ri wa-a<u>sh</u>-<u>haa</u>bu ljannati a<u>sh</u>-<u>haa</u>bu ljannati humu lf<u>aa</u>-izuun\r\r\n');
INSERT INTO quranLatin VALUES (5147, '59:21', 59, 21, 'law anzaln<u>aa</u> h<u>aadzaa</u> lqur-<u>aa</u>na \'al<u>aa</u> jabalin lara-aytahu kh<u>aa</u>syi\'an muta<u>sh</u>addi\'an min khasyyatill<u>aa</u>hi watilka l-amts<u>aa</u>lu na<u>dh</u>ribuh<u>aa</u> linn<u>aa</u>si la\'allahum yatafakkaruun\r\r\n');
INSERT INTO quranLatin VALUES (5148, '59:22', 59, 22, 'huwall<u>aa</u>hulla<u>dz</u>ii l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa \'<u>aa</u>limu lghaybi wasysyah<u>aa</u>dati huwa rra<u>h</u>m<u>aa</u>nu rra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (5149, '59:23', 59, 23, 'huwall<u>aa</u>hulla<u>dz</u>ii l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa lmaliku lqudduusu ssal<u>aa</u>mu lmu/minu lmuhayminu l\'aziizu ljabb<u>aa</u>ru lmutakabbiru sub<u>haa</u>nall<u>aa</u>hi \'amm<u>aa</u> yusyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (5150, '59:24', 59, 24, 'huwall<u>aa</u>hu lkh<u>aa</u>liqu lb<u>aa</u>ri-u lmu<u>sh</u>awwiru lahu l-asm<u>aa</u>u l<u>h</u>usn<u>aa</u> yusabbi<u>h</u>u lahu m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wahuwa l\'aziizu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (5151, '60:1', 60, 1, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tattakhi<u>dz</u>uu \'aduwwii wa\'aduwwakum awliy<u>aa</u>-a tulquuna ilayhim bilmawaddati waqad kafaruu bim<u>aa</u> j<u>aa</u>-akum mina l<u>h</u>aqqi yukhrijuuna rrasuula wa-iyy<u>aa</u>kum an tu/minuu bil<u>aa</u>hi rabbikum in kuntum kharajtum jih<u>aa</u>dan fii sabiilii wabtigh<u>aa</u>-a mar<u>daa</u>tii tusirruuna ilayhim bilmawaddati wa-an<u>aa</u> a\'lamu bim<u>aa</u> akhfaytum wam<u>aa</u> a\'lantum waman yaf\'alhu minkum faqad <u>dh</u>alla saw<u>aa</u>-a ssabiil\r\r\n');
INSERT INTO quranLatin VALUES (5152, '60:2', 60, 2, 'in yatsqafuukum yakuunuu lakam a\'d<u>aa</u>-an wayabsu<u>th</u>uu ilaykum aydiyahum wa-alsinatahum bissuu-i wawadduu law takfuruun\r\r\n');
INSERT INTO quranLatin VALUES (5153, '60:3', 60, 3, 'lan tanfa\'akum ar<u>haa</u>mukum wal<u>aa</u> awl<u>aa</u>dukum yawma lqiy<u>aa</u>mati yaf<u>sh</u>ilu baynakum wal<u>aa</u>hu bim<u>aa</u> ta\'maluuna ba<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (5154, '60:4', 60, 4, 'qad k<u>aa</u>nat lakum uswatun <u>h</u>asanatun fii ibr<u>aa</u>hiima walla<u>dz</u>iina ma\'ahu i<u>dz</u> q<u>aa</u>luu liqawmihim inn<u>aa</u> bura<u>aa</u>u minkum wamimm<u>aa</u> ta\'buduuna min duunill<u>aa</u>hi kafarn<u>aa</u> bikum wabad<u>aa</u> baynan<u>aa</u> wabaynakumu l\'ad<u>aa</u>watu walbagh<u>daa</u>u abadan <u>h</u>att<u>aa</u> tu/minuu bil<u>aa</u>hi wa<u>h</u>dahu ill<u>aa</u> qawla ibr<u>aa</u>hiima li-abiihi la-astaghfiranna laka wam<u>aa</u> amliku laka minall<u>aa</u>hi min syay-in rabban<u>aa</u> \'alayka tawakkaln<u>aa</u> wa-ilayka anabn<u>aa</u> wa-ilayka lma<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (5155, '60:5', 60, 5, 'rabban<u>aa</u> l<u>aa</u> taj\'aln<u>aa</u> fitnatan lilla<u>dz</u>iina kafaruu waghfir lan<u>aa</u> rabban<u>aa</u> innaka anta l\'aziizu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (5156, '60:6', 60, 6, 'laqad k<u>aa</u>na lakum fiihim uswatun <u>h</u>asanatun liman k<u>aa</u>na yarjuull<u>aa</u>ha walyawma l-<u>aa</u>khira waman yatawalla fa-innall<u>aa</u>ha huwa lghanniyyu l<u>h</u>amiid\r\r\n');
INSERT INTO quranLatin VALUES (5157, '60:7', 60, 7, '\'as<u>aa</u>ll<u>aa</u>hu an yaj\'ala baynakum wabaynalla<u>dz</u>iina \'<u>aa</u>daytum minhum mawaddatan wal<u>aa</u>hu qadiirun wal<u>aa</u>hu ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (5158, '60:8', 60, 8, 'l<u>aa</u> yanh<u>aa</u>kumull<u>aa</u>hu \'anilla<u>dz</u>iina lam yuq<u>aa</u>tiluukum fii ddiini walam yukhrijuukum min diy<u>aa</u>rikum an tabarruuhum watuqsi<u>th</u>uu ilayhim innall<u>aa</u>ha yu<u>h</u>ibbu lmuqsi<u>th</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (5159, '60:9', 60, 9, 'innam<u>aa</u> yanh<u>aa</u>kumull<u>aa</u>hu \'anilla<u>dz</u>iina q<u>aa</u>taluukum fii ddiini wa-akhrajuukum min diy<u>aa</u>rikum wa<u>zhaa</u>haruu \'al<u>aa</u> ikhr<u>aa</u>jikum an tawallawhum waman yatawallahum faul<u>aa</u>-ika humu <u>zhzhaa</u>limuun\r\r\n');
INSERT INTO quranLatin VALUES (5160, '60:10', 60, 10, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu i<u>dzaa</u> j<u>aa</u>-akumu lmu/min<u>aa</u>tu muh<u>aa</u>jir<u>aa</u>tin famta<u>h</u>inuuhunnall<u>aa</u>hu a\'lamu bi-iim<u>aa</u>nihinna fa-in \'alimtumuuhunna mu/min<u>aa</u>tin fal<u>aa</u> tarji\'uuhunna il<u>aa</u> lkuff<u>aa</u>ri l<u>aa</u> hunna <u>h</u>illun lahum wal<u>aa</u> hum ya<u>h</u>illuuna lahunna wa<u>aa</u>tuuhum m<u>aa</u> anfaquu wal<u>aa</u> jun<u>aah</u>a \'alaykum an tanki<u>h</u>uuhunna i<u>dzaa</u> <u>aa</u>taytumuuhunna ujuurahunna wal<u>aa</u> tumsikuu bi\'i<u>sh</u>ami lkaw<u>aa</u>firi was-aluu m<u>aa</u> anfaqtum walyas-aluu m<u>aa</u> anfaquu <u>dzaa</u>likum <u>h</u>ukmull<u>aa</u>hi ya<u>h</u>kumu baynakum wal<u>aa</u>hu \'aliimun <u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (5161, '60:11', 60, 11, 'wa-in f<u>aa</u>takum syay-un min azw<u>aa</u>jikum il<u>aa</u> lkuff<u>aa</u>ri fa\'<u>aa</u>qabtum fa<u>aa</u>tuulla<u>dz</u>iina <u>dz</u>ahabat azw<u>aa</u>juhum mitsla m<u>aa</u> anfaquu wattaquull<u>aa</u>halla<u>dz</u>ii antum bihi mu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (5162, '60:12', 60, 12, 'y<u>aa</u> ayyuh<u>aa</u> nnabiyyu i<u>dzaa</u> j<u>aa</u>-aka lmu/min<u>aa</u>tu yub<u>aa</u>yi\'naka \'al<u>aa</u> an l<u>aa</u> yusyrikna bil<u>aa</u>hi syay-an wal<u>aa</u> yasriqna wal<u>aa</u> yazniina wal<u>aa</u> yaqtulna awl<u>aa</u>dahunna wal<u>aa</u> ya/tiina bibuht<u>aa</u>nin yaftariinahu bayna aydiihinna wa-arjulihinna wal<u>aa</u> ya\'<u>sh</u>iinaka fii ma\'ruufin fab<u>aa</u>yi\'hunna wastaghfir lahunnall<u>aa</u>ha innall<u>aa</u>ha ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (5163, '60:13', 60, 13, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tatawallaw qawman gha<u>dh</u>iball<u>aa</u>hu \'alayhim qad ya-isuu mina l-<u>aa</u>khirati kam<u>aa</u> ya-isa lkuff<u>aa</u>ru min a<u>sh</u>-<u>haa</u>bi lqubuur\r\r\n');
INSERT INTO quranLatin VALUES (5164, '61:1', 61, 1, 'sabba<u>h</u>a lill<u>aa</u>hi m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii l-ar<u>dh</u>i wahuwa l\'aziizu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (5165, '61:2', 61, 2, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu lima taquuluuna m<u>aa</u> l<u>aa</u> taf\'aluun\r\r\n');
INSERT INTO quranLatin VALUES (5166, '61:3', 61, 3, 'kabura maqtan \'indall<u>aa</u>hi an taquuluu m<u>aa</u> l<u>aa</u> taf\'aluun\r\r\n');
INSERT INTO quranLatin VALUES (5167, '61:4', 61, 4, 'innall<u>aa</u>ha yu<u>h</u>ibbulla<u>dz</u>iina yuq<u>aa</u>tiluuna fii sabiilihi <u>sh</u>affan ka-annahum buny<u>aa</u>nun mar<u>sh</u>uu<u>sh</u>\r\r\n');
INSERT INTO quranLatin VALUES (5168, '61:5', 61, 5, 'wa-i<u>dz</u> q<u>aa</u>la muus<u>aa</u> liqawmihi y<u>aa</u> qawmi lima tu/<u>dz</u>uunanii waqad ta\'lamuuna annii rasuulull<u>aa</u>hi ilaykum falamm<u>aa</u> z<u>aa</u>ghuu az<u>aa</u>ghall<u>aa</u>hu quluubahum wal<u>aa</u>hu l<u>aa</u> yahdii lqawma lf<u>aa</u>siqiin\r\r\n');
INSERT INTO quranLatin VALUES (5169, '61:6', 61, 6, 'wa-i<u>dz</u> q<u>aa</u>la \'iis<u>aa</u> ibnu maryama y<u>aa</u> banii isr<u>aa</u>-iila innii rasuulull<u>aa</u>hi ilaykum mu<u>sh</u>addiqan lim<u>aa</u> bayna yadayya mina ttawr<u>aa</u>ti wamubasysyiran birasuulin ya/tii min ba\'dii ismuhu a<u>h</u>madu falamm<u>aa</u> j<u>aa</u>-ahum bilbayyin<u>aa</u>ti q<u>aa</u>luu h<u>aadzaa</u> si<u>h</u>run mubiin\r\r\n');
INSERT INTO quranLatin VALUES (5170, '61:7', 61, 7, 'waman a<u>zh</u>lamu mimmani iftar<u>aa</u> \'al<u>aa</u>ll<u>aa</u>hi lka<u>dz</u>iba wahuwa yud\'<u>aa</u> il<u>aa</u> l-isl<u>aa</u>mi wal<u>aa</u>hu l<u>aa</u> yahdii lqawma <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (5171, '61:8', 61, 8, 'yuriiduuna liyu<u>th</u>fi-uu nuurall<u>aa</u>hi bi-afw<u>aa</u>hihim wal<u>aa</u>hu mutimmu nuurihi walaw kariha lk<u>aa</u>firuun\r\r\n');
INSERT INTO quranLatin VALUES (5172, '61:9', 61, 9, 'huwalla<u>dz</u>ii arsala rasuulahu bilhud<u>aa</u> wadiini l<u>h</u>aqqi liyu<u>zh</u>hirahu \'al<u>aa</u> ddiini kullihi walaw kariha lmusyrikuun\r\r\n');
INSERT INTO quranLatin VALUES (5173, '61:10', 61, 10, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu hal adullukum \'al<u>aa</u> tij<u>aa</u>ratin tunjiikum min \'a<u>dzaa</u>bin liim\r\r\n');
INSERT INTO quranLatin VALUES (5174, '61:11', 61, 11, 'tu/minuuna bil<u>aa</u>hi warasuulihi watuj<u>aa</u>hiduuna fii sabiilill<u>aa</u>hi bi-amw<u>aa</u>likum wa-anfusikum <u>dzaa</u>likum khayrun lakum in kuntum ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (5175, '61:12', 61, 12, 'yaghfir lakum <u>dz</u>unuubakum wayudkhilkum jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru wamas<u>aa</u>kina <u>th</u>ayyibatan fii jann<u>aa</u>ti \'adnin <u>dzaa</u>lika lfawzu l\'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (5176, '61:13', 61, 13, 'waukhr<u>aa</u> tu<u>h</u>ibbuunah<u>aa</u> na<u>sh</u>run minall<u>aa</u>hi wafat<u>h</u>un qarriibun wabasysyiri lmu/miniin\r\r\n');
INSERT INTO quranLatin VALUES (5177, '61:14', 61, 14, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu kuunuu an<u>shaa</u>rall<u>aa</u>hi kam<u>aa</u> q<u>aa</u>la \'iis<u>aa</u> ibnu maryama lil<u>h</u>aw<u>aa</u>riyyiina man an<u>shaa</u>rii il<u>aa</u>ll<u>aa</u>hi q<u>aa</u>la l<u>h</u>aw<u>aa</u>riyyuuna na<u>h</u>nu an<u>shaa</u>rull<u>aa</u>hi fa<u>aa</u>manat <u>th</u><u>aa</u>-ifatun min banii isr<u>aa</u>-iila wakafarat <u>th</u><u>aa</u>-ifatun fa-ayyadn<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu \'al<u>aa</u> \'aduwwihim fa-a<u>sh</u>ba<u>h</u>uu <u>zhaa</u>hiriin\r\r\n');
INSERT INTO quranLatin VALUES (5178, '62:1', 62, 1, 'yusabbi<u>h</u>u lill<u>aa</u>hi m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii l-ar<u>dh</u>i lmaliki lqudduusi l\'aziizi l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (5179, '62:2', 62, 2, 'huwalla<u>dz</u>ii ba\'atsa fii l-ummiyyiina rasuulan minhum yatluu \'alayhim <u>aa</u>y<u>aa</u>tihi wayuzakkiihim wayu\'allimuhumu lkit<u>aa</u>ba wal<u>h</u>ikmata wa-in k<u>aa</u>nuu min qablu lafii <u>dh</u>al<u>aa</u>lin mubiin\r\r\n');
INSERT INTO quranLatin VALUES (5180, '62:3', 62, 3, 'wa<u>aa</u>khariina minhum lamm<u>aa</u> yal<u>h</u>aquu bihim wahuwa l\'aziizu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (5181, '62:4', 62, 4, '<u>dzaa</u>lika fa<u>dh</u>lull<u>aa</u>hi yu/tiihi man yasy<u>aa</u>u wal<u>aa</u>hu <u>dz</u>uu lfa<u>dh</u>li l\'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (5182, '62:5', 62, 5, 'matsalulla<u>dz</u>iina <u>h</u>ummiluu ttawr<u>aa</u>ta tsumma lam ya<u>h</u>miluuh<u>aa</u> kamatsali l<u>h</u>im<u>aa</u>ri ya<u>h</u>milu asf<u>aa</u>ran bi/sa matsalu lqawmilla<u>dz</u>iina ka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>till<u>aa</u>hi wal<u>aa</u>hu l<u>aa</u> yahdii lqawma <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (5183, '62:6', 62, 6, 'qul y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina h<u>aa</u>duu in za\'amtum annakum awliy<u>aa</u>u lill<u>aa</u>hi min duuni nn<u>aa</u>si fatamannawuu lmawta in kuntum <u>shaa</u>diqiin\r\r\n');
INSERT INTO quranLatin VALUES (5184, '62:7', 62, 7, 'wal<u>aa</u> yatamannawnahu abadan bim<u>aa</u> qaddamat aydiihim wal<u>aa</u>hu \'aliimun bi<u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (5185, '62:8', 62, 8, 'qul inna lmawtalla<u>dz</u>ii tafirruuna minhu fa-innahu mul<u>aa</u>qiikum tsumma turadduuna il<u>aa</u> \'<u>aa</u>limi lghaybi wasysyah<u>aa</u>dati fayunabbi-ukum bim<u>aa</u> kuntum ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (5186, '62:9', 62, 9, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu i<u>dzaa</u> nuudiya li<u>shsh</u>al<u>aa</u>ti min yawmi ljumu\'ati fas\'aw il<u>aa</u> <u>dz</u>ikrill<u>aa</u>hi wa<u>dz</u>aruu lbay\'a <u>dzaa</u>likum khayrun lakum in kuntum ta\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (5187, '62:10', 62, 10, 'fa-i<u>dzaa</u> qu<u>dh</u>iyati <u>shsh</u>al<u>aa</u>tu fantasyiruu fii l-ar<u>dh</u>i wabtaghuu min fa<u>dh</u>lill<u>aa</u>hi wa<u>dz</u>kuruull<u>aa</u>ha katsiiran la\'allakum tufli<u>h</u>uun<b>a </b>\r\r\n');
INSERT INTO quranLatin VALUES (5188, '62:11', 62, 11, 'wa-i<u>dzaa</u> ra-aw tij<u>aa</u>ratan aw lahwan infa<u>dhdh</u>uu ilayh<u>aa</u> watarakuuka q<u>aa</u>-iman qul m<u>aa</u> \'indall<u>aa</u>hi khayrun minallahwi wamina ttij<u>aa</u>rati wal<u>aa</u>hu khayru rr<u>aa</u>ziqiin\r\r\n');
INSERT INTO quranLatin VALUES (5189, '63:1', 63, 1, 'i<u>dzaa</u> j<u>aa</u>-aka lmun<u>aa</u>fiquuna q<u>aa</u>luu nasyhadu innaka larasuulull<u>aa</u>hi wal<u>aa</u>hu ya\'lamu innaka larasuuluhu wal<u>aa</u>hu yasyhadu inna lmun<u>aa</u>fiqiina lak<u>aadz</u>ibuun\r\r\n');
INSERT INTO quranLatin VALUES (5190, '63:2', 63, 2, 'ittakha<u>dz</u>uu aym<u>aa</u>nahum junnatan fa<u>sh</u>adduu \'an sabiilill<u>aa</u>hi innahum s<u>aa</u>-a m<u>aa</u> k<u>aa</u>nuu ya\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (5191, '63:3', 63, 3, '<u>dzaa</u>lika bi-annahum <u>aa</u>manuu tsumma kafaruu fa<u>th</u>ubi\'a \'al<u>aa</u> quluubihim fahum l<u>aa</u> yafqahuun\r\r\n');
INSERT INTO quranLatin VALUES (5192, '63:4', 63, 4, 'wa-i<u>dzaa</u> ra-aytahum tu\'jibuka ajs<u>aa</u>muhum wa-in yaquuluu tasma\'liqawlihim ka-annahum khusyubun musannadatun ya<u>h</u>sabuuna kulla <u>sh</u>ay<u>h</u>atin \'alayhim humu l\'aduwwu fa<u>hts</u>arhum q<u>aa</u>talahumull<u>aa</u>hu ann<u>aa</u> yu/fakuun\r\r\n');
INSERT INTO quranLatin VALUES (5193, '63:5', 63, 5, 'wa-i<u>dzaa</u> qiila lahum ta\'<u>aa</u>law yastaghfir lakum rasuulull<u>aa</u>hi lawwaw ruuusahum wara-aytahum ya<u>sh</u>udduuna wahum mustakbiruun\r\r\n');
INSERT INTO quranLatin VALUES (5194, '63:6', 63, 6, 'saw<u>aa</u>un \'alayhim astaghfarta lahum am lam tastaghfir lahum lan yaghfirall<u>aa</u>hu lahum innall<u>aa</u>ha l<u>aa</u> yahdii lqawma lf<u>aa</u>siqiin\r\r\n');
INSERT INTO quranLatin VALUES (5195, '63:7', 63, 7, 'humulla<u>dz</u>iina yaquuluuna l<u>aa</u> tunfiquu \'al<u>aa</u> man \'inda rasuulill<u>aa</u>hi <u>h</u>att<u>aa</u> yanfa<u>dhdh</u>uu walill<u>aa</u>hi khaz<u>aa</u>-inu ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i wal<u>aa</u>kinna lmun<u>aa</u>fiqiina l<u>aa</u> yafqahuun\r\r\n');
INSERT INTO quranLatin VALUES (5196, '63:8', 63, 8, 'yaquuluuna la-in raja\'n<u>aa</u> il<u>aa</u> lmadiinati layukhrijanna l-a\'azzu minh<u>aa</u> l-a<u>dz</u>alla walill<u>aa</u>hi l\'izzatu walirasuulihi walilmu/miniina wal<u>aa</u>kinna lmun<u>aa</u>fiqiina l<u>aa</u> ya\'lamuun\r\r\n');
INSERT INTO quranLatin VALUES (5197, '63:9', 63, 9, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu l<u>aa</u> tulhikum amw<u>aa</u>lukum wal<u>aa</u> awl<u>aa</u>dukum \'an <u>dz</u>ikrill<u>aa</u>hi waman yaf\'al <u>dzaa</u>lika faul<u>aa</u>-ika humu lkh<u>aa</u>siruun\r\r\n');
INSERT INTO quranLatin VALUES (5198, '63:10', 63, 10, 'wa-anfiquu min m<u>aa</u> razaqn<u>aa</u>kum min qabli an ya/tiya a<u>h</u>adakumu lmawtu fayaquula rabbi lawl<u>aa</u> akhkhartanii il<u>aa</u> ajalin qariibin fa-a<u>shsh</u>addaqa wa-akun mina <u>shshaa</u>li<u>h</u>iin\r\r\n');
INSERT INTO quranLatin VALUES (5199, '63:11', 63, 11, 'walan yu-akhkhirall<u>aa</u>hu nafsan i<u>dzaa</u> j<u>aa</u>-a ajaluh<u>aa</u> wal<u>aa</u>hu khabiirun bim<u>aa</u> ta\'maluun<dir>\r\r\n');
INSERT INTO quranLatin VALUES (5200, '64:1', 64, 1, 'yusabbi<u>h</u>u lill<u>aa</u>hi m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wam<u>aa</u> fii l-ar<u>dh</u>i lahu lmulku walahu l<u>h</u>amdu wahuwa \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (5201, '64:2', 64, 2, 'huwalla<u>dz</u>ii khalaqakum faminkum k<u>aa</u>firun waminkum mu/minun wal<u>aa</u>hu bim<u>aa</u> ta\'maluuna ba<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (5202, '64:3', 64, 3, 'khalaqa ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>a bil<u>h</u>aqqi wa<u>sh</u>awwarakum fa-a<u>h</u>sana <u>sh</u>uwarakum wa-ilayhi lma<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (5203, '64:4', 64, 4, 'ya\'lamu m<u>aa</u> fii ssam<u>aa</u>w<u>aa</u>ti wal-ar<u>dh</u>i waya\'lamu m<u>aa</u> tusirruuna wam<u>aa</u> tu\'linuuna wal<u>aa</u>hu \'aliimun bi<u>dzaa</u>ti <u>shsh</u>uduur\r\r\n');
INSERT INTO quranLatin VALUES (5204, '64:5', 64, 5, 'alam ya/tikum nabaulla<u>dz</u>iina kafaruu min qablu fa<u>dzaa</u>quu wab<u>aa</u>la amrihim walahum \'a<u>dzaa</u>bun liim\r\r\n');
INSERT INTO quranLatin VALUES (5205, '64:6', 64, 6, '<u>dzaa</u>lika bi-annahu k<u>aa</u>nat ta/tiihim rusuluhum bilbayyin<u>aa</u>ti faq<u>aa</u>luu abasyarun yahduunan<u>aa</u> fakafaruu watawallaw wastaghn<u>aa</u>ll<u>aa</u>hu wal<u>aa</u>hu ghaniyyun <u>h</u>amiid\r\r\n');
INSERT INTO quranLatin VALUES (5206, '64:7', 64, 7, 'za\'amalla<u>dz</u>iina kafaruu an lan yub\'atsuu qul bal<u>aa</u> warabbii latub\'atsunna tsumma latunabbaunna bim<u>aa</u> \'amiltum wa<u>dzaa</u>lika \'al<u>aa</u>ll<u>aa</u>hi yasiir\r\r\n');
INSERT INTO quranLatin VALUES (5207, '64:8', 64, 8, 'fa<u>aa</u>minuu bil<u>aa</u>hi warasuulihi wannuurilla<u>dz</u>ii anzaln<u>aa</u> wal<u>aa</u>hu bim<u>aa</u> ta\'maluuna khabiir\r\r\n');
INSERT INTO quranLatin VALUES (5208, '64:9', 64, 9, 'yawma yajma\'ukum liyawmi ljam\'i <u>dzaa</u>lika yawmu ttagh<u>aa</u>buni waman yu/min bil<u>aa</u>hi waya\'mal <u>shaa</u>li<u>h</u>an yukaffir \'anhu sayyi-<u>aa</u>tihi wayudkhilhu jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> abadan <u>dzaa</u>lika lfawzu l\'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (5209, '64:10', 64, 10, 'walla<u>dz</u>iina kafaruu waka<u>dzdz</u>abuu bi-<u>aa</u>y<u>aa</u>tin<u>aa</u> ul<u>aa</u>-ika a<u>sh</u>-<u>haa</u>bu nn<u>aa</u>ri kh<u>aa</u>lidiina fiih<u>aa</u> wabi/sa lma<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (5210, '64:11', 64, 11, 'm<u>aa</u> a<u>shaa</u>ba min mu<u>sh</u>iibatin ill<u>aa</u> bi-i<u>dz</u>nill<u>aa</u>hi waman yu/min bil<u>aa</u>hi yahdi qalbahu wal<u>aa</u>hu bikulli syay-in \'aliim\r\r\n');
INSERT INTO quranLatin VALUES (5211, '64:12', 64, 12, 'wa-a<u>th</u>ii\'uull<u>aa</u>ha wa-a<u>th</u>ii\'uu rrasuula fa-in tawallaytum fa-innam<u>aa</u> \'al<u>aa</u> rasuulin<u>aa</u> lbal<u>aa</u>ghu lmubiin\r\r\n');
INSERT INTO quranLatin VALUES (5212, '64:13', 64, 13, '<B>al</b>l<u>aa</u>hu l<u>aa</u> il<u>aa</u>ha ill<u>aa</u> huwa wa\'al<u>aa</u>ll<u>aa</u>hi falyatawakkali lmu/minuun\r\r\n');
INSERT INTO quranLatin VALUES (5213, '64:14', 64, 14, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu inna min azw<u>aa</u>jikum wa-awl<u>aa</u>dikum \'aduwwan lakum fa<u>hts</u>aruuhum wa-in ta\'fuu wata<u>sh</u>fa<u>h</u>uu wataghfiruu fa-innall<u>aa</u>ha ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (5214, '64:15', 64, 15, 'innam<u>aa</u> amw<u>aa</u>lukum wa-awl<u>aa</u>dukum fitnatun wal<u>aa</u>hu \'indahu ajrun \'a<u>zh</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (5215, '64:16', 64, 16, 'fattaquull<u>aa</u>ha m<u>aa</u> ista<u>th</u>a\'tum wasma\'uu wa-a<u>th</u>ii\'uu wa-anfiquu khayran li-anfusikum waman yuuqa syu<u>hh</u>a nafsihi faul<u>aa</u>-ika humu lmufli<u>h</u>uun\r\r\n');
INSERT INTO quranLatin VALUES (5216, '64:17', 64, 17, 'in tuqri<u>dh</u>uull<u>aa</u>ha qar<u>dh</u>an <u>h</u>asanan yu<u>daa</u>\'ifhu lakum wayaghfir lakum wal<u>aa</u>hu syakuurun <u>h</u>aliim\r\r\n');
INSERT INTO quranLatin VALUES (5217, '64:18', 64, 18, '\'<u>aa</u>limu lghaybi wasysyah<u>aa</u>dati l\'aziizu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (5218, '65:1', 65, 1, 'y<u>aa</u> ayyuh<u>aa</u> nnabiyyu i<u>dzaa</u> <u>th</u>allaqtumu nnis<u>aa</u>-a fa<u>th</u>alliquuhunna li\'iddatihinna wa-a<u>hs</u>uu l\'iddata wattaquull<u>aa</u>ha rabbakum l<u>aa</u> tukhrijuuhunna min buyuutihinna wal<u>aa</u> yakhrujna ill<u>aa</u> an ya/tiina bif<u>aah</u>isyatin mubayyinatin watilka <u>h</u>uduudull<u>aa</u>hi waman yata\'adda <u>h</u>uduudall<u>aa</u>hi faqad <u>zh</u>alama nafsahu l<u>aa</u> tadrii la\'allall<u>aa</u>ha yu<u>h</u>ditsu ba\'da <u>dzaa</u>lika amr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (5219, '65:2', 65, 2, 'fa-i<u>dzaa</u> balaghna ajalahunna fa-amsikuuhunna bima\'ruufin aw f<u>aa</u>riquuhunna bima\'ruufin wa-asyhiduu <u>dz</u>away \'adlin minkum wa-aqiimuu sysyah<u>aa</u>data lill<u>aa</u>hi <u>dzaa</u>likum yuu\'a<u>zh</u>u bihi man k<u>aa</u>na yu/minu bil<u>aa</u>hi walyawmi l-<u>aa</u>khiri waman yattaqill<u>aa</u>ha yaj\'al lahu makhraj<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (5220, '65:3', 65, 3, 'wayarzuqhu min <u>h</u>aytsu l<u>aa</u> ya<u>h</u>tasibu waman yatawakkal \'al<u>aa</u>ll<u>aa</u>hi fahuwa <u>h</u>asbuhu innall<u>aa</u>ha b<u>aa</u>lighu amrihi qad ja\'alall<u>aa</u>hu likulli syay-in qadr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (5221, '65:4', 65, 4, 'wall<u>aa</u>-ii ya-isna mina lma<u>h</u>ii<u>dh</u>i min nis<u>aa</u>-ikum ini irtabtum fa\'iddatuhunna tsal<u>aa</u>tsatu asyhurin wall<u>aa</u>-ii lam ya<u>h</u>i<u>dh</u>na waul<u>aa</u>tu l-a<u>h</u>m<u>aa</u>li ajaluhunna an ya<u>dh</u>a\'na <u>h</u>amlahunna waman yattaqill<u>aa</u>ha yaj\'al lahu min amrihi yusr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (5222, '65:5', 65, 5, '<u>dzaa</u>lika amrull<u>aa</u>hi anzalahu ilaykum waman yattaqill<u>aa</u>ha yukaffir \'anhu sayyi-<u>aa</u>tihi wayu\'<u>zh</u>im lahu ajr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (5223, '65:6', 65, 6, 'askinuuhunna min <u>h</u>aytsu sakantum min wujdikum wal<u>aa</u> tu<u>daa</u>rruuhunna litu<u>dh</u>ayyiquu \'alayhinna wa-in kunna ul<u>aa</u>ti <u>h</u>amlin fa-anfiquu \'alayhinna <u>h</u>att<u>aa</u> ya<u>dh</u>a\'na <u>h</u>amlahunna fa-in ar<u>dh</u>a\'na lakum fa<u>aa</u>tuuhunna ujuurahunna wa/tamiruu baynakum bima\'ruufin wa-in ta\'<u>aa</u>sartum fasatur<u>dh</u>i\'u lahu ukhr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (5224, '65:7', 65, 7, 'liyunfiq <u>dz</u>uu sa\'atin min sa\'atihi waman qudira \'alayhi rizquhu falyunfiq mimm<u>aa</u> <u>aa</u>t<u>aa</u>hull<u>aa</u>hu l<u>aa</u> yukallifull<u>aa</u>hu nafsan ill<u>aa</u> m<u>aa</u> <u>aa</u>t<u>aa</u>h<u>aa</u> sayaj\'alull<u>aa</u>hu ba\'da \'usrin yusr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (5225, '65:8', 65, 8, 'waka-ayyin min qaryatin \'atat \'an amri rabbih<u>aa</u> warusulihi fa<u>haa</u>sabn<u>aa</u>h<u>aa</u> <u>h</u>is<u>aa</u>ban syadiidan wa\'a<u>dzdz</u>abn<u>aa</u>h<u>aa</u> \'a<u>dzaa</u>ban nukr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (5226, '65:9', 65, 9, ' fa<u>dzaa</u>qat wab<u>aa</u>la amrih<u>aa</u> wak<u>aa</u>na \'<u>aa</u>qibatu amrih<u>aa</u> khusr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (5227, '65:10', 65, 10, 'a\'addall<u>aa</u>hu lahum \'a<u>dzaa</u>ban syadiidan fattaquull<u>aa</u>ha y<u>aa</u> ulii l-alb<u>aa</u>billa<u>dz</u>iina <u>aa</u>manuu qad anzalall<u>aa</u>hu ilaykum <u>dz</u>ikr<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (5228, '65:11', 65, 11, 'rasuulan yatluu \'alaykum <u>aa</u>y<u>aa</u>till<u>aa</u>hi mubayyin<u>aa</u>tin liyukhrijalla<u>dz</u>iina <u>aa</u>manuu wa\'amiluu <u>shshaa</u>li<u>haa</u>ti mina <u>zhzh</u>ulum<u>aa</u>ti il<u>aa</u> nnuuri waman yu/min bil<u>aa</u>hi waya\'mal <u>shaa</u>li<u>h</u>an yudkhilhu jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru kh<u>aa</u>lidiina fiih<u>aa</u> abadan qad a<u>h</u>sanall<u>aa</u>hu lahu rizq<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (5229, '65:12', 65, 12, '<B>al</b>l<u>aa</u>hulla<u>dz</u>ii khalaqa sab\'a sam<u>aa</u>w<u>aa</u>tin wamina l-ar<u>dh</u>i mitslahunna yatanazzalu l-amru baynahunna lita\'lamuu annall<u>aa</u>ha \'al<u>aa</u> kulli syay-in qadiirun wa-annall<u>aa</u>ha qad a<u>hath</u>a bikulli syay-in \'ilm<u>aa</u><dir>\r\r\n');
INSERT INTO quranLatin VALUES (5230, '66:1', 66, 1, 'y<u>aa</u> ayyuh<u>aa</u> nnabiyyu lima tu<u>h</u>arrimu m<u>aa</u> a<u>h</u>allall<u>aa</u>hu laka tabtaghii mar<u>daa</u>ta azw<u>aa</u>jika wal<u>aa</u>hu ghafuurun ra<u>h</u>iim\r\r\n');
INSERT INTO quranLatin VALUES (5231, '66:2', 66, 2, 'qad fara<u>dh</u>all<u>aa</u>hu lakum ta<u>h</u>illata aym<u>aa</u>nikum wal<u>aa</u>hu mawl<u>aa</u>kum wahuwa l\'aliimu l<u>h</u>akiim\r\r\n');
INSERT INTO quranLatin VALUES (5232, '66:3', 66, 3, 'wa-i<u>dz</u> asarra nnabiyyu il<u>aa</u> ba\'<u>dh</u>i azw<u>aa</u>jihi <u>h</u>adiitsan falamm<u>aa</u> nabba-at bihi wa-a<u>zh</u>harahull<u>aa</u>hu \'alayhi \'arrafa ba\'<u>dh</u>ahu wa-a\'ra<u>dh</u>a \'an ba\'<u>dh</u>in falamm<u>aa</u> nabba-ah<u>aa</u> bihi q<u>aa</u>lat man anba-aka h<u>aadzaa</u> q<u>aa</u>la nabba-aniya l\'aliimu lkhabiir\r\r\n');
INSERT INTO quranLatin VALUES (5233, '66:4', 66, 4, 'in tatuub<u>aa</u> il<u>aa</u>ll<u>aa</u>hi faqad <u>sh</u>aghat quluubukum<u>aa</u> wa-in ta<u>zhaa</u>har<u>aa</u> \'alayhi fa-innall<u>aa</u>ha huwa mawl<u>aa</u>hu wajibriilu wa<u>shaa</u>li<u>h</u>u lmu/miniina walmal<u>aa</u>-ikatu ba\'da <u>dzaa</u>lika <u>zh</u>ahiir\r\r\n');
INSERT INTO quranLatin VALUES (5234, '66:5', 66, 5, '\'as<u>aa</u> rabbuhu in <u>th</u>allaqakunna an yubdilahu azw<u>aa</u>jan khayran minkunna muslim<u>aa</u>tin mu/min<u>aa</u>tin q<u>aa</u>nit<u>aa</u>tin t<u>aa</u>-ib<u>aa</u>tin \'<u>aa</u>bid<u>aa</u>tin s<u>aa</u>-i<u>haa</u>tin tsayyib<u>aa</u>tin wa-abk<u>aa</u>r<u>aa</u>\r\r\n');
INSERT INTO quranLatin VALUES (5235, '66:6', 66, 6, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu quu anfusakum wa-ahliikum n<u>aa</u>ran waquuduh<u>aa</u> nn<u>aa</u>su wal<u>h</u>ij<u>aa</u>ratu \'alayh<u>aa</u> mal<u>aa</u>-ikatun ghil<u>aats</u>un syid<u>aa</u>dun l<u>aa</u> ya\'<u>sh</u>uunall<u>aa</u>ha m<u>aa</u> amarahum wayaf\'aluuna m<u>aa</u> yu/maruun\r\r\n');
INSERT INTO quranLatin VALUES (5236, '66:7', 66, 7, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina kafaruu l<u>aa</u> ta\'ta<u>dz</u>iruu lyawma innam<u>aa</u> tujzawna m<u>aa</u> kuntum ta\'maluun\r\r\n');
INSERT INTO quranLatin VALUES (5237, '66:8', 66, 8, 'y<u>aa</u> ayyuh<u>aa</u>lla<u>dz</u>iina <u>aa</u>manuu tuubuu il<u>aa</u>ll<u>aa</u>hi tawbatan na<u>sh</u>uu<u>h</u>an \'as<u>aa</u> rabbukum an yukaffira \'ankum sayyi-<u>aa</u>tikum wayudkhilakum jann<u>aa</u>tin tajrii min ta<u>h</u>tih<u>aa</u> l-anh<u>aa</u>ru yawma l<u>aa</u> yukhziill<u>aa</u>hu nnabiyya walla<u>dz</u>iina <u>aa</u>manuu ma\'ahu nuuruhum yas\'<u>aa</u> bayna aydiihim wabi-aym<u>aa</u>nihim yaquuluuna rabban<u>aa</u> atmim lan<u>aa</u> nuuran<u>aa</u> waghfir lan<u>aa</u> innaka \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (5238, '66:9', 66, 9, 'y<u>aa</u> ayyuh<u>aa</u> nnabiyyu j<u>aa</u>hidi lkuff<u>aa</u>ra walmun<u>aa</u>fiqiina waghlu<u>zh</u> \'alayhim wama/w<u>aa</u>hum jahannamu wabi/sa lma<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (5239, '66:10', 66, 10, '<u>dh</u>araball<u>aa</u>hu matsalan lilla<u>dz</u>iina kafaruu imra-ata nuu<u>h</u>in wamra-ata luu<u>th</u>in k<u>aa</u>nat<u>aa</u> ta<u>h</u>ta \'abdayni min \'ib<u>aa</u>din<u>aa</u> <u>shaa</u>li<u>h</u>ayni fakh<u>aa</u>nat<u>aa</u>hum<u>aa</u> falam yughniy<u>aa</u> \'anhum<u>aa</u> minall<u>aa</u>hi syay-an waqiila udkhul<u>aa</u> nn<u>aa</u>ra ma\'a dd<u>aa</u>khiliin\r\r\n');
INSERT INTO quranLatin VALUES (5240, '66:11', 66, 11, 'wa<u>dh</u>araball<u>aa</u>hu matsalan lilla<u>dz</u>iina <u>aa</u>manuu imra-ata fir\'awna i<u>dz</u> q<u>aa</u>lat rabbi ibni lii \'indaka baytan fii ljannati wanajjinii min fir\'awna wa\'amalihi wanajjinii mina lqawmi <u>zhzhaa</u>limiin\r\r\n');
INSERT INTO quranLatin VALUES (5241, '66:12', 66, 12, 'wamaryama ibnata \'imr<u>aa</u>nallatii a<u>hs</u>anat farjah<u>aa</u> fanafakhn<u>aa</u> fiihi min ruu<u>h</u>in<u>aa</u> wa<u>sh</u>addaqat bikalim<u>aa</u>ti rabbih<u>aa</u> wakutubihi wak<u>aa</u>nat mina lq<u>aa</u>nitiin\r\r\n');
INSERT INTO quranLatin VALUES (5242, '67:1', 67, 1, 'tab<u>aa</u>rakalla<u>dz</u>ii biyadihi lmulku wahuwa \'al<u>aa</u> kulli syay-in qadiir\r\r\n');
INSERT INTO quranLatin VALUES (5243, '67:2', 67, 2, '<B>al</b>la<u>dz</u>ii khalaqa lmawta wal<u>h</u>ay<u>aa</u>ta liyabluwakum ayyukum a<u>h</u>sanu \'amalan wahuwa l\'aziizu lghafuur\r\r\n');
INSERT INTO quranLatin VALUES (5244, '67:3', 67, 3, '<B>al</b>la<u>dz</u>ii khalaqa sab\'a sam<u>aa</u>w<u>aa</u>tin <u>th</u>ib<u>aa</u>qan m<u>aa</u> tar<u>aa</u> fii khalqi rra<u>h</u>m<u>aa</u>ni min taf<u>aa</u>wutin farji\'i lba<u>sh</u>ara hal tar<u>aa</u> min fu<u>th</u>uur\r\r\n');
INSERT INTO quranLatin VALUES (5245, '67:4', 67, 4, 'tsumma irji\'i lba<u>sh</u>ara karratayni yanqalib ilayka lba<u>sh</u>aru kh<u>aa</u>si-an wahuwa <u>h</u>asiir\r\r\n');
INSERT INTO quranLatin VALUES (5246, '67:5', 67, 5, 'walaqad zayyann<u>aa</u> ssam<u>aa</u>-a dduny<u>aa</u> bima<u>shaa</u>bii<u>h</u>a waja\'aln<u>aa</u>h<u>aa</u> rujuuman lisysyay<u>aath</u>iini wa-a\'tadn<u>aa</u> lahum \'a<u>dzaa</u>ba ssa\'iir\r\r\n');
INSERT INTO quranLatin VALUES (5247, '67:6', 67, 6, 'walilla<u>dz</u>iina kafaruu birabbihim \'a<u>dzaa</u>bu jahannama wabi/sa lma<u>sh</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (5248, '67:7', 67, 7, 'i<u>dzaa</u> ulquu fiih<u>aa</u> sami\'uu lah<u>aa</u> syahiiqan wahiya tafuur\r\r\n');
INSERT INTO quranLatin VALUES (5249, '67:8', 67, 8, 'tak<u>aa</u>du tamayyazu mina alghay<u>zh</u>i kullam<u>aa</u> ulqiya fiih<u>aa</u> fawjun sa-alahum khazanatuh<u>aa</u> alam ya/tikum na<u>dz</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (5250, '67:9', 67, 9, 'q<u>aa</u>luu bal<u>aa</u> qad j<u>aa</u>-an<u>aa</u> na<u>dz</u>iirun faka<u>dzdz</u>abn<u>aa</u> waquln<u>aa</u> m<u>aa</u> nazzalall<u>aa</u>hu min syay-in in antum ill<u>aa</u> fii <u>dh</u>al<u>aa</u>lin kabiir\r\r\n');
INSERT INTO quranLatin VALUES (5251, '67:10', 67, 10, 'waq<u>aa</u>luu law kunn<u>aa</u> nasma\'u aw na\'qilu m<u>aa</u> kunn<u>aa</u> fii a<u>sh</u>-<u>haa</u>bi ssa\'iir\r\r\n');
INSERT INTO quranLatin VALUES (5252, '67:11', 67, 11, 'fa\'tarafuu bi<u>dz</u>anbihim fasu<u>h</u>qan li-a<u>sh</u>-<u>haa</u>bi ssa\'iir\r\r\n');
INSERT INTO quranLatin VALUES (5253, '67:12', 67, 12, 'innalla<u>dz</u>iina yakhsyawna rabbahum bilghaybi lahum maghfiratun wa-ajrun kabiir\r\r\n');
INSERT INTO quranLatin VALUES (5254, '67:13', 67, 13, 'wa-asirruu qawlakum awi ijharuu bihi innahu \'aliimun bi<u>dzaa</u>ti <u>shsh</u>uduur\r\r\n');
INSERT INTO quranLatin VALUES (5255, '67:14', 67, 14, 'al<u>aa</u> ya\'lamu man khalaqa wahuwalla<u>th</u>iifu lkhabiir\r\r\n');
INSERT INTO quranLatin VALUES (5256, '67:15', 67, 15, 'huwalla<u>dz</u>ii ja\'ala lakumu l-ar<u>dh</u>a <u>dz</u>aluulan famsyuu fii man<u>aa</u>kibih<u>aa</u> wakuluu min rizqihi wa-ilayhi nnusyuur\r\r\n');
INSERT INTO quranLatin VALUES (5257, '67:16', 67, 16, 'a-amintum man fii ssam<u>aa</u>-i an yakhsifa bikumu l-ar<u>dh</u>a fa-i<u>dzaa</u> hiya tamuur\r\r\n');
INSERT INTO quranLatin VALUES (5258, '67:17', 67, 17, 'am amintum man fii ssam<u>aa</u>-i an yursila \'alaykum <u>has</u>iban fasata\'lamuuna kayfa na<u>dz</u>iir\r\r\n');
INSERT INTO quranLatin VALUES (5259, '67:18', 67, 18, 'walaqad ka<u>dzdz</u>aballa<u>dz</u>iina min qablihim fakayfa k<u>aa</u>na nakiir\r\r\n');
